import React, { useState, useEffect } from 'react'
import { Button } from '@persona-framework/ui'
import { X, RefreshCw, Loader2 } from 'lucide-react'
import Editor from '@monaco-editor/react'

interface BlameInfo {
  line: number
  commit: string
  author: string
  authorEmail: string
  date: string
  message: string
  content: string
}

interface GitBlameViewProps {
  filePath: string | null
  onClose?: () => void
}

export function GitBlameView({ filePath, onClose }: GitBlameViewProps) {
  const [blameData, setBlameData] = useState<BlameInfo[]>([])
  const [fileContent, setFileContent] = useState<string>('')
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState<string | null>(null)
  const [hoveredLine, setHoveredLine] = useState<BlameInfo | null>(null)
  const [tooltipPosition, setTooltipPosition] = useState<{ x: number; y: number } | null>(null)

  const fetchGitBlame = async (path: string) => {
    setLoading(true)
    setError(null)

    try {
      const blameResponse = await fetch(`/api/git/blame?path=${encodeURIComponent(path)}`)
      if (!blameResponse.ok) {
        throw new Error(`Failed to fetch git blame: ${blameResponse.statusText}`)
      }
      const blameJson = await blameResponse.json()
      setBlameData(blameJson.lines || [])

      const absolutePath = path.startsWith('/') ? path : `/workspace/persona-framework/${path}`
      const contentResponse = await fetch(`/api/project/read?path=${encodeURIComponent(absolutePath)}`)
      if (contentResponse.ok) {
        const contentJson = await contentResponse.json()
        setFileContent(contentJson.content || '')
      }

      setLoading(false)
    } catch (err: any) {
      setError(err.message || 'Failed to load git blame')
      setLoading(false)
    }
  }

  useEffect(() => {
    if (filePath) {
      fetchGitBlame(filePath)
    }
  }, [filePath])

  const handleRefresh = () => {
    if (filePath) {
      fetchGitBlame(filePath)
    }
  }

  const handleLineHover = (blame: BlameInfo, event: React.MouseEvent) => {
    setHoveredLine(blame)
    setTooltipPosition({ x: event.clientX, y: event.clientY })
  }

  const handleLineLeave = () => {
    setHoveredLine(null)
    setTooltipPosition(null)
  }

  const formatDate = (dateStr: string): string => {
    try {
      const date = new Date(dateStr)
      const now = new Date()
      const diffMs = now.getTime() - date.getTime()
      const diffDays = Math.floor(diffMs / (1000 * 60 * 60 * 24))

      if (diffDays === 0) return 'Today'
      if (diffDays === 1) return 'Yesterday'
      if (diffDays < 7) return `${diffDays}d ago`
      if (diffDays < 30) return `${Math.floor(diffDays / 7)}w ago`
      if (diffDays < 365) return `${Math.floor(diffDays / 30)}mo ago`
      return `${Math.floor(diffDays / 365)}y ago`
    } catch {
      return dateStr
    }
  }

  const getLanguage = (path: string | null): string => {
    if (!path) return 'plaintext'

    const ext = path.split('.').pop()?.toLowerCase()
    const languageMap: Record<string, string> = {
      ts: 'typescript',
      tsx: 'typescript',
      js: 'javascript',
      jsx: 'javascript',
      json: 'json',
      md: 'markdown',
      java: 'java',
      kt: 'kotlin',
      py: 'python',
      css: 'css',
      html: 'html',
      xml: 'xml',
      yaml: 'yaml',
      yml: 'yaml',
      sh: 'shell',
      sql: 'sql',
    }
    return languageMap[ext || ''] || 'plaintext'
  }

  if (!filePath) {
    return (
      <div className="w-full h-full flex flex-col bg-[#1e1e1e]">
        <div className="p-10 text-center text-[#858585]">
          Select a file to view git blame
        </div>
      </div>
    )
  }

  if (loading) {
    return (
      <div className="w-full h-full flex flex-col bg-[#1e1e1e]">
        <div className="flex items-center gap-2 px-3 py-2 bg-[#2b2b2b] border-b border-[#3c3c3c] text-[#cccccc] text-xs">
          <span>{filePath}</span>
        </div>
        <div className="flex flex-col items-center justify-center gap-3 p-10">
          <Loader2 className="w-6 h-6 animate-spin" />
          <span className="text-sm">Loading git blame...</span>
        </div>
      </div>
    )
  }

  if (error) {
    return (
      <div className="w-full h-full flex flex-col bg-[#1e1e1e]">
        <div className="flex items-center gap-2 px-3 py-2 bg-[#2b2b2b] border-b border-[#3c3c3c] text-[#cccccc] text-xs">
          <span>{filePath}</span>
          {onClose && (
            <Button
              variant="ghost"
              size="sm"
              onClick={onClose}
              className="ml-auto"
            >
              <X className="w-4 h-4" />
            </Button>
          )}
        </div>
        <div className="p-5 text-[#f48771]">{error}</div>
      </div>
    )
  }

  return (
    <div className="w-full h-full flex flex-col bg-[#1e1e1e]">
      <div className="flex items-center justify-between px-3 py-2 bg-[#2b2b2b] border-b border-[#3c3c3c] text-[#cccccc] text-xs">
        <div className="flex items-center gap-2">
          <span>{filePath}</span>
          <span className="text-[11px] text-[#858585]">Git Blame</span>
        </div>
        <div className="flex items-center gap-2">
          <Button
            variant="ghost"
            size="sm"
            onClick={handleRefresh}
            title="Refresh blame"
          >
            <RefreshCw className="w-4 h-4" />
          </Button>
          {onClose && (
            <Button
              variant="ghost"
              size="sm"
              onClick={onClose}
              title="Close blame view"
            >
              <X className="w-4 h-4" />
            </Button>
          )}
        </div>
      </div>

      <div className="flex flex-1 overflow-hidden">
        <div className="w-[280px] border-r border-[#3c3c3c] overflow-auto bg-[#252526] font-mono text-[11px]">
          {blameData.map((blame, index) => (
            <div
              key={index}
              className="flex px-2 h-[19px] items-center gap-2 border-b border-transparent hover:bg-[#2b2b2b] cursor-pointer"
              onMouseEnter={(e) => handleLineHover(blame, e)}
              onMouseLeave={handleLineLeave}
              title={`${blame.author}: ${blame.message}`}
            >
              <span className="min-w-[60px] text-[#f59e0b] font-medium">
                {blame.commit.substring(0, 7)}
              </span>
              <span className="min-w-[80px] max-w-[80px] overflow-hidden text-ellipsis whitespace-nowrap text-[#4fc3f7]">
                {blame.author}
              </span>
              <span className="min-w-[75px] text-[#858585]">
                {formatDate(blame.date)}
              </span>
            </div>
          ))}
        </div>

        <div className="flex-1 min-w-0">
          <Editor
            height="100%"
            language={getLanguage(filePath)}
            value={fileContent}
            theme="vs-dark"
            options={{
              readOnly: true,
              minimap: { enabled: false },
              fontSize: 13,
              lineNumbers: 'on',
              scrollBeyondLastLine: false,
              automaticLayout: true,
              scrollbar: {
                vertical: 'visible',
                horizontal: 'visible',
              },
            }}
          />
        </div>
      </div>

      {hoveredLine && tooltipPosition && (
        <div
          className="absolute bg-[#2b2b2b] border border-[#4fc3f7] rounded p-3 text-[#cccccc] text-xs z-[1000] max-w-[400px] shadow-lg"
          style={{
            top: tooltipPosition.y + 10,
            left: tooltipPosition.x + 10,
          }}
        >
          <div className="flex gap-2 mb-1.5">
            <span className="text-[#858585] min-w-[80px]">Commit:</span>
            <span className="text-white flex-1 break-words">{hoveredLine.commit}</span>
          </div>
          <div className="flex gap-2 mb-1.5">
            <span className="text-[#858585] min-w-[80px]">Author:</span>
            <span className="text-white flex-1 break-words">
              {hoveredLine.author} &lt;{hoveredLine.authorEmail}&gt;
            </span>
          </div>
          <div className="flex gap-2 mb-1.5">
            <span className="text-[#858585] min-w-[80px]">Date:</span>
            <span className="text-white flex-1 break-words">{hoveredLine.date}</span>
          </div>
          <div className="flex gap-2">
            <span className="text-[#858585] min-w-[80px]">Message:</span>
            <span className="text-white flex-1 break-words">{hoveredLine.message}</span>
          </div>
        </div>
      )}
    </div>
  )
}

