import { useState } from 'react'
import {
  Box, Play, Square, RotateCcw, Pause, Trash2, Terminal,
  FileText, Activity, Search, Filter
} from 'lucide-react'
import { IconButton } from '@ui/components/atoms'
import { StatusBadge, DataTable, SectionHeader, ResourceCard, ActionMenu, ActionItem } from '../shared'
import { mockContainers } from '../../mock-data'
import type { Container } from '../../types'

type ViewMode = 'table' | 'cards'

function formatRelativeTime(dateStr: string): string {
  const diff = Date.now() - new Date(dateStr).getTime()
  const hours = Math.floor(diff / 3600000)
  const days = Math.floor(diff / 86400000)
  if (days > 0) return `${days}d ago`
  if (hours > 0) return `${hours}h ago`
  return 'Just now'
}

function ContainerActions({ container, compact = false }: { container: Container; compact?: boolean }) {
  const isRunning = container.state === 'running'
  const isPaused = container.state === 'paused'

  if (compact) {
    return (
      <ActionMenu>
        {!isRunning && <ActionItem icon={<Play className="h-3 w-3" />} label="Start" onClick={() => {}} />}
        {isRunning && <ActionItem icon={<Square className="h-3 w-3" />} label="Stop" onClick={() => {}} />}
        {isRunning && !isPaused && <ActionItem icon={<Pause className="h-3 w-3" />} label="Pause" onClick={() => {}} />}
        {isPaused && <ActionItem icon={<Play className="h-3 w-3" />} label="Unpause" onClick={() => {}} />}
        <ActionItem icon={<RotateCcw className="h-3 w-3" />} label="Restart" onClick={() => {}} />
        <ActionItem icon={<Terminal className="h-3 w-3" />} label="Exec" onClick={() => {}} />
        <ActionItem icon={<FileText className="h-3 w-3" />} label="Logs" onClick={() => {}} />
        <ActionItem icon={<Activity className="h-3 w-3" />} label="Stats" onClick={() => {}} />
        <ActionItem icon={<Trash2 className="h-3 w-3" />} label="Remove" onClick={() => {}} variant="danger" />
      </ActionMenu>
    )
  }

  return (
    <div className="flex items-center gap-1">
      {!isRunning && (
        <IconButton icon={<Play className="h-3 w-3" />} size="sm" tooltip="Start" onClick={() => {}} />
      )}
      {isRunning && (
        <IconButton icon={<Square className="h-3 w-3" />} size="sm" tooltip="Stop" onClick={() => {}} />
      )}
      <IconButton icon={<RotateCcw className="h-3 w-3" />} size="sm" tooltip="Restart" onClick={() => {}} />
      <IconButton icon={<Terminal className="h-3 w-3" />} size="sm" tooltip="Exec" onClick={() => {}} />
      <IconButton icon={<FileText className="h-3 w-3" />} size="sm" tooltip="Logs" onClick={() => {}} />
    </div>
  )
}

export function ContainersSection() {
  const [viewMode, setViewMode] = useState<ViewMode>('table')
  const [filter, setFilter] = useState('')
  const [showAll, setShowAll] = useState(false)

  const containers = showAll
    ? mockContainers
    : mockContainers.filter((c) => c.state === 'running')

  const filteredContainers = filter
    ? containers.filter((c) =>
        c.names[0].toLowerCase().includes(filter.toLowerCase()) ||
        c.image.toLowerCase().includes(filter.toLowerCase())
      )
    : containers

  const columns = [
    {
      key: 'name',
      header: 'Container',
      render: (c: Container) => (
        <div className="flex items-center gap-2">
          <Box className="h-4 w-4 text-[var(--accent-primary)]" />
          <div>
            <div className="font-medium">{c.names[0].replace('/', '')}</div>
            <div className="text-xs text-[var(--text-muted)]">{c.id.slice(0, 12)}</div>
          </div>
        </div>
      ),
    },
    {
      key: 'image',
      header: 'Image',
      render: (c: Container) => (
        <span className="text-[var(--text-secondary)]">{c.image}</span>
      ),
    },
    {
      key: 'state',
      header: 'Status',
      width: '100px',
      render: (c: Container) => <StatusBadge status={c.state} />,
    },
    {
      key: 'ports',
      header: 'Ports',
      render: (c: Container) =>
        c.ports.length > 0 ? (
          <span className="text-xs font-mono">
            {c.ports.map((p) => `${p.publicPort || p.privatePort}:${p.privatePort}`).join(', ')}
          </span>
        ) : (
          <span className="text-[var(--text-muted)]">-</span>
        ),
    },
    {
      key: 'created',
      header: 'Created',
      width: '100px',
      render: (c: Container) => (
        <span className="text-[var(--text-secondary)]">{formatRelativeTime(c.created)}</span>
      ),
    },
    {
      key: 'actions',
      header: '',
      width: '140px',
      render: (c: Container) => <ContainerActions container={c} />,
    },
  ]

  return (
    <div className="h-full flex flex-col">
      <SectionHeader
        title="Containers"
        subtitle={`${filteredContainers.length} container${filteredContainers.length !== 1 ? 's' : ''}`}
        icon={<Box className="h-5 w-5" />}
        onRefresh={() => {}}
        actions={
          <div className="flex items-center gap-2">
            <div className="relative">
              <Search className="h-3.5 w-3.5 absolute left-2 top-1/2 -translate-y-1/2 text-[var(--text-muted)]" />
              <input
                type="text"
                placeholder="Filter..."
                value={filter}
                onChange={(e) => setFilter(e.target.value)}
                className="pl-7 pr-3 py-1 text-xs bg-[var(--bg-primary)] border border-[var(--border-primary)] rounded w-40 text-[var(--text-primary)] placeholder:text-[var(--text-muted)] focus:outline-none focus:border-[var(--accent-primary)]"
              />
            </div>
            <IconButton
              icon={<Filter className="h-3.5 w-3.5" />}
              size="sm"
              tooltip={showAll ? 'Show running only' : 'Show all'}
              active={showAll}
              onClick={() => setShowAll(!showAll)}
            />
          </div>
        }
      />

      <div className="flex-1 overflow-auto">
        {viewMode === 'table' ? (
          <DataTable
            data={filteredContainers}
            columns={columns}
            keyExtractor={(c) => c.id}
            emptyMessage="No containers found"
            compact
          />
        ) : (
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-3 p-1">
            {filteredContainers.map((container) => (
              <ResourceCard
                key={container.id}
                title={container.names[0].replace('/', '')}
                subtitle={container.image}
                icon={<Box className="h-5 w-5" />}
                status={<StatusBadge status={container.state} />}
                stats={[
                  { label: 'Ports', value: container.ports.length > 0 ? container.ports.map(p => p.publicPort || p.privatePort).join(', ') : '-' },
                  { label: 'Created', value: formatRelativeTime(container.created) },
                ]}
                actions={<ContainerActions container={container} compact />}
              />
            ))}
          </div>
        )}
      </div>
    </div>
  )
}
