import { useState } from 'react'
import { Layers, Trash2, Tag, Upload, Download, Search } from 'lucide-react'
import { IconButton } from '@ui/components/atoms'
import { DataTable, SectionHeader, ActionMenu, ActionItem } from '../shared'
import { mockImages } from '../../mock-data'
import type { DockerImage } from '../../types'

function formatBytes(bytes: number): string {
  const units = ['B', 'KB', 'MB', 'GB']
  let size = bytes
  let unitIndex = 0
  while (size >= 1024 && unitIndex < units.length - 1) {
    size /= 1024
    unitIndex++
  }
  return `${size.toFixed(1)} ${units[unitIndex]}`
}

function formatRelativeTime(dateStr: string): string {
  const diff = Date.now() - new Date(dateStr).getTime()
  const days = Math.floor(diff / 86400000)
  const weeks = Math.floor(days / 7)
  if (weeks > 0) return `${weeks}w ago`
  if (days > 0) return `${days}d ago`
  return 'Today'
}

function ImageActions({ image }: { image: DockerImage }) {
  return (
    <ActionMenu>
      <ActionItem icon={<Tag className="h-3 w-3" />} label="Tag" onClick={() => {}} />
      <ActionItem icon={<Upload className="h-3 w-3" />} label="Push" onClick={() => {}} />
      <ActionItem icon={<Trash2 className="h-3 w-3" />} label="Remove" onClick={() => {}} variant="danger" />
    </ActionMenu>
  )
}

export function ImagesSection() {
  const [filter, setFilter] = useState('')

  const filteredImages = filter
    ? mockImages.filter((img) =>
        img.repoTags.some((tag) => tag.toLowerCase().includes(filter.toLowerCase()))
      )
    : mockImages

  const columns = [
    {
      key: 'image',
      header: 'Image',
      render: (img: DockerImage) => (
        <div className="flex items-center gap-2">
          <Layers className="h-4 w-4 text-[var(--accent-primary)]" />
          <div>
            <div className="font-medium">{img.repoTags[0]?.split(':')[0] || '<none>'}</div>
            <div className="text-xs text-[var(--text-muted)]">{img.id.slice(0, 19)}</div>
          </div>
        </div>
      ),
    },
    {
      key: 'tags',
      header: 'Tags',
      render: (img: DockerImage) => (
        <div className="flex flex-wrap gap-1">
          {img.repoTags.map((tag) => (
            <span
              key={tag}
              className="text-xs px-1.5 py-0.5 bg-[var(--bg-tertiary)] rounded text-[var(--text-secondary)]"
            >
              {tag.split(':')[1] || 'latest'}
            </span>
          ))}
        </div>
      ),
    },
    {
      key: 'size',
      header: 'Size',
      width: '100px',
      render: (img: DockerImage) => (
        <span className="text-[var(--text-secondary)] font-mono text-xs">
          {formatBytes(img.size)}
        </span>
      ),
    },
    {
      key: 'created',
      header: 'Created',
      width: '100px',
      render: (img: DockerImage) => (
        <span className="text-[var(--text-secondary)]">{formatRelativeTime(img.created)}</span>
      ),
    },
    {
      key: 'actions',
      header: '',
      width: '60px',
      render: (img: DockerImage) => <ImageActions image={img} />,
    },
  ]

  return (
    <div className="h-full flex flex-col">
      <SectionHeader
        title="Images"
        subtitle={`${filteredImages.length} image${filteredImages.length !== 1 ? 's' : ''}`}
        icon={<Layers className="h-5 w-5" />}
        onRefresh={() => {}}
        actions={
          <div className="flex items-center gap-2">
            <div className="relative">
              <Search className="h-3.5 w-3.5 absolute left-2 top-1/2 -translate-y-1/2 text-[var(--text-muted)]" />
              <input
                type="text"
                placeholder="Filter..."
                value={filter}
                onChange={(e) => setFilter(e.target.value)}
                className="pl-7 pr-3 py-1 text-xs bg-[var(--bg-primary)] border border-[var(--border-primary)] rounded w-40 text-[var(--text-primary)] placeholder:text-[var(--text-muted)] focus:outline-none focus:border-[var(--accent-primary)]"
              />
            </div>
            <IconButton
              icon={<Download className="h-3.5 w-3.5" />}
              size="sm"
              tooltip="Pull image"
              onClick={() => {}}
            />
          </div>
        }
      />

      <div className="flex-1 overflow-auto">
        <DataTable
          data={filteredImages}
          columns={columns}
          keyExtractor={(img) => img.id}
          emptyMessage="No images found"
          compact
        />
      </div>
    </div>
  )
}
