import { useState } from 'react'
import { Key, Plus, Eye, EyeOff, RotateCcw, Trash2, Search } from 'lucide-react'
import { IconButton } from '@ui/components/atoms'
import { SectionHeader, ProviderIcon, DataTable, ActionMenu, ActionItem } from '../shared'
import { mockSecrets } from '../../mock-data'
import type { Secret } from '../../types'

function formatRelativeTime(dateStr: string): string {
  const diff = Date.now() - new Date(dateStr).getTime()
  const days = Math.floor(diff / 86400000)
  const weeks = Math.floor(days / 7)
  if (weeks > 0) return `${weeks}w ago`
  if (days > 0) return `${days}d ago`
  return 'Today'
}

function SecretActions({ secret }: { secret: Secret }) {
  return (
    <ActionMenu>
      <ActionItem icon={<Eye className="h-3 w-3" />} label="View" onClick={() => {}} />
      <ActionItem icon={<RotateCcw className="h-3 w-3" />} label="Rotate" onClick={() => {}} />
      <ActionItem icon={<Trash2 className="h-3 w-3" />} label="Delete" onClick={() => {}} variant="danger" />
    </ActionMenu>
  )
}

export function SecretsSection() {
  const [filter, setFilter] = useState('')

  const filteredSecrets = filter
    ? mockSecrets.filter((s) =>
        s.name.toLowerCase().includes(filter.toLowerCase()) ||
        s.path.toLowerCase().includes(filter.toLowerCase())
      )
    : mockSecrets

  const columns = [
    {
      key: 'name',
      header: 'Secret',
      render: (s: Secret) => (
        <div className="flex items-center gap-2">
          <Key className="h-4 w-4 text-[var(--accent-warning)]" />
          <div>
            <div className="font-medium text-[var(--text-primary)]">{s.name}</div>
            <div className="text-xs text-[var(--text-muted)]">{s.path}</div>
          </div>
        </div>
      ),
    },
    {
      key: 'provider',
      header: 'Provider',
      width: '120px',
      render: (s: Secret) => (
        <div className="flex items-center gap-2">
          <ProviderIcon provider={s.provider} size="sm" />
          <span className="text-xs text-[var(--text-secondary)]">{s.provider}</span>
        </div>
      ),
    },
    {
      key: 'version',
      header: 'Version',
      width: '80px',
      render: (s: Secret) => (
        <span className="text-xs font-mono text-[var(--text-muted)]">v{s.version}</span>
      ),
    },
    {
      key: 'updated',
      header: 'Updated',
      width: '100px',
      render: (s: Secret) => (
        <span className="text-xs text-[var(--text-secondary)]">{formatRelativeTime(s.updatedAt)}</span>
      ),
    },
    {
      key: 'actions',
      header: '',
      width: '60px',
      render: (s: Secret) => <SecretActions secret={s} />,
    },
  ]

  return (
    <div className="h-full flex flex-col">
      <SectionHeader
        title="Secrets"
        subtitle={`${filteredSecrets.length} secret${filteredSecrets.length !== 1 ? 's' : ''}`}
        icon={<Key className="h-5 w-5" />}
        onRefresh={() => {}}
        actions={
          <div className="flex items-center gap-2">
            <div className="relative">
              <Search className="h-3.5 w-3.5 absolute left-2 top-1/2 -translate-y-1/2 text-[var(--text-muted)]" />
              <input
                type="text"
                placeholder="Filter..."
                value={filter}
                onChange={(e) => setFilter(e.target.value)}
                className="pl-7 pr-3 py-1 text-xs bg-[var(--bg-primary)] border border-[var(--border-primary)] rounded w-40 text-[var(--text-primary)] placeholder:text-[var(--text-muted)] focus:outline-none focus:border-[var(--accent-primary)]"
              />
            </div>
            <IconButton
              icon={<Plus className="h-3.5 w-3.5" />}
              size="sm"
              tooltip="Add secret"
              onClick={() => {}}
            />
          </div>
        }
      />

      <div className="flex-1 overflow-auto">
        <DataTable
          data={filteredSecrets}
          columns={columns}
          keyExtractor={(s) => s.id}
          emptyMessage="No secrets found"
          compact
        />
      </div>
    </div>
  )
}
