import React, { forwardRef } from 'react'

export interface IconTrayItemProps {
  icon: React.ReactNode
  label: string
  active?: boolean
  side: 'left' | 'right'
  dragging?: boolean
  dragOver?: boolean
  onClick?: () => void
  onDragStart?: (e: React.DragEvent) => void
  onDragOver?: (e: React.DragEvent) => void
  onDragLeave?: (e: React.DragEvent) => void
  onDrop?: (e: React.DragEvent) => void
  onDragEnd?: () => void
  onKeyDown?: (e: React.KeyboardEvent) => void
}

export const IconTrayItem = forwardRef<HTMLDivElement, IconTrayItemProps>(
  (
    {
      icon,
      label,
      active = false,
      side,
      dragging = false,
      dragOver = false,
      onClick,
      onDragStart,
      onDragOver,
      onDragLeave,
      onDrop,
      onDragEnd,
      onKeyDown,
    },
    ref
  ) => {
    const borderSide = side === 'left' ? 'border-l-2 border-l' : 'border-r-2 border-r'
    const activeBorder = side === 'left' ? 'border-l-[var(--accent-primary)]' : 'border-r-[var(--accent-primary)]'

    return (
      <div
        ref={ref}
        role="button"
        tabIndex={0}
        aria-label={label}
        aria-pressed={active}
        onClick={onClick}
        onKeyDown={(e) => {
          if (e.key === 'Enter' || e.key === ' ') {
            e.preventDefault()
            onClick?.()
          }
          onKeyDown?.(e)
        }}
        draggable
        onDragStart={onDragStart}
        onDragOver={onDragOver}
        onDragLeave={onDragLeave}
        onDrop={onDrop}
        onDragEnd={onDragEnd}
        title={label}
        className={`
          w-[var(--icon-tray-width)] h-[var(--icon-tray-width)]
          flex items-center justify-center
          cursor-grab border-b border-[var(--border-primary)]
          bg-[var(--bg-secondary)]
          transition-all duration-150
          hover:bg-[var(--bg-hover)] hover:scale-105
          active:cursor-grabbing
          focus:outline-none focus-visible:ring-2 focus-visible:ring-inset focus-visible:ring-[var(--accent-primary)]
          [&>svg]:h-5 [&>svg]:w-5
          ${active ? `bg-[var(--bg-primary)] ${borderSide} ${activeBorder} text-[var(--accent-primary)]` : 'text-[var(--text-secondary)] hover:text-[var(--text-primary)]'}
          ${dragging ? 'opacity-50 scale-95' : ''}
          ${dragOver ? 'border-t-2 border-t-[var(--accent-primary)] bg-[var(--accent-hover)]' : ''}
        `}
      >
        {icon}
      </div>
    )
  }
)

IconTrayItem.displayName = 'IconTrayItem'
