import { useState } from 'react'
import {
  Activity, AlertTriangle, Bell, BellOff, Check,
  Clock, TrendingUp, TrendingDown, Minus
} from 'lucide-react'
import { IconButton } from '@ui/components/atoms'
import { StatusBadge, SectionHeader, DataTable } from '../shared'
import { mockAlerts } from '../../mock-data'
import type { Alert } from '../../types'

function formatRelativeTime(dateStr: string): string {
  const diff = Date.now() - new Date(dateStr).getTime()
  const mins = Math.floor(diff / 60000)
  const hours = Math.floor(diff / 3600000)
  const days = Math.floor(diff / 86400000)
  if (days > 0) return `${days}d ago`
  if (hours > 0) return `${hours}h ago`
  if (mins > 0) return `${mins}m ago`
  return 'Just now'
}

function SeverityBadge({ severity }: { severity: Alert['severity'] }) {
  const config = {
    critical: { variant: 'error' as const, icon: <AlertTriangle className="h-3 w-3" /> },
    warning: { variant: 'warning' as const, icon: <AlertTriangle className="h-3 w-3" /> },
    info: { variant: 'accent' as const, icon: <Bell className="h-3 w-3" /> },
  }
  const { variant, icon } = config[severity]
  return (
    <span className={`inline-flex items-center gap-1 text-xs px-2 py-0.5 rounded font-medium
      ${variant === 'error' ? 'bg-[var(--accent-error)]/20 text-[var(--accent-error)]' : ''}
      ${variant === 'warning' ? 'bg-[var(--accent-warning)]/20 text-[var(--accent-warning)]' : ''}
      ${variant === 'accent' ? 'bg-[var(--accent-primary)]/20 text-[var(--accent-primary)]' : ''}
    `}>
      {icon}
      {severity}
    </span>
  )
}

function MetricCard({
  title,
  value,
  unit,
  trend,
  trendValue,
}: {
  title: string
  value: number
  unit: string
  trend: 'up' | 'down' | 'stable'
  trendValue?: string
}) {
  const TrendIcon = trend === 'up' ? TrendingUp : trend === 'down' ? TrendingDown : Minus
  const trendColor = trend === 'up' ? 'text-[var(--accent-error)]' : trend === 'down' ? 'text-[var(--accent-success)]' : 'text-[var(--text-muted)]'

  return (
    <div className="bg-[var(--bg-secondary)] rounded-lg border border-[var(--border-primary)] p-4">
      <div className="flex items-center justify-between mb-2">
        <span className="text-xs text-[var(--text-secondary)]">{title}</span>
        {trendValue && (
          <div className={`flex items-center gap-1 text-xs ${trendColor}`}>
            <TrendIcon className="h-3 w-3" />
            <span>{trendValue}</span>
          </div>
        )}
      </div>
      <div className="flex items-baseline gap-1">
        <span className="text-2xl font-semibold text-[var(--text-primary)]">{value}</span>
        <span className="text-sm text-[var(--text-muted)]">{unit}</span>
      </div>
    </div>
  )
}

function MiniChart({ data, color }: { data: number[]; color: string }) {
  const max = Math.max(...data)
  const min = Math.min(...data)
  const range = max - min || 1

  return (
    <div className="flex items-end gap-0.5 h-8">
      {data.map((value, idx) => (
        <div
          key={idx}
          className="w-1 rounded-t"
          style={{
            height: `${((value - min) / range) * 100}%`,
            minHeight: '4px',
            backgroundColor: color,
            opacity: 0.6 + (idx / data.length) * 0.4,
          }}
        />
      ))}
    </div>
  )
}

export function MonitoringSection() {
  const [alertFilter, setAlertFilter] = useState<'all' | 'firing' | 'resolved'>('all')

  const filteredAlerts = alertFilter === 'all'
    ? mockAlerts
    : mockAlerts.filter((a) => a.status === alertFilter)

  // Sample metric data
  const cpuData = [45, 52, 48, 61, 58, 72, 68, 75, 82, 78, 85, 80]
  const memoryData = [62, 64, 65, 68, 70, 72, 75, 78, 82, 85, 88, 92]
  const requestsData = [120, 135, 142, 128, 156, 168, 145, 172, 185, 192, 178, 195]

  const alertColumns = [
    {
      key: 'severity',
      header: 'Severity',
      width: '100px',
      render: (a: Alert) => <SeverityBadge severity={a.severity} />,
    },
    {
      key: 'name',
      header: 'Alert',
      render: (a: Alert) => (
        <div>
          <div className="font-medium text-[var(--text-primary)]">{a.name}</div>
          <div className="text-xs text-[var(--text-muted)]">{a.message}</div>
        </div>
      ),
    },
    {
      key: 'source',
      header: 'Source',
      width: '120px',
      render: (a: Alert) => (
        <span className="text-xs text-[var(--text-secondary)]">{a.source}</span>
      ),
    },
    {
      key: 'status',
      header: 'Status',
      width: '100px',
      render: (a: Alert) => <StatusBadge status={a.status} />,
    },
    {
      key: 'time',
      header: 'Time',
      width: '100px',
      render: (a: Alert) => (
        <span className="text-xs text-[var(--text-muted)]">{formatRelativeTime(a.timestamp)}</span>
      ),
    },
    {
      key: 'actions',
      header: '',
      width: '80px',
      render: (a: Alert) => (
        <div className="flex items-center gap-1">
          {a.status === 'firing' && (
            <IconButton icon={<Check className="h-3 w-3" />} size="sm" tooltip="Acknowledge" onClick={() => {}} />
          )}
          <IconButton icon={<BellOff className="h-3 w-3" />} size="sm" tooltip="Silence" onClick={() => {}} />
        </div>
      ),
    },
  ]

  return (
    <div className="h-full flex flex-col">
      <SectionHeader
        title="Monitoring"
        subtitle="System health & alerts"
        icon={<Activity className="h-5 w-5" />}
        onRefresh={() => {}}
      />

      <div className="flex-1 overflow-auto space-y-6 pr-1">
        {/* Metrics Overview */}
        <div>
          <h3 className="text-xs font-medium text-[var(--text-secondary)] mb-3">System Metrics</h3>
          <div className="grid grid-cols-3 gap-3">
            <div className="bg-[var(--bg-secondary)] rounded-lg border border-[var(--border-primary)] p-4">
              <div className="flex items-center justify-between mb-3">
                <span className="text-xs text-[var(--text-secondary)]">CPU Usage</span>
                <span className="text-xs text-[var(--accent-error)]">+12%</span>
              </div>
              <div className="flex items-end justify-between">
                <div>
                  <span className="text-2xl font-semibold text-[var(--text-primary)]">80</span>
                  <span className="text-sm text-[var(--text-muted)]">%</span>
                </div>
                <MiniChart data={cpuData} color="var(--accent-error)" />
              </div>
            </div>

            <div className="bg-[var(--bg-secondary)] rounded-lg border border-[var(--border-primary)] p-4">
              <div className="flex items-center justify-between mb-3">
                <span className="text-xs text-[var(--text-secondary)]">Memory</span>
                <span className="text-xs text-[var(--accent-warning)]">+8%</span>
              </div>
              <div className="flex items-end justify-between">
                <div>
                  <span className="text-2xl font-semibold text-[var(--text-primary)]">92</span>
                  <span className="text-sm text-[var(--text-muted)]">%</span>
                </div>
                <MiniChart data={memoryData} color="var(--accent-warning)" />
              </div>
            </div>

            <div className="bg-[var(--bg-secondary)] rounded-lg border border-[var(--border-primary)] p-4">
              <div className="flex items-center justify-between mb-3">
                <span className="text-xs text-[var(--text-secondary)]">Requests/min</span>
                <span className="text-xs text-[var(--accent-success)]">+15%</span>
              </div>
              <div className="flex items-end justify-between">
                <div>
                  <span className="text-2xl font-semibold text-[var(--text-primary)]">195</span>
                  <span className="text-sm text-[var(--text-muted)]">req/m</span>
                </div>
                <MiniChart data={requestsData} color="var(--accent-success)" />
              </div>
            </div>
          </div>
        </div>

        {/* Alerts */}
        <div>
          <div className="flex items-center justify-between mb-3">
            <h3 className="text-xs font-medium text-[var(--text-secondary)]">
              Active Alerts ({filteredAlerts.length})
            </h3>
            <div className="flex items-center gap-1">
              <button
                onClick={() => setAlertFilter('all')}
                className={`px-2 py-1 text-xs rounded ${alertFilter === 'all' ? 'bg-[var(--accent-primary)] text-white' : 'text-[var(--text-secondary)] hover:bg-[var(--bg-hover)]'}`}
              >
                All
              </button>
              <button
                onClick={() => setAlertFilter('firing')}
                className={`px-2 py-1 text-xs rounded ${alertFilter === 'firing' ? 'bg-[var(--accent-error)] text-white' : 'text-[var(--text-secondary)] hover:bg-[var(--bg-hover)]'}`}
              >
                Firing
              </button>
              <button
                onClick={() => setAlertFilter('resolved')}
                className={`px-2 py-1 text-xs rounded ${alertFilter === 'resolved' ? 'bg-[var(--accent-success)] text-white' : 'text-[var(--text-secondary)] hover:bg-[var(--bg-hover)]'}`}
              >
                Resolved
              </button>
            </div>
          </div>
          <DataTable
            data={filteredAlerts}
            columns={alertColumns}
            keyExtractor={(a) => a.id}
            emptyMessage="No alerts"
            compact
          />
        </div>
      </div>
    </div>
  )
}
