import {
  Box, Layers, GitBranch, Cloud, Boxes, Key,
  Activity, CheckCircle2, XCircle, AlertTriangle, Clock
} from 'lucide-react'
import { StatusBadge, ResourceCard, ProviderIcon } from '../shared'
import { mockContainers, mockPipelines, mockProviders, mockInfraStacks, mockAlerts } from '../../mock-data'
import type { DevOpsSection } from '../../types'

interface OverviewSectionProps {
  onNavigate: (section: DevOpsSection) => void
}

function StatCard({
  title,
  value,
  icon,
  items,
  onClick,
}: {
  title: string
  value: number
  icon: React.ReactNode
  items?: { label: string; value: number; variant?: 'success' | 'warning' | 'error' }[]
  onClick?: () => void
}) {
  return (
    <div
      onClick={onClick}
      className={`
        bg-[var(--bg-secondary)] rounded-lg border border-[var(--border-primary)] p-4
        ${onClick ? 'cursor-pointer hover:border-[var(--accent-primary)] transition-colors' : ''}
      `}
    >
      <div className="flex items-center gap-3 mb-3">
        <div className="p-2 bg-[var(--accent-primary)]/10 rounded-lg text-[var(--accent-primary)]">
          {icon}
        </div>
        <div>
          <div className="text-2xl font-semibold text-[var(--text-primary)]">{value}</div>
          <div className="text-xs text-[var(--text-secondary)]">{title}</div>
        </div>
      </div>
      {items && items.length > 0 && (
        <div className="flex items-center gap-3 pt-3 border-t border-[var(--border-subtle)]">
          {items.map((item, idx) => (
            <div key={idx} className="flex items-center gap-1.5">
              <div
                className={`w-2 h-2 rounded-full ${
                  item.variant === 'success' ? 'bg-[var(--accent-success)]' :
                  item.variant === 'warning' ? 'bg-[var(--accent-warning)]' :
                  item.variant === 'error' ? 'bg-[var(--accent-error)]' :
                  'bg-[var(--text-muted)]'
                }`}
              />
              <span className="text-xs text-[var(--text-muted)]">{item.value} {item.label}</span>
            </div>
          ))}
        </div>
      )}
    </div>
  )
}

function RecentPipelineRun({ pipeline }: { pipeline: typeof mockPipelines[0] }) {
  const run = pipeline.lastRun
  if (!run) return null

  const StatusIcon = run.status === 'success' ? CheckCircle2 :
                     run.status === 'failure' ? XCircle :
                     run.status === 'running' ? Clock : AlertTriangle

  const statusColor = run.status === 'success' ? 'text-[var(--accent-success)]' :
                      run.status === 'failure' ? 'text-[var(--accent-error)]' :
                      run.status === 'running' ? 'text-[var(--accent-primary)]' :
                      'text-[var(--text-muted)]'

  return (
    <div className="flex items-center justify-between py-2 px-3 hover:bg-[var(--bg-hover)] rounded transition-colors">
      <div className="flex items-center gap-3">
        <ProviderIcon provider={pipeline.provider} size="sm" />
        <div>
          <div className="text-sm text-[var(--text-primary)]">{pipeline.name}</div>
          <div className="text-xs text-[var(--text-muted)]">{run.commitMessage.slice(0, 50)}</div>
        </div>
      </div>
      <div className="flex items-center gap-2">
        <StatusIcon className={`h-4 w-4 ${statusColor}`} />
        <span className="text-xs font-mono text-[var(--text-muted)]">#{run.number}</span>
      </div>
    </div>
  )
}

function ActiveAlert({ alert }: { alert: typeof mockAlerts[0] }) {
  const severityColor = alert.severity === 'critical' ? 'border-l-[var(--accent-error)]' :
                        alert.severity === 'warning' ? 'border-l-[var(--accent-warning)]' :
                        'border-l-[var(--accent-primary)]'

  return (
    <div className={`py-2 px-3 border-l-2 ${severityColor} hover:bg-[var(--bg-hover)] rounded-r transition-colors`}>
      <div className="flex items-center justify-between">
        <span className="text-sm text-[var(--text-primary)]">{alert.name}</span>
        <StatusBadge status={alert.status} size="sm" />
      </div>
      <p className="text-xs text-[var(--text-muted)] mt-0.5">{alert.message}</p>
    </div>
  )
}

export function OverviewSection({ onNavigate }: OverviewSectionProps) {
  const runningContainers = mockContainers.filter((c) => c.state === 'running').length
  const stoppedContainers = mockContainers.filter((c) => c.state !== 'running').length

  const successPipelines = mockPipelines.filter((p) => p.lastRun?.status === 'success').length
  const failedPipelines = mockPipelines.filter((p) => p.lastRun?.status === 'failure').length
  const runningPipelines = mockPipelines.filter((p) => p.lastRun?.status === 'running').length

  const connectedProviders = mockProviders.filter((p) => p.status === 'connected').length
  const errorProviders = mockProviders.filter((p) => p.status === 'error').length

  const firingAlerts = mockAlerts.filter((a) => a.status === 'firing').length

  return (
    <div className="h-full overflow-auto pr-1">
      <div className="mb-6">
        <h2 className="text-lg font-semibold text-[var(--text-primary)] mb-1">DevOps Overview</h2>
        <p className="text-sm text-[var(--text-secondary)]">
          Monitor your infrastructure, pipelines, and deployments
        </p>
      </div>

      {/* Quick Stats */}
      <div className="grid grid-cols-2 lg:grid-cols-4 gap-3 mb-6">
        <StatCard
          title="Containers"
          value={mockContainers.length}
          icon={<Box className="h-5 w-5" />}
          items={[
            { label: 'running', value: runningContainers, variant: 'success' },
            { label: 'stopped', value: stoppedContainers },
          ]}
          onClick={() => onNavigate('containers')}
        />
        <StatCard
          title="Pipelines"
          value={mockPipelines.length}
          icon={<GitBranch className="h-5 w-5" />}
          items={[
            { label: 'success', value: successPipelines, variant: 'success' },
            { label: 'failed', value: failedPipelines, variant: 'error' },
            { label: 'running', value: runningPipelines, variant: 'warning' },
          ]}
          onClick={() => onNavigate('pipelines')}
        />
        <StatCard
          title="Cloud Providers"
          value={mockProviders.length}
          icon={<Cloud className="h-5 w-5" />}
          items={[
            { label: 'connected', value: connectedProviders, variant: 'success' },
            { label: 'error', value: errorProviders, variant: 'error' },
          ]}
          onClick={() => onNavigate('providers')}
        />
        <StatCard
          title="Infra Stacks"
          value={mockInfraStacks.length}
          icon={<Boxes className="h-5 w-5" />}
          items={[
            { label: 'active', value: mockInfraStacks.filter((s) => s.status === 'active').length, variant: 'success' },
          ]}
          onClick={() => onNavigate('infrastructure')}
        />
      </div>

      {/* Two Column Layout */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Recent Pipeline Runs */}
        <div className="bg-[var(--bg-secondary)] rounded-lg border border-[var(--border-primary)] overflow-hidden">
          <div className="flex items-center justify-between px-4 py-3 border-b border-[var(--border-subtle)]">
            <h3 className="text-sm font-medium text-[var(--text-primary)]">Recent Pipeline Runs</h3>
            <button
              onClick={() => onNavigate('pipelines')}
              className="text-xs text-[var(--accent-primary)] hover:underline"
            >
              View all
            </button>
          </div>
          <div className="p-2">
            {mockPipelines.slice(0, 4).map((pipeline) => (
              <RecentPipelineRun key={pipeline.id} pipeline={pipeline} />
            ))}
          </div>
        </div>

        {/* Active Alerts */}
        <div className="bg-[var(--bg-secondary)] rounded-lg border border-[var(--border-primary)] overflow-hidden">
          <div className="flex items-center justify-between px-4 py-3 border-b border-[var(--border-subtle)]">
            <div className="flex items-center gap-2">
              <h3 className="text-sm font-medium text-[var(--text-primary)]">Active Alerts</h3>
              {firingAlerts > 0 && (
                <span className="px-1.5 py-0.5 text-xs bg-[var(--accent-error)]/20 text-[var(--accent-error)] rounded">
                  {firingAlerts} firing
                </span>
              )}
            </div>
            <button
              onClick={() => onNavigate('monitoring')}
              className="text-xs text-[var(--accent-primary)] hover:underline"
            >
              View all
            </button>
          </div>
          <div className="p-2 space-y-1">
            {mockAlerts.slice(0, 4).map((alert) => (
              <ActiveAlert key={alert.id} alert={alert} />
            ))}
          </div>
        </div>
      </div>
    </div>
  )
}
