import React from 'react'
import { useQuery } from '@tanstack/react-query'
import { getDefaultApiClient } from '@shared/api/client'
import { Button, Input, Select, SelectContent, SelectItem, SelectTrigger, SelectValue, Badge, Spinner } from '@persona-framework/ui'
import { Info, Search } from 'lucide-react'

interface AgentEvent {
  id: string
  timestamp: string
  taskId: string
  type: string
  message: string
  toolName?: string
  success?: boolean
  // Token usage metadata (if provided by backend)
  inputTokens?: number
  outputTokens?: number
  totalTokens?: number
  model?: string
}

export function ActivityPanel({ onViewDetails }: { onViewDetails?: (taskId: string) => void }) {
  const [typeFilter, setTypeFilter] = React.useState<string>('all')
  const [searchQuery, setSearchQuery] = React.useState('')
  const [taskIdFilter, setTaskIdFilter] = React.useState('')

  const { data: events = [], isLoading } = useQuery<AgentEvent[]>({
    queryKey: ['agent-events-panel'],
    queryFn: async () => {
      const client = getDefaultApiClient()
      const res = await client.get('/agent/events', { params: { limit: 200 } })
      return res?.data || []
    },
    refetchInterval: 3000,
    retry: false,
  })

  const filteredEvents = React.useMemo(() => {
    return events.filter(event => {
      if (typeFilter !== 'all' && event.type !== typeFilter) return false
      if (taskIdFilter && !event.taskId.includes(taskIdFilter)) return false
      if (searchQuery && !event.message.toLowerCase().includes(searchQuery.toLowerCase())) return false
      return true
    })
  }, [events, typeFilter, taskIdFilter, searchQuery])

  // Calculate aggregate token statistics
  const stats = React.useMemo(() => {
    const totalTokens = events.reduce((sum, e) => sum + (e.totalTokens || 0), 0)
    const eventsWithTokens = events.filter(e => e.totalTokens).length

    return {
      totalTokens,
      hasTokenData: eventsWithTokens > 0,
    }
  }, [events])

  const getTypeColor = (type: string) => {
    switch (type) {
      case 'error':
      case 'failed':
        return '#f48771'
      case 'success':
      case 'completed':
        return '#73c991'
      case 'progress':
      case 'started':
        return '#4fc1ff'
      default:
        return '#cccccc'
    }
  }

  if (isLoading) {
    return (
      <div className="flex flex-col h-full bg-[#1e1e1e] text-[#cccccc]">
        <div className="p-2 border-b border-[#3c3c3c] text-[13px] font-semibold">Agent Activity</div>
        <div className="p-4 flex justify-center">
          <Spinner className="h-5 w-5" />
          <span className="ml-2 text-sm">Loading events...</span>
        </div>
      </div>
    )
  }

  return (
    <div className="flex flex-col h-full bg-[#1e1e1e] text-[#cccccc]">
      <div className="p-2 border-b border-[#3c3c3c] text-[13px] font-semibold">Agent Activity</div>
      <div className="p-2 border-b border-[#3c3c3c] flex flex-col gap-1.5">
        <div className="flex gap-1.5">
          <Select value={typeFilter} onValueChange={setTypeFilter}>
            <SelectTrigger className="min-w-[120px] h-7 text-xs bg-[#3c3c3c] border-[#3c3c3c]">
              <SelectValue placeholder="All Types" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Types</SelectItem>
              <SelectItem value="started">Started</SelectItem>
              <SelectItem value="progress">Progress</SelectItem>
              <SelectItem value="success">Success</SelectItem>
              <SelectItem value="failed">Failed</SelectItem>
              <SelectItem value="error">Error</SelectItem>
              <SelectItem value="completed">Completed</SelectItem>
            </SelectContent>
          </Select>
          <div className="relative flex-1">
            <Search className="absolute left-2 top-1/2 -translate-y-1/2 h-3.5 w-3.5 text-[#858585]" />
            <Input
              placeholder="Search messages..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="h-7 text-xs pl-7 bg-[#3c3c3c] border-[#3c3c3c]"
            />
          </div>
        </div>
        <Input
          placeholder="Filter by Task ID..."
          value={taskIdFilter}
          onChange={(e) => setTaskIdFilter(e.target.value)}
          className="h-7 text-xs bg-[#3c3c3c] border-[#3c3c3c]"
        />
        <div className="text-[10px] text-[#858585]">
          Showing {filteredEvents.length} of {events.length} events
        </div>
      </div>
      {stats.hasTokenData && (
        <div className="px-3 py-2 border-b border-[#3c3c3c] bg-[#252525] flex gap-4 items-center text-[11px]">
          <span className="text-[#858585] font-semibold">SESSION TOTALS:</span>
          <div className="flex items-center gap-1">
            <span className="text-[#858585]">Tokens:</span>
            <span className="text-[#4fc1ff] font-semibold">
              {stats.totalTokens.toLocaleString()}
            </span>
          </div>
        </div>
      )}
      <div className="flex-1 overflow-y-auto p-3 flex flex-col gap-2">
        {filteredEvents.length === 0 ? (
          <div className="text-[#858585] text-center p-4">
            No recent activity
          </div>
        ) : (
          filteredEvents.map((event) => (
            <div
              key={event.id}
              className="p-2 bg-[#252525] rounded text-xs"
              style={{ borderLeft: `3px solid ${getTypeColor(event.type)}` }}
            >
              <div className="flex justify-between mb-1">
                <Badge variant="secondary" className="text-[10px] px-1.5 py-0">
                  {event.type}
                </Badge>
                <span className="text-[10px] text-[#858585]">
                  {new Date(event.timestamp).toLocaleTimeString()}
                </span>
              </div>
              <div className="text-xs mb-1">
                {event.message}
              </div>
              {event.toolName && (
                <div className="text-[10px] text-[#858585]">
                  Tool: {event.toolName}
                </div>
              )}
              {(event.totalTokens || event.model) && (
                <div className="text-[10px] mt-1.5 p-1.5 bg-[#1e1e1e] rounded flex gap-3 flex-wrap border-l-2 border-[#007acc]">
                  {event.totalTokens && (
                    <div className="flex flex-col gap-0.5">
                      <span className="text-[#858585] text-[9px]">TOKENS</span>
                      <span className="text-[#4fc1ff] font-semibold">
                        {event.totalTokens.toLocaleString()}
                      </span>
                      {event.inputTokens !== undefined && event.outputTokens !== undefined && (
                        <span className="text-[#666] text-[9px]">
                          {event.inputTokens.toLocaleString()} in / {event.outputTokens.toLocaleString()} out
                        </span>
                      )}
                    </div>
                  )}
                  {event.model && (
                    <div className="flex flex-col gap-0.5">
                      <span className="text-[#858585] text-[9px]">MODEL</span>
                      <span className="text-[#a78bfa] text-[10px]">
                        {event.model.replace('claude-', '').replace('gpt-', '')}
                      </span>
                    </div>
                  )}
                </div>
              )}
              {onViewDetails && (
                <Button
                  size="sm"
                  variant="ghost"
                  onClick={() => onViewDetails(event.taskId)}
                  className="mt-1.5 h-6 text-[11px] text-[#cccccc] hover:bg-[#3c3c3c]"
                >
                  <Info className="h-3 w-3 mr-1" />
                  View Task
                </Button>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  )
}

