import React, { useState, useEffect } from 'react'
import { Button } from '@persona-framework/ui'
import { Bookmark, Trash2, Plus, FileCode, X, Tag } from 'lucide-react'

const STORAGE_KEY = 'wraith_bookmarks'

interface BookmarkItem {
  id: string
  filePath: string
  line: number
  label?: string
  color: 'blue' | 'green' | 'yellow' | 'red' | 'purple'
  createdAt: number
}

const COLORS = {
  blue: { bg: 'bg-[rgba(91,206,250,0.2)]', border: 'border-[#5bcefa]', text: 'text-[#5bcefa]' },
  green: { bg: 'bg-[rgba(0,255,127,0.2)]', border: 'border-[#00ff7f]', text: 'text-[#00ff7f]' },
  yellow: { bg: 'bg-[rgba(255,215,0,0.2)]', border: 'border-[#ffd700]', text: 'text-[#ffd700]' },
  red: { bg: 'bg-[rgba(244,135,113,0.2)]', border: 'border-[#f48771]', text: 'text-[#f48771]' },
  purple: { bg: 'bg-[rgba(157,0,255,0.2)]', border: 'border-[#9d00ff]', text: 'text-[#9d00ff]' },
}

function loadBookmarks(): BookmarkItem[] {
  try {
    const stored = localStorage.getItem(STORAGE_KEY)
    if (stored) {
      const parsed = JSON.parse(stored)
      if (Array.isArray(parsed)) return parsed
    }
  } catch {
    // Invalid storage
  }
  return []
}

function saveBookmarks(bookmarks: BookmarkItem[]) {
  try {
    localStorage.setItem(STORAGE_KEY, JSON.stringify(bookmarks))
  } catch {
    // Storage unavailable
  }
}

interface BookmarksPanelProps {
  onFileClick?: (filePath: string, line?: number) => void
}

export function BookmarksPanel({ onFileClick }: BookmarksPanelProps) {
  const [bookmarks, setBookmarks] = useState<BookmarkItem[]>(() => loadBookmarks())
  const [showAddForm, setShowAddForm] = useState(false)
  const [newBookmark, setNewBookmark] = useState({
    filePath: '',
    line: 1,
    label: '',
    color: 'blue' as BookmarkItem['color'],
  })
  const [filter, setFilter] = useState('')

  useEffect(() => {
    saveBookmarks(bookmarks)
  }, [bookmarks])

  const addBookmark = () => {
    if (!newBookmark.filePath.trim()) return

    const bookmark: BookmarkItem = {
      id: crypto.randomUUID(),
      filePath: newBookmark.filePath.trim(),
      line: newBookmark.line,
      label: newBookmark.label.trim() || undefined,
      color: newBookmark.color,
      createdAt: Date.now(),
    }

    setBookmarks([bookmark, ...bookmarks])
    setNewBookmark({ filePath: '', line: 1, label: '', color: 'blue' })
    setShowAddForm(false)
  }

  const removeBookmark = (id: string) => {
    setBookmarks(bookmarks.filter(b => b.id !== id))
  }

  const handleBookmarkClick = (bookmark: BookmarkItem) => {
    onFileClick?.(bookmark.filePath, bookmark.line)
  }

  const filteredBookmarks = bookmarks.filter(b =>
    b.filePath.toLowerCase().includes(filter.toLowerCase()) ||
    b.label?.toLowerCase().includes(filter.toLowerCase())
  )

  const getFileName = (path: string) => path.split('/').pop() || path

  return (
    <div className="flex flex-col h-full bg-[#1e1e1e] text-[#cccccc] overflow-hidden">
      <div className="p-3 border-b border-[#3c3c3c] bg-[#252526]">
        <div className="flex items-center justify-between mb-3">
          <div className="flex items-center gap-2">
            <Bookmark className="w-4 h-4 text-[#5bcefa]" />
            <span className="text-sm font-medium text-white">Bookmarks</span>
            <span className="text-xs text-[#808080]">({bookmarks.length})</span>
          </div>
          <Button
            size="sm"
            variant="ghost"
            onClick={() => setShowAddForm(!showAddForm)}
            className="h-7 w-7 p-0"
            aria-label={showAddForm ? 'Cancel add bookmark' : 'Add bookmark'}
          >
            {showAddForm ? <X className="w-4 h-4" /> : <Plus className="w-4 h-4" />}
          </Button>
        </div>

        <input
          type="text"
          placeholder="Filter bookmarks..."
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
          className="w-full px-3 py-1.5 text-sm bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white placeholder-[#808080] focus:outline-none focus:border-[#5bcefa]"
        />
      </div>

      {showAddForm && (
        <div className="p-3 border-b border-[#3c3c3c] bg-[#2d2d30]">
          <div className="flex flex-col gap-2">
            <input
              type="text"
              placeholder="File path (e.g., src/index.ts)"
              value={newBookmark.filePath}
              onChange={(e) => setNewBookmark({ ...newBookmark, filePath: e.target.value })}
              className="w-full px-3 py-1.5 text-sm bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white placeholder-[#808080] focus:outline-none focus:border-[#5bcefa]"
            />
            <div className="flex gap-2">
              <input
                type="number"
                min={1}
                placeholder="Line"
                value={newBookmark.line}
                onChange={(e) => setNewBookmark({ ...newBookmark, line: parseInt(e.target.value) || 1 })}
                className="w-20 px-3 py-1.5 text-sm bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white placeholder-[#808080] focus:outline-none focus:border-[#5bcefa]"
              />
              <input
                type="text"
                placeholder="Label (optional)"
                value={newBookmark.label}
                onChange={(e) => setNewBookmark({ ...newBookmark, label: e.target.value })}
                className="flex-1 px-3 py-1.5 text-sm bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white placeholder-[#808080] focus:outline-none focus:border-[#5bcefa]"
              />
            </div>
            <div className="flex items-center gap-2">
              <span className="text-xs text-[#808080]">Color:</span>
              {(Object.keys(COLORS) as BookmarkItem['color'][]).map(color => (
                <button
                  key={color}
                  onClick={() => setNewBookmark({ ...newBookmark, color })}
                  className={`w-5 h-5 rounded-full border-2 transition-transform ${
                    newBookmark.color === color ? 'scale-125' : ''
                  } ${COLORS[color].bg} ${COLORS[color].border}`}
                  aria-label={`Select ${color} color`}
                />
              ))}
            </div>
            <Button size="sm" onClick={addBookmark} disabled={!newBookmark.filePath.trim()}>
              <Plus className="w-3.5 h-3.5 mr-1.5" />
              Add Bookmark
            </Button>
          </div>
        </div>
      )}

      <div className="flex-1 overflow-auto">
        {filteredBookmarks.length === 0 ? (
          <div className="flex flex-col items-center justify-center h-full p-6 text-center">
            <div className="p-3 bg-[#252526] rounded-full mb-4">
              <Bookmark className="w-8 h-8 text-[#858585]" />
            </div>
            <div className="text-sm text-[#cccccc] font-medium mb-1">
              {bookmarks.length === 0 ? 'No bookmarks yet' : 'No matching bookmarks'}
            </div>
            <div className="text-xs text-[#858585]">
              {bookmarks.length === 0
                ? 'Add bookmarks to quickly navigate to important code'
                : 'Try a different search term'}
            </div>
          </div>
        ) : (
          <div className="p-2">
            {filteredBookmarks.map(bookmark => {
              const colorStyle = COLORS[bookmark.color]
              return (
                <div
                  key={bookmark.id}
                  className={`group flex items-start gap-2 p-2 rounded-md cursor-pointer transition-all hover:bg-[#2d2d30] mb-1 border-l-2 ${colorStyle.border}`}
                  onClick={() => handleBookmarkClick(bookmark)}
                >
                  <FileCode className={`w-4 h-4 mt-0.5 flex-shrink-0 ${colorStyle.text}`} />
                  <div className="flex-1 min-w-0">
                    <div className="text-sm text-white truncate" title={bookmark.filePath}>
                      {getFileName(bookmark.filePath)}
                    </div>
                    <div className="text-xs text-[#808080] truncate" title={bookmark.filePath}>
                      {bookmark.filePath}:{bookmark.line}
                    </div>
                    {bookmark.label && (
                      <div className="flex items-center gap-1 mt-1">
                        <Tag className="w-3 h-3 text-[#808080]" />
                        <span className="text-xs text-[#b0b0b0]">{bookmark.label}</span>
                      </div>
                    )}
                  </div>
                  <button
                    onClick={(e) => {
                      e.stopPropagation()
                      removeBookmark(bookmark.id)
                    }}
                    className="opacity-0 group-hover:opacity-100 p-1 rounded hover:bg-[#5a1d1d] transition-opacity"
                    aria-label="Remove bookmark"
                  >
                    <Trash2 className="w-3.5 h-3.5 text-[#f48771]" />
                  </button>
                </div>
              )
            })}
          </div>
        )}
      </div>
    </div>
  )
}

