import React, { useState, useEffect } from 'react'
import { Button } from '@persona-framework/ui'
import {
  Settings, Plus, Trash2, Eye, EyeOff, Copy, Check, ChevronDown, ChevronRight,
  Shield, AlertTriangle, Download, Upload
} from 'lucide-react'

const STORAGE_KEY = 'wraith_environments'

type EnvironmentType = 'development' | 'staging' | 'production' | 'custom'

interface EnvVariable {
  key: string
  value: string
  isSecret: boolean
}

interface Environment {
  id: string
  name: string
  type: EnvironmentType
  variables: EnvVariable[]
  isActive: boolean
}

const ENV_TYPE_STYLES: Record<EnvironmentType, { bg: string; border: string; text: string }> = {
  development: {
    bg: 'bg-[rgba(0,255,127,0.2)]',
    border: 'border-[#00ff7f]',
    text: 'text-[#00ff7f]',
  },
  staging: {
    bg: 'bg-[rgba(255,215,0,0.2)]',
    border: 'border-[#ffd700]',
    text: 'text-[#ffd700]',
  },
  production: {
    bg: 'bg-[rgba(244,135,113,0.2)]',
    border: 'border-[#f48771]',
    text: 'text-[#f48771]',
  },
  custom: {
    bg: 'bg-[rgba(157,0,255,0.2)]',
    border: 'border-[#9d00ff]',
    text: 'text-[#9d00ff]',
  },
}

function loadEnvironments(): Environment[] {
  try {
    const stored = localStorage.getItem(STORAGE_KEY)
    if (stored) {
      const parsed = JSON.parse(stored)
      if (Array.isArray(parsed)) return parsed
    }
  } catch {
    // Invalid storage
  }
  return getDefaultEnvironments()
}

function saveEnvironments(envs: Environment[]) {
  try {
    localStorage.setItem(STORAGE_KEY, JSON.stringify(envs))
  } catch {
    // Storage unavailable
  }
}

function getDefaultEnvironments(): Environment[] {
  return [
    {
      id: 'dev',
      name: 'Development',
      type: 'development',
      isActive: true,
      variables: [
        { key: 'API_URL', value: 'http://localhost:3000', isSecret: false },
        { key: 'DEBUG', value: 'true', isSecret: false },
      ],
    },
    {
      id: 'staging',
      name: 'Staging',
      type: 'staging',
      isActive: false,
      variables: [
        { key: 'API_URL', value: 'https://staging.example.com', isSecret: false },
        { key: 'DEBUG', value: 'false', isSecret: false },
      ],
    },
    {
      id: 'prod',
      name: 'Production',
      type: 'production',
      isActive: false,
      variables: [
        { key: 'API_URL', value: 'https://api.example.com', isSecret: false },
        { key: 'API_KEY', value: 'sk-prod-xxxxx', isSecret: true },
      ],
    },
  ]
}

export function EnvironmentManagerPanel() {
  const [environments, setEnvironments] = useState<Environment[]>(() => loadEnvironments())
  const [expandedEnvs, setExpandedEnvs] = useState<Set<string>>(new Set(['dev']))
  const [revealedSecrets, setRevealedSecrets] = useState<Set<string>>(new Set())
  const [copiedKey, setCopiedKey] = useState<string | null>(null)
  const [showAddEnv, setShowAddEnv] = useState(false)
  const [newEnv, setNewEnv] = useState({ name: '', type: 'custom' as EnvironmentType })

  useEffect(() => {
    saveEnvironments(environments)
  }, [environments])

  const toggleExpanded = (envId: string) => {
    setExpandedEnvs(prev => {
      const next = new Set(prev)
      if (next.has(envId)) {
        next.delete(envId)
      } else {
        next.add(envId)
      }
      return next
    })
  }

  const toggleSecret = (key: string) => {
    setRevealedSecrets(prev => {
      const next = new Set(prev)
      if (next.has(key)) {
        next.delete(key)
      } else {
        next.add(key)
      }
      return next
    })
  }

  const copyValue = (key: string, value: string) => {
    navigator.clipboard.writeText(value)
    setCopiedKey(key)
    setTimeout(() => setCopiedKey(null), 2000)
  }

  const setActiveEnv = (envId: string) => {
    setEnvironments(environments.map(env => ({
      ...env,
      isActive: env.id === envId,
    })))
  }

  const addEnvironment = () => {
    if (!newEnv.name.trim()) return

    const env: Environment = {
      id: crypto.randomUUID(),
      name: newEnv.name.trim(),
      type: newEnv.type,
      isActive: false,
      variables: [],
    }

    setEnvironments([...environments, env])
    setNewEnv({ name: '', type: 'custom' })
    setShowAddEnv(false)
    setExpandedEnvs(prev => new Set([...prev, env.id]))
  }

  const deleteEnvironment = (envId: string) => {
    setEnvironments(environments.filter(e => e.id !== envId))
  }

  const addVariable = (envId: string) => {
    setEnvironments(environments.map(env => {
      if (env.id !== envId) return env
      return {
        ...env,
        variables: [...env.variables, { key: '', value: '', isSecret: false }],
      }
    }))
  }

  const updateVariable = (envId: string, index: number, updates: Partial<EnvVariable>) => {
    setEnvironments(environments.map(env => {
      if (env.id !== envId) return env
      const newVars = [...env.variables]
      newVars[index] = { ...newVars[index], ...updates }
      return { ...env, variables: newVars }
    }))
  }

  const deleteVariable = (envId: string, index: number) => {
    setEnvironments(environments.map(env => {
      if (env.id !== envId) return env
      return {
        ...env,
        variables: env.variables.filter((_, i) => i !== index),
      }
    }))
  }

  const exportEnv = (env: Environment) => {
    const content = env.variables
      .map(v => `${v.key}=${v.value}`)
      .join('\n')
    const blob = new Blob([content], { type: 'text/plain' })
    const url = URL.createObjectURL(blob)
    const a = document.createElement('a')
    a.href = url
    a.download = `.env.${env.name.toLowerCase().replace(/\s+/g, '-')}`
    a.click()
    URL.revokeObjectURL(url)
  }

  const activeEnv = environments.find(e => e.isActive)

  return (
    <div className="flex flex-col h-full bg-[#1e1e1e] text-[#cccccc] overflow-hidden">
      <div className="p-3 border-b border-[#3c3c3c] bg-[#252526]">
        <div className="flex items-center justify-between mb-2">
          <div className="flex items-center gap-2">
            <Settings className="w-4 h-4 text-[#5bcefa]" />
            <span className="text-sm font-medium text-white">Environments</span>
          </div>
          <Button
            size="sm"
            variant="ghost"
            onClick={() => setShowAddEnv(!showAddEnv)}
            className="h-7 w-7 p-0"
            aria-label="Add environment"
          >
            <Plus className="w-4 h-4" />
          </Button>
        </div>

        {activeEnv && (
          <div className={`flex items-center gap-2 px-2 py-1 rounded text-xs ${ENV_TYPE_STYLES[activeEnv.type].bg}`}>
            <div className={`w-2 h-2 rounded-full ${ENV_TYPE_STYLES[activeEnv.type].bg} ${ENV_TYPE_STYLES[activeEnv.type].border} border-2`} />
            <span className={ENV_TYPE_STYLES[activeEnv.type].text}>Active: {activeEnv.name}</span>
          </div>
        )}
      </div>

      {showAddEnv && (
        <div className="p-3 border-b border-[#3c3c3c] bg-[#2d2d30]">
          <div className="flex flex-col gap-2">
            <input
              type="text"
              placeholder="Environment name"
              value={newEnv.name}
              onChange={(e) => setNewEnv({ ...newEnv, name: e.target.value })}
              className="w-full px-3 py-1.5 text-sm bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white placeholder-[#808080] focus:outline-none focus:border-[#5bcefa]"
            />
            <select
              value={newEnv.type}
              onChange={(e) => setNewEnv({ ...newEnv, type: e.target.value as EnvironmentType })}
              className="w-full px-3 py-1.5 text-sm bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white focus:outline-none focus:border-[#5bcefa]"
            >
              <option value="development">Development</option>
              <option value="staging">Staging</option>
              <option value="production">Production</option>
              <option value="custom">Custom</option>
            </select>
            <Button size="sm" onClick={addEnvironment} disabled={!newEnv.name.trim()}>
              <Plus className="w-3.5 h-3.5 mr-1.5" />
              Add Environment
            </Button>
          </div>
        </div>
      )}

      <div className="flex-1 overflow-auto p-2">
        {environments.map(env => {
          const style = ENV_TYPE_STYLES[env.type]
          const isExpanded = expandedEnvs.has(env.id)

          return (
            <div key={env.id} className="mb-2">
              <div
                className={`flex items-center gap-2 p-2 rounded-md cursor-pointer transition-all hover:bg-[#2d2d30] ${
                  env.isActive ? `${style.bg} border ${style.border}` : ''
                }`}
                onClick={() => toggleExpanded(env.id)}
              >
                {isExpanded ? (
                  <ChevronDown className="w-4 h-4 text-[#808080]" />
                ) : (
                  <ChevronRight className="w-4 h-4 text-[#808080]" />
                )}
                <span className={`text-sm font-medium ${env.isActive ? style.text : 'text-white'}`}>
                  {env.name}
                </span>
                <span className={`text-[10px] uppercase px-1.5 py-0.5 rounded ${style.bg} ${style.text}`}>
                  {env.type}
                </span>
                <span className="text-xs text-[#808080]">({env.variables.length})</span>
                <div className="flex-1" />
                {!env.isActive && (
                  <button
                    onClick={(e) => {
                      e.stopPropagation()
                      setActiveEnv(env.id)
                    }}
                    className="px-2 py-0.5 text-[10px] bg-[#3c3c3c] text-[#b0b0b0] rounded hover:bg-[#4c4c4c] transition-colors"
                  >
                    Activate
                  </button>
                )}
                <button
                  onClick={(e) => {
                    e.stopPropagation()
                    exportEnv(env)
                  }}
                  className="p-1 rounded hover:bg-[#3c3c3c] transition-colors"
                  aria-label="Export environment"
                >
                  <Download className="w-3.5 h-3.5 text-[#808080]" />
                </button>
                <button
                  onClick={(e) => {
                    e.stopPropagation()
                    deleteEnvironment(env.id)
                  }}
                  className="p-1 rounded hover:bg-[#5a1d1d] transition-colors"
                  aria-label="Delete environment"
                >
                  <Trash2 className="w-3.5 h-3.5 text-[#f48771]" />
                </button>
              </div>

              {isExpanded && (
                <div className="ml-6 mt-1 border-l-2 border-[#3c3c3c] pl-3">
                  {env.variables.map((variable, idx) => (
                    <div key={idx} className="flex items-center gap-2 py-1.5">
                      <input
                        type="text"
                        placeholder="KEY"
                        value={variable.key}
                        onChange={(e) => updateVariable(env.id, idx, { key: e.target.value })}
                        className="w-32 px-2 py-1 text-xs bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white font-mono placeholder-[#808080] focus:outline-none focus:border-[#5bcefa]"
                      />
                      <span className="text-[#606060]">=</span>
                      <div className="flex-1 flex items-center gap-1">
                        <input
                          type={variable.isSecret && !revealedSecrets.has(`${env.id}-${idx}`) ? 'password' : 'text'}
                          placeholder="value"
                          value={variable.value}
                          onChange={(e) => updateVariable(env.id, idx, { value: e.target.value })}
                          className="flex-1 px-2 py-1 text-xs bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white font-mono placeholder-[#808080] focus:outline-none focus:border-[#5bcefa]"
                        />
                      </div>
                      <button
                        onClick={() => updateVariable(env.id, idx, { isSecret: !variable.isSecret })}
                        className={`p-1 rounded transition-colors ${
                          variable.isSecret ? 'bg-[rgba(244,135,113,0.2)] text-[#f48771]' : 'hover:bg-[#3c3c3c] text-[#808080]'
                        }`}
                        aria-label={variable.isSecret ? 'Mark as non-secret' : 'Mark as secret'}
                        title={variable.isSecret ? 'Secret' : 'Not secret'}
                      >
                        <Shield className="w-3.5 h-3.5" />
                      </button>
                      {variable.isSecret && (
                        <button
                          onClick={() => toggleSecret(`${env.id}-${idx}`)}
                          className="p-1 rounded hover:bg-[#3c3c3c] transition-colors"
                          aria-label={revealedSecrets.has(`${env.id}-${idx}`) ? 'Hide value' : 'Show value'}
                        >
                          {revealedSecrets.has(`${env.id}-${idx}`) ? (
                            <EyeOff className="w-3.5 h-3.5 text-[#808080]" />
                          ) : (
                            <Eye className="w-3.5 h-3.5 text-[#808080]" />
                          )}
                        </button>
                      )}
                      <button
                        onClick={() => copyValue(`${env.id}-${idx}`, variable.value)}
                        className="p-1 rounded hover:bg-[#3c3c3c] transition-colors"
                        aria-label="Copy value"
                      >
                        {copiedKey === `${env.id}-${idx}` ? (
                          <Check className="w-3.5 h-3.5 text-[#00ff7f]" />
                        ) : (
                          <Copy className="w-3.5 h-3.5 text-[#808080]" />
                        )}
                      </button>
                      <button
                        onClick={() => deleteVariable(env.id, idx)}
                        className="p-1 rounded hover:bg-[#5a1d1d] transition-colors"
                        aria-label="Delete variable"
                      >
                        <Trash2 className="w-3.5 h-3.5 text-[#f48771]" />
                      </button>
                    </div>
                  ))}
                  <button
                    onClick={() => addVariable(env.id)}
                    className="flex items-center gap-1.5 text-xs text-[#5bcefa] hover:text-[#7dd3fc] py-1.5 transition-colors"
                  >
                    <Plus className="w-3.5 h-3.5" />
                    Add Variable
                  </button>
                </div>
              )}
            </div>
          )
        })}
      </div>
    </div>
  )
}

