import React, { useState, useEffect } from 'react'
import { Button, Input, Badge, Tabs, TabsList, TabsTrigger, TabsContent } from '@persona-framework/ui'
import {
  AlertCircle,
  AlertTriangle,
  Info,
  X,
  RotateCcw,
  Search,
  Filter,
  ChevronRight,
} from 'lucide-react'

interface Problem {
  id: string
  severity: 'error' | 'warning' | 'info'
  message: string
  source: string
  filePath: string
  line: number
  column: number
  code?: string
  category?: string
}

interface ProblemGroup {
  filePath: string
  problems: Problem[]
  expanded: boolean
}

type TabValue = 'all' | 'errors' | 'warnings' | 'info'

export function ProblemsPanel() {
  const [activeTab, setActiveTab] = useState<TabValue>('all')
  const [searchQuery, setSearchQuery] = useState('')
  const [problemGroups, setProblemGroups] = useState<ProblemGroup[]>([])

  useEffect(() => {
    loadProblems()

    const interval = setInterval(loadProblems, 5000)
    return () => clearInterval(interval)
  }, [])

  const loadProblems = async () => {
    try {
      const response = await fetch('/api/diagnostics/all')
      if (response.ok) {
        const data = await response.json()
        const grouped = groupProblemsByFile(data.problems || getMockProblems())
        setProblemGroups(grouped)
      } else {
        const grouped = groupProblemsByFile(getMockProblems())
        setProblemGroups(grouped)
      }
    } catch (error) {
      console.error('Failed to load problems:', error)
      const grouped = groupProblemsByFile(getMockProblems())
      setProblemGroups(grouped)
    }
  }

  const getMockProblems = (): Problem[] => {
    return [
      {
        id: 'prob-1',
        severity: 'error',
        message: 'Unresolved reference: PersonaService',
        source: 'Kotlin Compiler',
        filePath: '/leviathan/src/main/kotlin/Controller.kt',
        line: 45,
        column: 23,
        code: 'UNRESOLVED_REFERENCE',
        category: 'compilation',
      },
      {
        id: 'prob-2',
        severity: 'error',
        message: 'Type mismatch: inferred type is String but Int was expected',
        source: 'Kotlin Compiler',
        filePath: '/leviathan/src/main/kotlin/Controller.kt',
        line: 67,
        column: 15,
        code: 'TYPE_MISMATCH',
        category: 'compilation',
      },
      {
        id: 'prob-3',
        severity: 'warning',
        message: 'Unused import statement',
        source: 'Kotlin Compiler',
        filePath: '/leviathan/src/main/kotlin/Controller.kt',
        line: 5,
        column: 1,
        code: 'UNUSED_IMPORT',
        category: 'style',
      },
      {
        id: 'prob-4',
        severity: 'error',
        message: 'cannot find symbol: method processData()',
        source: 'Java Compiler',
        filePath: '/persona-core/src/main/java/DataProcessor.java',
        line: 123,
        column: 12,
        category: 'compilation',
      },
      {
        id: 'prob-8',
        severity: 'error',
        message: "Property 'userData' does not exist on type 'User'",
        source: 'TypeScript',
        filePath: '/bael/src/components/UserProfile.tsx',
        line: 78,
        column: 18,
        code: 'TS2339',
        category: 'type-check',
      },
      {
        id: 'prob-9',
        severity: 'warning',
        message: "'useState' is defined but never used",
        source: 'ESLint',
        filePath: '/bael/src/components/UserProfile.tsx',
        line: 3,
        column: 10,
        code: 'no-unused-vars',
        category: 'lint',
      },
      {
        id: 'prob-10',
        severity: 'info',
        message: 'Consider using optional chaining: user?.name',
        source: 'ESLint',
        filePath: '/bael/src/components/UserProfile.tsx',
        line: 92,
        column: 20,
        code: 'prefer-optional-chain',
        category: 'suggestion',
      },
    ]
  }

  const groupProblemsByFile = (problems: Problem[]): ProblemGroup[] => {
    const groups: { [key: string]: Problem[] } = {}

    problems.forEach((problem) => {
      if (!groups[problem.filePath]) {
        groups[problem.filePath] = []
      }
      groups[problem.filePath].push(problem)
    })

    return Object.entries(groups).map(([filePath, problems]) => ({
      filePath,
      problems: problems.sort((a, b) => a.line - b.line),
      expanded: true,
    }))
  }

  const toggleGroup = (filePath: string) => {
    setProblemGroups((prev) =>
      prev.map((group) =>
        group.filePath === filePath ? { ...group, expanded: !group.expanded } : group
      )
    )
  }

  const getSeverityIcon = (severity: Problem['severity']) => {
    switch (severity) {
      case 'error':
        return <AlertCircle className="text-[#f48771] w-3.5 h-3.5" />
      case 'warning':
        return <AlertTriangle className="text-[#f59e0b] w-3.5 h-3.5" />
      case 'info':
        return <Info className="text-[#4fc3f7] w-3.5 h-3.5" />
    }
  }

  const filterProblems = (problems: Problem[]): Problem[] => {
    return problems.filter((problem) => {
      if (activeTab !== 'all') {
        if (activeTab === 'errors' && problem.severity !== 'error') return false
        if (activeTab === 'warnings' && problem.severity !== 'warning') return false
        if (activeTab === 'info' && problem.severity !== 'info') return false
      }

      if (searchQuery) {
        const query = searchQuery.toLowerCase()
        return (
          problem.message.toLowerCase().includes(query) ||
          problem.filePath.toLowerCase().includes(query) ||
          problem.source.toLowerCase().includes(query)
        )
      }

      return true
    })
  }

  const filteredGroups = problemGroups
    .map((group) => ({
      ...group,
      problems: filterProblems(group.problems),
    }))
    .filter((group) => group.problems.length > 0)

  const allProblems = problemGroups.flatMap((g) => g.problems)
  const errorCount = allProblems.filter((p) => p.severity === 'error').length
  const warningCount = allProblems.filter((p) => p.severity === 'warning').length
  const infoCount = allProblems.filter((p) => p.severity === 'info').length

  const handleProblemClick = (problem: Problem) => {
    console.log('Jump to:', problem.filePath, problem.line, problem.column)
  }

  return (
    <div className="flex flex-col h-full bg-[#1e1e1e] text-[#cccccc]">
      <div className="flex items-center justify-between px-3 py-2 border-b border-[#3c3c3c] bg-[#2b2b2b]">
        <div className="text-[13px] font-semibold text-white">Problems</div>

        <div className="flex gap-1">
          <Button
            variant="ghost"
            size="sm"
            onClick={loadProblems}
            title="Refresh"
          >
            <RotateCcw className="w-3.5 h-3.5" />
          </Button>
          <Button
            variant="ghost"
            size="sm"
            title="Clear All"
          >
            <X className="w-3.5 h-3.5" />
          </Button>
        </div>
      </div>

      <Tabs value={activeTab} onValueChange={(value) => setActiveTab(value as TabValue)} className="flex flex-col flex-1">
        <div className="px-3 py-2 border-b border-[#3c3c3c]">
          <TabsList>
            <TabsTrigger value="all">
              All
              <Badge variant="secondary" className="ml-1.5 text-[10px]">{allProblems.length}</Badge>
            </TabsTrigger>
            <TabsTrigger value="errors">
              Errors
              <Badge variant="destructive" className="ml-1.5 text-[10px]">{errorCount}</Badge>
            </TabsTrigger>
            <TabsTrigger value="warnings">
              Warnings
              <Badge variant="default" className="ml-1.5 text-[10px]">{warningCount}</Badge>
            </TabsTrigger>
            <TabsTrigger value="info">
              Info
              <Badge variant="secondary" className="ml-1.5 text-[10px]">{infoCount}</Badge>
            </TabsTrigger>
          </TabsList>
        </div>

        <div className="px-3 py-2 border-b border-[#3c3c3c]">
          <div className="relative">
            <Search className="absolute left-2 top-1/2 -translate-y-1/2 w-3.5 h-3.5 text-[#858585]" />
            <Input
              placeholder="Filter problems..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              size="sm"
              className="w-full pl-8"
            />
          </div>
        </div>

        <TabsContent value={activeTab} className="flex-1 overflow-auto">
          {filteredGroups.length === 0 && (
            <div className="px-5 py-10 text-center text-[#858585] text-[13px]">
              {allProblems.length === 0
                ? 'No problems detected!'
                : `No problems match your filter`}
            </div>
          )}

          {filteredGroups.map((group) => (
            <div key={group.filePath} className="border-b border-[#3c3c3c]">
              <div
                className="flex items-center gap-2 px-3 py-2 bg-[#252526] cursor-pointer hover:bg-[#2b2b2b]"
                onClick={() => toggleGroup(group.filePath)}
              >
                <ChevronRight
                  className={`w-3 h-3 min-w-[12px] transition-transform ${group.expanded ? 'rotate-90' : ''}`}
                />
                <div className="flex-1 text-xs font-semibold text-[#cccccc]">
                  {group.filePath.split('/').pop()}
                </div>
                <div className="text-[11px] text-[#858585]">
                  {group.problems.length} problem{group.problems.length !== 1 ? 's' : ''}
                </div>
              </div>

              {group.expanded && (
                <div className="bg-[#1e1e1e]">
                  {group.problems.map((problem) => (
                    <div
                      key={problem.id}
                      className="flex items-start gap-2 px-3 py-2 border-b border-[#2b2b2b] cursor-pointer hover:bg-[#252526] transition-colors"
                      onClick={() => handleProblemClick(problem)}
                    >
                      <div className="mt-0.5">{getSeverityIcon(problem.severity)}</div>
                      <div className="flex-1 min-w-0">
                        <div className="text-xs text-[#cccccc] mb-1 leading-[1.4]">{problem.message}</div>
                        <div className="flex items-center gap-2 text-[10px] text-[#858585]">
                          <span>{problem.source}</span>
                          <span>•</span>
                          <span className="font-mono">
                            {group.filePath.split('/').pop()}:{problem.line}:{problem.column}
                          </span>
                          {problem.code && (
                            <>
                              <span>•</span>
                              <span className="text-[#4fc3f7]">{problem.code}</span>
                            </>
                          )}
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          ))}
        </TabsContent>
      </Tabs>

      <div className="flex items-center gap-4 px-3 py-2 border-t border-[#3c3c3c] bg-[#252526] text-[11px]">
        <div className="flex items-center gap-1.5">
          <AlertCircle className="text-[#f48771] w-3 h-3" />
          <strong>{errorCount}</strong>
          <span>Errors</span>
        </div>
        <div className="flex items-center gap-1.5">
          <AlertTriangle className="text-[#f59e0b] w-3 h-3" />
          <strong>{warningCount}</strong>
          <span>Warnings</span>
        </div>
        <div className="flex items-center gap-1.5">
          <Info className="text-[#4fc3f7] w-3 h-3" />
          <strong>{infoCount}</strong>
          <span>Info</span>
        </div>
      </div>
    </div>
  )
}

