import React, { useState, useEffect } from 'react'
import { Button, Spinner } from '@persona-framework/ui'
import { CheckSquare, AlertTriangle, Wrench, Bug, RefreshCw, FileCode, Filter } from 'lucide-react'
import { makeHttpClient } from '../../../data/https'

const http = makeHttpClient('/api')

type TodoType = 'TODO' | 'FIXME' | 'HACK' | 'BUG' | 'NOTE'

interface TodoItem {
  id: string
  type: TodoType
  text: string
  filePath: string
  line: number
  author?: string
}

const TODO_STYLES: Record<TodoType, { icon: React.ReactNode; bg: string; border: string; text: string }> = {
  TODO: {
    icon: <CheckSquare className="w-3.5 h-3.5" />,
    bg: 'bg-[rgba(91,206,250,0.2)]',
    border: 'border-[#5bcefa]',
    text: 'text-[#5bcefa]',
  },
  FIXME: {
    icon: <Wrench className="w-3.5 h-3.5" />,
    bg: 'bg-[rgba(255,215,0,0.2)]',
    border: 'border-[#ffd700]',
    text: 'text-[#ffd700]',
  },
  HACK: {
    icon: <AlertTriangle className="w-3.5 h-3.5" />,
    bg: 'bg-[rgba(244,135,113,0.2)]',
    border: 'border-[#f48771]',
    text: 'text-[#f48771]',
  },
  BUG: {
    icon: <Bug className="w-3.5 h-3.5" />,
    bg: 'bg-[rgba(244,135,113,0.2)]',
    border: 'border-[#f48771]',
    text: 'text-[#f48771]',
  },
  NOTE: {
    icon: <FileCode className="w-3.5 h-3.5" />,
    bg: 'bg-[rgba(157,0,255,0.2)]',
    border: 'border-[#9d00ff]',
    text: 'text-[#9d00ff]',
  },
}

interface TodoTrackerPanelProps {
  onFileClick?: (filePath: string, line?: number) => void
}

export function TodoTrackerPanel({ onFileClick }: TodoTrackerPanelProps) {
  const [todos, setTodos] = useState<TodoItem[]>([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)
  const [filter, setFilter] = useState('')
  const [typeFilter, setTypeFilter] = useState<TodoType | 'ALL'>('ALL')
  const [scanning, setScanning] = useState(false)

  const fetchTodos = async () => {
    try {
      setLoading(true)
      setError(null)
      const response = await http.get('/nexus/todos')
      if (response.ok) {
        const data = await response.json()
        setTodos(data)
      } else if (response.status === 404) {
        // No todos endpoint yet, show demo data
        setTodos(getDemoTodos())
      } else {
        setError('Failed to fetch TODOs')
      }
    } catch {
      // API not available, show demo data
      setTodos(getDemoTodos())
    } finally {
      setLoading(false)
    }
  }

  const scanWorkspace = async () => {
    try {
      setScanning(true)
      const response = await http.post('/nexus/todos/scan', {})
      if (response.ok) {
        await fetchTodos()
      }
    } catch {
      // Scan failed silently
    } finally {
      setScanning(false)
    }
  }

  useEffect(() => {
    fetchTodos()
  }, [])

  const getDemoTodos = (): TodoItem[] => [
    { id: '1', type: 'TODO', text: 'Add error handling for edge cases', filePath: 'src/services/api.ts', line: 45 },
    { id: '2', type: 'FIXME', text: 'Memory leak in useEffect cleanup', filePath: 'src/hooks/useSubscription.ts', line: 23 },
    { id: '3', type: 'HACK', text: 'Temporary workaround for Safari bug', filePath: 'src/utils/browser.ts', line: 12 },
    { id: '4', type: 'BUG', text: 'Race condition in concurrent requests', filePath: 'src/data/https.ts', line: 89 },
    { id: '5', type: 'NOTE', text: 'Consider refactoring this module', filePath: 'src/legacy/parser.ts', line: 1 },
    { id: '6', type: 'TODO', text: 'Implement pagination for large datasets', filePath: 'src/components/DataGrid.tsx', line: 156 },
  ]

  const filteredTodos = todos.filter(todo => {
    const matchesText =
      todo.text.toLowerCase().includes(filter.toLowerCase()) ||
      todo.filePath.toLowerCase().includes(filter.toLowerCase())
    const matchesType = typeFilter === 'ALL' || todo.type === typeFilter
    return matchesText && matchesType
  })

  const groupedByFile = filteredTodos.reduce<Record<string, TodoItem[]>>((acc, todo) => {
    if (!acc[todo.filePath]) acc[todo.filePath] = []
    acc[todo.filePath].push(todo)
    return acc
  }, {})

  const counts = todos.reduce<Record<TodoType, number>>(
    (acc, todo) => {
      acc[todo.type] = (acc[todo.type] || 0) + 1
      return acc
    },
    { TODO: 0, FIXME: 0, HACK: 0, BUG: 0, NOTE: 0 }
  )

  const getFileName = (path: string) => path.split('/').pop() || path

  if (loading) {
    return (
      <div className="flex flex-col h-full bg-[#1e1e1e] text-[#cccccc] overflow-hidden">
        <div className="flex flex-col items-center justify-center h-full gap-3">
          <Spinner size="lg" />
          <div className="text-sm text-[#b0b0b0]">Scanning for TODOs...</div>
        </div>
      </div>
    )
  }

  if (error) {
    return (
      <div className="flex flex-col h-full bg-[#1e1e1e] text-[#cccccc] overflow-hidden">
        <div className="flex flex-col items-center justify-center h-full p-6">
          <div className="p-3 bg-[#5a1d1d] rounded-full mb-4">
            <AlertTriangle className="w-8 h-8 text-[#f48771]" />
          </div>
          <div className="text-sm text-[#f48771] font-medium mb-2">Failed to load TODOs</div>
          <div className="text-xs text-[#858585] text-center mb-4">{error}</div>
          <Button size="sm" variant="outline" onClick={fetchTodos}>
            <RefreshCw className="w-3.5 h-3.5 mr-1.5" />
            Retry
          </Button>
        </div>
      </div>
    )
  }

  return (
    <div className="flex flex-col h-full bg-[#1e1e1e] text-[#cccccc] overflow-hidden">
      <div className="p-3 border-b border-[#3c3c3c] bg-[#252526]">
        <div className="flex items-center justify-between mb-3">
          <div className="flex items-center gap-2">
            <CheckSquare className="w-4 h-4 text-[#5bcefa]" />
            <span className="text-sm font-medium text-white">TODO Tracker</span>
            <span className="text-xs text-[#808080]">({todos.length})</span>
          </div>
          <Button
            size="sm"
            variant="ghost"
            onClick={scanWorkspace}
            disabled={scanning}
            className="h-7 px-2"
            aria-label="Rescan workspace"
          >
            <RefreshCw className={`w-3.5 h-3.5 ${scanning ? 'animate-spin' : ''}`} />
          </Button>
        </div>

        <input
          type="text"
          placeholder="Filter TODOs..."
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
          className="w-full px-3 py-1.5 text-sm bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white placeholder-[#808080] focus:outline-none focus:border-[#5bcefa] mb-2"
        />

        <div className="flex gap-1 flex-wrap">
          <button
            onClick={() => setTypeFilter('ALL')}
            className={`px-2 py-1 text-[10px] font-medium rounded transition-colors ${
              typeFilter === 'ALL'
                ? 'bg-[#5bcefa] text-[#1e1e1e]'
                : 'bg-[#3c3c3c] text-[#b0b0b0] hover:bg-[#4c4c4c]'
            }`}
          >
            ALL ({todos.length})
          </button>
          {(Object.keys(TODO_STYLES) as TodoType[]).map(type => (
            <button
              key={type}
              onClick={() => setTypeFilter(type)}
              className={`px-2 py-1 text-[10px] font-medium rounded transition-colors ${
                typeFilter === type
                  ? `${TODO_STYLES[type].bg} ${TODO_STYLES[type].text} ${TODO_STYLES[type].border} border`
                  : 'bg-[#3c3c3c] text-[#b0b0b0] hover:bg-[#4c4c4c]'
              }`}
            >
              {type} ({counts[type]})
            </button>
          ))}
        </div>
      </div>

      <div className="flex-1 overflow-auto">
        {filteredTodos.length === 0 ? (
          <div className="flex flex-col items-center justify-center h-full p-6 text-center">
            <div className="p-3 bg-[#252526] rounded-full mb-4">
              <CheckSquare className="w-8 h-8 text-[#858585]" />
            </div>
            <div className="text-sm text-[#cccccc] font-medium mb-1">
              {todos.length === 0 ? 'No TODOs found' : 'No matching TODOs'}
            </div>
            <div className="text-xs text-[#858585]">
              {todos.length === 0
                ? 'Your codebase is clean!'
                : 'Try a different filter'}
            </div>
          </div>
        ) : (
          <div className="p-2">
            {Object.entries(groupedByFile).map(([filePath, items]) => (
              <div key={filePath} className="mb-3">
                <div className="flex items-center gap-2 px-2 py-1 text-xs text-[#808080]">
                  <FileCode className="w-3.5 h-3.5" />
                  <span className="font-medium text-[#b0b0b0]">{getFileName(filePath)}</span>
                  <span className="text-[#606060]">({items.length})</span>
                </div>
                {items.map(todo => {
                  const style = TODO_STYLES[todo.type]
                  return (
                    <div
                      key={todo.id}
                      className="flex items-start gap-2 p-2 ml-2 rounded-md cursor-pointer transition-all hover:bg-[#2d2d30] border-l-2 mb-1"
                      style={{ borderLeftColor: style.border.replace('border-[', '').replace(']', '') }}
                      onClick={() => onFileClick?.(filePath, todo.line)}
                    >
                      <span className={`flex-shrink-0 mt-0.5 ${style.text}`}>{style.icon}</span>
                      <div className="flex-1 min-w-0">
                        <div className="flex items-center gap-2 mb-0.5">
                          <span className={`text-[10px] font-bold uppercase ${style.text}`}>
                            {todo.type}
                          </span>
                          <span className="text-[10px] text-[#606060]">Line {todo.line}</span>
                        </div>
                        <div className="text-sm text-[#cccccc]">{todo.text}</div>
                      </div>
                    </div>
                  )
                })}
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  )
}

