import { useState } from "react"
import {
  Button,
  Card,
  Label,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  Spinner,
  Textarea,
} from "@persona-framework/ui"
import { Play } from "lucide-react"
import { makeHttpClient } from '../../../../data/https'

const http = makeHttpClient('/api')

interface PromptTestPanelProps {
  promptContent: string
  variables: Record<string, string>
}

export function PromptTestPanel({ promptContent, variables }: PromptTestPanelProps) {
  const [selectedModel, setSelectedModel] = useState("claude-3-sonnet")
  const [testInput, setTestInput] = useState("")
  const [output, setOutput] = useState("")
  const [isRunning, setIsRunning] = useState(false)
  const [metadata, setMetadata] = useState<{
    duration: number
    tokensUsed: number
  } | null>(null)

  const runTest = async () => {
    setIsRunning(true)
    setOutput("")
    setMetadata(null)

    const startTime = Date.now()

    let processedPrompt = promptContent
    Object.entries(variables).forEach(([key, value]) => {
      processedPrompt = processedPrompt.replace(new RegExp(`{{${key}}}`, "g"), value)
    })

    try {
      const response = await http.post('/public/chat/PAIMON', {
        messages: [
          { role: 'system', content: processedPrompt },
          { role: 'user', content: testInput || 'Hello' }
        ]
      })

      const duration = Date.now() - startTime
      const completion = response.data

      setOutput(completion.message || completion.content || JSON.stringify(completion, null, 2))
      setMetadata({
        tokensUsed: completion.tokensUsed || completion.usage?.totalTokens || 0,
        duration
      })
    } catch (error: any) {
      const duration = Date.now() - startTime
      setOutput(`Error: ${error?.response?.data?.message || error?.message || 'Failed to execute prompt'}`)
      setMetadata({ tokensUsed: 0, duration })
    } finally {
      setIsRunning(false)
    }
  }

  return (
    <div className="flex flex-col gap-3 h-full">
      <Label className="font-semibold">Test Prompt</Label>

      <div className="flex items-end flex-wrap gap-2">
        <div className="flex-1 min-w-[200px]">
          <Label className="text-sm">Model</Label>
          <Select value={selectedModel} onValueChange={setSelectedModel}>
            <SelectTrigger>
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="qwen2.5-coder:7b-instruct-q4_0">Qwen 2.5 Coder 7B</SelectItem>
              <SelectItem value="llama3.1:8b">Llama 3.1 8B</SelectItem>
              <SelectItem value="llama3.2:1b">Llama 3.2 1B</SelectItem>
            </SelectContent>
          </Select>
        </div>
        <Button
          disabled={isRunning || !promptContent}
          onClick={runTest}
        >
          <Play className="mr-2 h-4 w-4" />
          Run Test
        </Button>
      </div>

      <div>
        <Label className="text-sm">Test Input</Label>
        <Textarea
          onChange={(e) => setTestInput(e.target.value)}
          placeholder="Enter test input..."
          rows={3}
          value={testInput}
          className="resize-y"
        />
      </div>

      <div className="flex-1 min-h-[200px]">
        <Label className="text-sm">Output</Label>
        <Card className="flex-1 overflow-auto p-3">
          {isRunning && (
            <div className="flex items-center gap-2">
              <Spinner size="sm" />
              <span className="text-muted-foreground">Running test...</span>
            </div>
          )}
          {!isRunning && !output && (
            <div className="text-muted-foreground">
              Configure your prompt and click "Run Test" to see results.
            </div>
          )}
          {!isRunning && output && (
            <>
              <div className="whitespace-pre-wrap">
                {output}
              </div>
              {metadata && (
                <div className="flex flex-wrap gap-4 text-sm text-muted-foreground mt-2">
                  <span>
                    Tokens:
                    {" "}
                    <strong>{metadata.tokensUsed}</strong>
                  </span>
                  <span>
                    Duration:
                    {" "}
                    <strong>
                      {metadata.duration}
                      ms
                    </strong>
                  </span>
                </div>
              )}
            </>
          )}
        </Card>
      </div>
    </div>
  )
}
