interface AuthConfig {
  type: AuthType
  bearer?: { token: string }
  basic?: { username: string; password: string }
  apiKey?: { key: string; value: string; addTo: 'header' | 'query' }
  oauth2?: {
    grantType: 'authorization_code' | 'client_credentials' | 'password'
    accessTokenUrl: string
    clientId: string
    clientSecret: string
    scope?: string
  }
}

