import React from 'react'

interface SkeletonProps {
  className?: string
  width?: string | number
  height?: string | number
  rounded?: 'none' | 'sm' | 'md' | 'lg' | 'full'
  animate?: boolean
}

export function Skeleton({
  className = '',
  width,
  height,
  rounded = 'md',
  animate = true,
}: SkeletonProps) {
  const roundedClasses = {
    none: '',
    sm: 'rounded-sm',
    md: 'rounded',
    lg: 'rounded-lg',
    full: 'rounded-full',
  }

  const style: React.CSSProperties = {}
  if (width) style.width = typeof width === 'number' ? `${width}px` : width
  if (height) style.height = typeof height === 'number' ? `${height}px` : height

  return (
    <div
      className={`bg-[#3c3c3c] ${roundedClasses[rounded]} ${animate ? 'animate-pulse' : ''} ${className}`}
      style={style}
    />
  )
}

export function SkeletonText({
  lines = 3,
  className = '',
}: {
  lines?: number
  className?: string
}) {
  return (
    <div className={`space-y-2 ${className}`}>
      {Array.from({ length: lines }).map((_, i) => (
        <Skeleton
          key={i}
          height={14}
          width={i === lines - 1 ? '60%' : '100%'}
        />
      ))}
    </div>
  )
}

export function SkeletonCard({ className = '' }: { className?: string }) {
  return (
    <div className={`p-4 bg-[#252526] border border-[#3c3c3c] rounded-lg ${className}`}>
      <div className="flex items-center gap-3 mb-4">
        <Skeleton width={40} height={40} rounded="full" />
        <div className="flex-1 space-y-2">
          <Skeleton height={14} width="50%" />
          <Skeleton height={10} width="30%" />
        </div>
      </div>
      <SkeletonText lines={2} />
    </div>
  )
}

export function SkeletonList({
  items = 5,
  className = '',
}: {
  items?: number
  className?: string
}) {
  return (
    <div className={`space-y-2 ${className}`}>
      {Array.from({ length: items }).map((_, i) => (
        <div key={i} className="flex items-center gap-3 py-1.5">
          <Skeleton width={14} height={14} rounded="sm" />
          <Skeleton height={14} width={`${60 + Math.random() * 30}%`} />
        </div>
      ))}
    </div>
  )
}

export function SkeletonFileTree({ depth = 3, className = '' }: { depth?: number; className?: string }) {
  const renderLevel = (currentDepth: number, indent: number): React.ReactNode => {
    if (currentDepth <= 0) return null

    const itemCount = 2 + Math.floor(Math.random() * 3)

    return Array.from({ length: itemCount }).map((_, i) => {
      const isFolder = i === 0 && currentDepth > 1
      return (
        <div key={i}>
          <div
            className="flex items-center gap-2 py-1"
            style={{ paddingLeft: `${indent * 12}px` }}
          >
            <Skeleton width={14} height={14} rounded="sm" />
            <Skeleton height={12} width={`${50 + Math.random() * 80}px`} />
          </div>
          {isFolder && renderLevel(currentDepth - 1, indent + 1)}
        </div>
      )
    })
  }

  return (
    <div className={className}>
      {renderLevel(depth, 0)}
    </div>
  )
}

export function SkeletonEditor({ className = '' }: { className?: string }) {
  return (
    <div className={`bg-[#1e1e1e] ${className}`}>
      {/* Header */}
      <div className="flex items-center gap-2 px-3 py-2 bg-[#2b2b2b] border-b border-[#3c3c3c]">
        <Skeleton width={120} height={14} />
      </div>
      {/* Editor content */}
      <div className="p-4 space-y-2">
        {Array.from({ length: 15 }).map((_, i) => (
          <div key={i} className="flex items-center gap-3">
            <Skeleton width={24} height={12} className="opacity-50" />
            <Skeleton
              height={12}
              width={`${20 + Math.random() * 60}%`}
              style={{ marginLeft: `${(i % 3) * 16}px` }}
            />
          </div>
        ))}
      </div>
    </div>
  )
}
