import React, { useState, useEffect, useRef } from 'react'
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  Input,
} from '@persona-framework/ui'
import { Search, XCircle, FileText, Loader2 } from 'lucide-react'

interface WorkspaceSymbol {
  name: string
  kind: string
  containerName?: string
  filePath: string
  line: number
  column: number
}

interface WorkspaceSymbolsDialogProps {
  open: boolean
  onClose: () => void
  onNavigate: (filePath: string, line: number, column: number) => void
}

export function WorkspaceSymbolsDialog({ open, onClose, onNavigate }: WorkspaceSymbolsDialogProps) {
  const [searchQuery, setSearchQuery] = useState('')
  const [symbols, setSymbols] = useState<WorkspaceSymbol[]>([])
  const [filteredSymbols, setFilteredSymbols] = useState<WorkspaceSymbol[]>([])
  const [selectedIndex, setSelectedIndex] = useState(0)
  const [loading, setLoading] = useState(false)
  const inputRef = useRef<HTMLInputElement>(null)
  const selectedItemRef = useRef<HTMLDivElement>(null)

  const fetchSymbols = async (query: string) => {
    if (!query.trim()) {
      setFilteredSymbols([])
      return
    }

    setLoading(true)

    try {
      const response = await fetch(`/api/workspace/symbols?query=${encodeURIComponent(query)}`)
      if (response.ok) {
        const data = await response.json()
        setSymbols(data.symbols || [])
        setFilteredSymbols(data.symbols || [])
      } else {
        setSymbols([])
        setFilteredSymbols([])
      }
    } catch (error) {
      console.error('Failed to fetch symbols:', error)
      setSymbols([])
      setFilteredSymbols([])
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    const timer = setTimeout(() => {
      if (searchQuery.trim()) {
        fetchSymbols(searchQuery)
      } else {
        setFilteredSymbols([])
      }
    }, 300)

    return () => clearTimeout(timer)
  }, [searchQuery])

  useEffect(() => {
    if (open) {
      setSearchQuery('')
      setSymbols([])
      setFilteredSymbols([])
      setSelectedIndex(0)
      setTimeout(() => inputRef.current?.focus(), 100)
    }
  }, [open])

  useEffect(() => {
    if (selectedItemRef.current) {
      selectedItemRef.current.scrollIntoView({
        block: 'nearest',
        behavior: 'smooth',
      })
    }
  }, [selectedIndex])

  const handleKeyDown = (e: React.KeyboardEvent) => {
    switch (e.key) {
      case 'ArrowDown':
        e.preventDefault()
        setSelectedIndex(prev => Math.min(prev + 1, filteredSymbols.length - 1))
        break
      case 'ArrowUp':
        e.preventDefault()
        setSelectedIndex(prev => Math.max(prev - 1, 0))
        break
      case 'Enter':
        e.preventDefault()
        if (filteredSymbols[selectedIndex]) {
          handleSelect(filteredSymbols[selectedIndex])
        }
        break
      case 'Escape':
        e.preventDefault()
        onClose()
        break
    }
  }

  const handleSelect = (symbol: WorkspaceSymbol) => {
    onNavigate(symbol.filePath, symbol.line, symbol.column)
    onClose()
  }

  const getSymbolIcon = (kind: string) => {
    const iconMap: Record<string, string> = {
      function: 'ùëì',
      method: 'ùëì',
      class: 'C',
      interface: 'I',
      variable: 'v',
      constant: 'c',
      property: 'p',
      enum: 'E',
      namespace: 'N',
      type: 'T',
    }
    return iconMap[kind.toLowerCase()] || '‚óÜ'
  }

  return (
    <Dialog open={open} onOpenChange={(isOpen) => !isOpen && onClose()}>
      <DialogContent className="max-w-[600px] w-[90vw] max-h-[70vh] p-0">
        <div className="flex flex-col h-[60vh] bg-[#1e1e1e]" onKeyDown={handleKeyDown}>
          <div className="p-3 border-b border-[#3c3c3c]">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-[#858585]" />
              <Input
                ref={inputRef}
                placeholder="Search symbols (functions, classes, variables...)"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="w-full pl-10 pr-10"
              />
              {searchQuery && (
                <XCircle
                  className="absolute right-3 top-1/2 -translate-y-1/2 w-4 h-4 text-[#858585] cursor-pointer"
                  onClick={() => setSearchQuery('')}
                />
              )}
            </div>
          </div>

          <div className="flex-1 overflow-y-auto p-0">
            {loading && (
              <div className="flex flex-col items-center justify-center gap-3 p-10">
                <Loader2 className="w-5 h-5 animate-spin" />
                <span className="text-xs text-[#858585]">
                  Searching workspace...
                </span>
              </div>
            )}

            {!loading && searchQuery && filteredSymbols.length === 0 && (
              <div className="p-10 text-center text-[#858585]">
                No symbols found for "{searchQuery}"
              </div>
            )}

            {!loading && !searchQuery && (
              <div className="p-10 text-center text-[#858585]">
                Type to search for functions, classes, variables, and more
              </div>
            )}

            {!loading && filteredSymbols.map((symbol, index) => (
              <div
                key={`${symbol.filePath}-${symbol.name}-${symbol.line}`}
                ref={index === selectedIndex ? selectedItemRef : null}
                className={`flex items-center gap-3 px-4 py-2 cursor-pointer border-l-3 ${
                  index === selectedIndex
                    ? 'bg-[#2b2b2b] border-l-[#4fc3f7]'
                    : 'border-l-transparent hover:bg-[#2b2b2b] hover:border-l-[#4fc3f7]'
                }`}
                onClick={() => handleSelect(symbol)}
                onMouseEnter={() => setSelectedIndex(index)}
              >
                <div className="text-base min-w-[16px] text-[#4fc3f7]">
                  {getSymbolIcon(symbol.kind)}
                </div>
                <div className="flex-1 flex flex-col gap-0.5 min-w-0">
                  <div className="text-[13px] text-white font-medium overflow-hidden text-ellipsis whitespace-nowrap">
                    {symbol.containerName && `${symbol.containerName}.`}
                    {symbol.name}
                  </div>
                  <div className="text-[11px] text-[#858585] overflow-hidden text-ellipsis whitespace-nowrap">
                    <FileText className="inline w-2.5 h-2.5 mr-1" />
                    {symbol.filePath} : {symbol.line}
                  </div>
                </div>
                <div className="text-[10px] px-1.5 py-0.5 rounded bg-[#4fc3f722] text-[#4fc3f7] font-semibold uppercase">
                  {symbol.kind}
                </div>
              </div>
            ))}
          </div>
        </div>
      </DialogContent>
    </Dialog>
  )
}

