import React from 'react'
import {
  FileText,
  FileCode,
  FileJson,
  FileType,
  Image,
  Film,
  Music,
  Archive,
  Database,
  Settings,
  Lock,
  Terminal,
  Book,
  Braces,
  Hash,
  Coffee,
  Gem,
  Leaf,
  Globe,
  Box,
  Cog,
  Ship,
  File,
} from 'lucide-react'

interface FileIconConfig {
  icon: React.ReactNode
  color: string
}

const EXTENSION_ICONS: Record<string, FileIconConfig> = {
  // TypeScript
