  '.mjs': { icon: <FileCode size={14} />, color: '#f7df1e' },
  '.cjs': { icon: <FileCode size={14} />, color: '#f7df1e' },

  // JSON
  '.json': { icon: <FileJson size={14} />, color: '#cbcb41' },
  '.jsonc': { icon: <FileJson size={14} />, color: '#cbcb41' },

  // Config
  '.yaml': { icon: <Settings size={14} />, color: '#cb171e' },
  '.yml': { icon: <Settings size={14} />, color: '#cb171e' },
  '.toml': { icon: <Settings size={14} />, color: '#9c4221' },
  '.ini': { icon: <Settings size={14} />, color: '#6d8086' },
