  '.kts': { icon: <Gem size={14} />, color: '#7f52ff' },

  // Java
  '.java': { icon: <Coffee size={14} />, color: '#b07219' },
  '.jar': { icon: <Coffee size={14} />, color: '#b07219' },

  // Rust
  '.rs': { icon: <Cog size={14} />, color: '#dea584' },

  // Python
  '.py': { icon: <FileCode size={14} />, color: '#3572a5' },
  '.pyw': { icon: <FileCode size={14} />, color: '#3572a5' },
  '.pyi': { icon: <FileCode size={14} />, color: '#3572a5' },

  // Go
  '.go': { icon: <Leaf size={14} />, color: '#00add8' },

  // Web
  '.html': { icon: <Globe size={14} />, color: '#e34c26' },
  '.htm': { icon: <Globe size={14} />, color: '#e34c26' },
  '.css': { icon: <Hash size={14} />, color: '#563d7c' },
  '.scss': { icon: <Hash size={14} />, color: '#c6538c' },
  '.sass': { icon: <Hash size={14} />, color: '#c6538c' },
  '.less': { icon: <Hash size={14} />, color: '#1d365d' },

  // Markdown & Docs
  '.md': { icon: <Book size={14} />, color: '#083fa1' },
  '.markdown': { icon: <Book size={14} />, color: '#083fa1' },
  '.mdx': { icon: <Book size={14} />, color: '#fcb32c' },
  '.txt': { icon: <FileText size={14} />, color: '#6d8086' },

  // Shell
  '.sh': { icon: <Terminal size={14} />, color: '#89e051' },
