  // Fonts
  '.woff': { icon: <FileType size={14} />, color: '#6d8086' },
  '.woff2': { icon: <FileType size={14} />, color: '#6d8086' },
  '.ttf': { icon: <FileType size={14} />, color: '#6d8086' },
  '.otf': { icon: <FileType size={14} />, color: '#6d8086' },

  // Build
  '.gradle': { icon: <Cog size={14} />, color: '#02303a' },
  '.groovy': { icon: <Cog size={14} />, color: '#4298b8' },
