const FILENAME_ICONS: Record<string, FileIconConfig> = {
  'Dockerfile': { icon: <Ship size={14} />, color: '#384d54' },
  'Makefile': { icon: <Cog size={14} />, color: '#6d8086' },
  'package.json': { icon: <Box size={14} />, color: '#cb3837' },
  'package-lock.json': { icon: <Lock size={14} />, color: '#cb3837' },
  'yarn.lock': { icon: <Lock size={14} />, color: '#2c8ebb' },
  'pnpm-lock.yaml': { icon: <Lock size={14} />, color: '#f9ad00' },
  'tsconfig.json': { icon: <FileCode size={14} />, color: '#3178c6' },
  'vite.config.ts': { icon: <Cog size={14} />, color: '#646cff' },
  'vite.config.js': { icon: <Cog size={14} />, color: '#646cff' },
  '.gitignore': { icon: <Settings size={14} />, color: '#f14e32' },
  '.npmrc': { icon: <Settings size={14} />, color: '#cb3837' },
  '.prettierrc': { icon: <Settings size={14} />, color: '#56b3b4' },
  '.eslintrc': { icon: <Settings size={14} />, color: '#4b32c3' },
  '.eslintrc.js': { icon: <Settings size={14} />, color: '#4b32c3' },
  '.eslintrc.json': { icon: <Settings size={14} />, color: '#4b32c3' },
  'README.md': { icon: <Book size={14} />, color: '#083fa1' },
  'LICENSE': { icon: <FileText size={14} />, color: '#d4af37' },
  'Cargo.toml': { icon: <Cog size={14} />, color: '#dea584' },
  'Cargo.lock': { icon: <Lock size={14} />, color: '#dea584' },
  'build.gradle': { icon: <Cog size={14} />, color: '#02303a' },
  'settings.gradle': { icon: <Cog size={14} />, color: '#02303a' },
}

const DEFAULT_ICON: FileIconConfig = { icon: <File size={14} />, color: '#6d8086' }

export function getFileIcon(fileName: string): FileIconConfig {
  // Check exact filename match first
  if (FILENAME_ICONS[fileName]) {
    return FILENAME_ICONS[fileName]
  }

  // Check for extension
  const lastDot = fileName.lastIndexOf('.')
  if (lastDot !== -1) {
    const ext = fileName.slice(lastDot).toLowerCase()

    // Check for .d.ts special case
    if (fileName.endsWith('.d.ts')) {
      return EXTENSION_ICONS['.d.ts']
    }

    if (EXTENSION_ICONS[ext]) {
      return EXTENSION_ICONS[ext]
    }
  }

  return DEFAULT_ICON
}

interface FileIconProps {
  fileName: string
  size?: number
  className?: string
}

export function FileIcon({ fileName, size = 14, className }: FileIconProps) {
  const { icon, color } = getFileIcon(fileName)

  return (
    <span className={className} style={{ color }}>
      {React.cloneElement(icon as React.ReactElement, { size })}
    </span>
  )
}
