import { useState } from 'react'
import { Layers, Play, Square, RotateCcw, Trash2, ChevronDown, ChevronRight, FileCode } from 'lucide-react'
import { IconButton } from '@ui/components/atoms'
import { StatusBadge, SectionHeader, ResourceCard, ActionMenu, ActionItem } from '../shared'
import { mockComposeProjects } from '../../mock-data'
import type { ComposeProject, ComposeService } from '../../types'

function formatRelativeTime(dateStr: string): string {
  const diff = Date.now() - new Date(dateStr).getTime()
  const hours = Math.floor(diff / 3600000)
  const days = Math.floor(diff / 86400000)
  if (days > 0) return `${days}d ago`
  if (hours > 0) return `${hours}h ago`
  return 'Just now'
}

function ServiceRow({ service }: { service: ComposeService }) {
  return (
    <div className="flex items-center justify-between py-2 px-3 hover:bg-[var(--bg-hover)] rounded">
      <div className="flex items-center gap-2">
        <div className={`w-2 h-2 rounded-full ${service.status === 'running' ? 'bg-[var(--accent-success)]' : 'bg-[var(--text-muted)]'}`} />
        <span className="text-sm text-[var(--text-primary)]">{service.name}</span>
        <span className="text-xs text-[var(--text-muted)]">{service.image}</span>
      </div>
      <div className="flex items-center gap-3">
        <span className="text-xs text-[var(--text-secondary)]">
          {service.replicas.running}/{service.replicas.desired}
        </span>
        {service.ports.length > 0 && (
          <span className="text-xs font-mono text-[var(--text-muted)]">
            {service.ports.join(', ')}
          </span>
        )}
        <StatusBadge status={service.status} size="sm" />
      </div>
    </div>
  )
}

function ProjectCard({ project }: { project: ComposeProject }) {
  const [expanded, setExpanded] = useState(true)
  const runningCount = project.services.filter((s) => s.status === 'running').length

  return (
    <div className="bg-[var(--bg-secondary)] rounded-lg border border-[var(--border-primary)] overflow-hidden">
      <div
        className="flex items-center justify-between p-3 cursor-pointer hover:bg-[var(--bg-hover)]"
        onClick={() => setExpanded(!expanded)}
      >
        <div className="flex items-center gap-3">
          <button className="text-[var(--text-secondary)]">
            {expanded ? <ChevronDown className="h-4 w-4" /> : <ChevronRight className="h-4 w-4" />}
          </button>
          <Layers className="h-5 w-5 text-[var(--accent-primary)]" />
          <div>
            <div className="flex items-center gap-2">
              <span className="font-medium text-[var(--text-primary)]">{project.name}</span>
              <StatusBadge status={project.status} />
            </div>
            <div className="text-xs text-[var(--text-muted)] flex items-center gap-2 mt-0.5">
              <FileCode className="h-3 w-3" />
              {project.configFiles.join(', ')}
            </div>
          </div>
        </div>
        <div className="flex items-center gap-3" onClick={(e) => e.stopPropagation()}>
          <span className="text-xs text-[var(--text-secondary)]">
            {runningCount}/{project.services.length} services
          </span>
          <div className="flex items-center gap-1">
            <IconButton icon={<Play className="h-3 w-3" />} size="sm" tooltip="Start all" onClick={() => {}} />
            <IconButton icon={<Square className="h-3 w-3" />} size="sm" tooltip="Stop all" onClick={() => {}} />
            <IconButton icon={<RotateCcw className="h-3 w-3" />} size="sm" tooltip="Restart all" onClick={() => {}} />
            <ActionMenu>
              <ActionItem icon={<Play className="h-3 w-3" />} label="Up" onClick={() => {}} />
              <ActionItem icon={<Square className="h-3 w-3" />} label="Down" onClick={() => {}} />
              <ActionItem icon={<RotateCcw className="h-3 w-3" />} label="Rebuild" onClick={() => {}} />
              <ActionItem icon={<Trash2 className="h-3 w-3" />} label="Remove" onClick={() => {}} variant="danger" />
            </ActionMenu>
          </div>
        </div>
      </div>

      {expanded && (
        <div className="border-t border-[var(--border-subtle)] bg-[var(--bg-primary)]">
          {project.services.map((service) => (
            <ServiceRow key={service.name} service={service} />
          ))}
        </div>
      )}
    </div>
  )
}

export function ComposeSection() {
  return (
    <div className="h-full flex flex-col">
      <SectionHeader
        title="Compose Projects"
        subtitle={`${mockComposeProjects.length} project${mockComposeProjects.length !== 1 ? 's' : ''}`}
        icon={<Layers className="h-5 w-5" />}
        onRefresh={() => {}}
      />

      <div className="flex-1 overflow-auto space-y-3 pr-1">
        {mockComposeProjects.map((project) => (
          <ProjectCard key={project.name} project={project} />
        ))}
      </div>
    </div>
  )
}
