import React from 'react'
import { TabItem } from '../molecules'

export interface Tab {
  id: string
  title: string
  icon?: React.ReactNode
  filePath?: string
}

export interface TabBarProps {
  tabs: Tab[]
  activeTabId: string | null
  pinnedTabs: string[]
  onTabClick: (tabId: string) => void
  onTabClose: (tabId: string) => void
  onTabPin: (tabId: string) => void
  onTabContextMenu?: (tabId: string, e: React.MouseEvent) => void
  className?: string
}

export const TabBar: React.FC<TabBarProps> = ({
  tabs,
  activeTabId,
  pinnedTabs,
  onTabClick,
  onTabClose,
  onTabPin,
  onTabContextMenu,
  className = '',
}) => {
  if (tabs.length === 0) return null

  // Sort tabs: pinned first
  const sortedTabs = [...tabs].sort((a, b) => {
    const aPinned = pinnedTabs.includes(a.id)
    const bPinned = pinnedTabs.includes(b.id)
    if (aPinned && !bPinned) return -1
    if (!aPinned && bPinned) return 1
    return 0
  })

  return (
    <div
      role="tablist"
      aria-label="Open files"
      className={`flex items-center bg-[var(--bg-secondary)] border-b border-[var(--border-primary)] overflow-x-auto ${className}`}
    >
      {sortedTabs.map((tab) => {
        const isPinned = pinnedTabs.includes(tab.id)
        return (
          <TabItem
            key={tab.id}
            id={tab.id}
            title={tab.title}
            icon={tab.icon}
            active={activeTabId === tab.id}
            pinned={isPinned}
            onClick={() => onTabClick(tab.id)}
            onClose={() => onTabClose(tab.id)}
            onPin={() => onTabPin(tab.id)}
            onContextMenu={(e) => onTabContextMenu?.(tab.id, e)}
          />
        )
      })}
    </div>
  )
}

