import React from 'react'
import { Button, Input, Label, Separator } from '@persona-framework/ui'
import { Lock, User, ChevronDown, ChevronUp } from 'lucide-react'
import { GoogleSignInButton, GitHubSignInButton } from '../auth'

interface LoginPageProps {
  onSuccess?: () => void
}

export function LoginPage({ onSuccess }: LoginPageProps) {
  const [showPasswordLogin, setShowPasswordLogin] = React.useState(false)
  const [username, setUsername] = React.useState('')
  const [password, setPassword] = React.useState('')
  const [isLoading, setIsLoading] = React.useState(false)
  const [error, setError] = React.useState<string | null>(null)

  // Listen for OAuth popup success messages
  React.useEffect(() => {
    const handleMessage = async (event: MessageEvent) => {
      // Verify message origin for security - allow localhost origins in development
      const isLocalhost = event.origin.startsWith('http://localhost:') || event.origin.startsWith('http://127.0.0.1:')
      if (!isLocalhost && event.origin !== window.location.origin) {
        return
      }

      if (event.data.type === 'OAUTH_SUCCESS') {
        // Wait a bit for localStorage sync
        await new Promise(resolve => setTimeout(resolve, 300))

        // Check if tokens were saved
        const hasToken = localStorage.getItem('wraith_access_token')

        if (hasToken && onSuccess) {
          onSuccess()
        }
      }
    }

    window.addEventListener('message', handleMessage)
    return () => window.removeEventListener('message', handleMessage)
  }, [onSuccess])

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    setError(null)
    setIsLoading(true)

    try {
      // TODO: Implement password login with auth service
      setError('Password login coming soon. Please use Google or GitHub to sign in.')
    } catch (err: any) {
      const errorMessage = err.response?.data?.message || 'Login failed'
      setError(errorMessage)
    } finally {
      setIsLoading(false)
    }
  }

  return (
    <div className="flex items-center justify-center min-h-screen bg-background">
      <div className="bg-card rounded-xl shadow-lg p-8 max-w-[400px] w-full border">
        <h1 className="text-3xl font-semibold text-center mb-2">Welcome to Wraith</h1>
        <p className="text-base text-center text-muted-foreground mb-6">Sign in to continue</p>

        {/* Primary OAuth login options */}
        <div className="flex flex-col gap-3 mb-6">
          <GoogleSignInButton disabled={isLoading} />
          <GitHubSignInButton disabled={isLoading} />
        </div>

        {/* Collapsible password login section */}
        <div className="border-t border-border pt-4">
          <button
            type="button"
            onClick={() => setShowPasswordLogin(!showPasswordLogin)}
            className="flex items-center justify-center gap-2 w-full text-sm text-muted-foreground hover:text-foreground transition-colors py-2"
          >
            {showPasswordLogin ? (
              <>
                <ChevronUp className="h-4 w-4" />
                Hide password login
              </>
            ) : (
              <>
                <ChevronDown className="h-4 w-4" />
                Sign in with password instead
              </>
            )}
          </button>

          {showPasswordLogin && (
            <form onSubmit={handleSubmit} className="mt-4">
              <div className="bg-muted/50 rounded-lg p-3 mb-4 text-xs text-muted-foreground text-center">
                Password login is coming soon. Please use OAuth above.
              </div>

              <div className="flex flex-col gap-2 mb-4">
                <Label htmlFor="username">Username</Label>
                <div className="relative">
                  <User className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                  <Input
                    id="username"
                    type="text"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    placeholder="Enter your username"
                    className="pl-9"
                    disabled
                  />
                </div>
              </div>

              <div className="flex flex-col gap-2 mb-4">
                <Label htmlFor="password">Password</Label>
                <div className="relative">
                  <Lock className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                  <Input
                    id="password"
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Enter your password"
                    className="pl-9"
                    disabled
                  />
                </div>
              </div>

              {error && <p className="text-destructive text-sm text-center mt-4">{error}</p>}

              <Button
                size="lg"
                type="submit"
                disabled={true}
                className="mt-4 w-full"
                variant="outline"
              >
                Coming Soon
              </Button>
            </form>
          )}
        </div>
      </div>
    </div>
  )
}

