import React, { useState, useEffect, useRef } from 'react'
import {
  Button,
  Badge,
  Progress,
  Tabs,
  TabsList,
  TabsTrigger,
  TabsContent,
  DropdownMenu,
  DropdownMenuTrigger,
  DropdownMenuContent,
  DropdownMenuItem,
} from '@persona-framework/ui'
import {
  Play,
  Square,
  RotateCw,
  Settings,
  CheckCircle,
  XCircle,
  AlertTriangle,
  ChevronDown,
} from 'lucide-react'

interface BuildTask {
  id: string
  name: string
  command: string
  type: 'npm' | 'gradle' | 'maven' | 'make' | 'custom'
  icon: string
  description?: string
}

interface BuildHistory {
  id: string
  taskId: string
  taskName: string
  status: 'success' | 'failed' | 'cancelled'
  startTime: Date
  endTime: Date
  duration: number
  exitCode: number
}

type BuildStatus = 'idle' | 'running' | 'success' | 'failed' | 'cancelled'
type TabValue = 'tasks' | 'output' | 'history'

export function BuildPanel() {
  const [activeTab, setActiveTab] = useState<TabValue>('tasks')
  const [selectedConfig, setSelectedConfig] = useState('Development')
  const [buildStatus, setBuildStatus] = useState<BuildStatus>('idle')
  const [currentTask, setCurrentTask] = useState<BuildTask | null>(null)
  const [buildOutput, setBuildOutput] = useState<string[]>([])
  const [buildProgress, setBuildProgress] = useState(0)
  const [buildHistory, setBuildHistory] = useState<BuildHistory[]>([])
  const [tasks, setTasks] = useState<BuildTask[]>([])
  const outputRef = useRef<HTMLDivElement>(null)
  const wsRef = useRef<WebSocket | null>(null)

  useEffect(() => {
    loadTasks()
    loadBuildHistory()
  }, [])

  useEffect(() => {
    if (outputRef.current) {
      outputRef.current.scrollTop = outputRef.current.scrollHeight
    }
  }, [buildOutput])

  const loadTasks = async () => {
    try {
      const response = await fetch('/api/build/tasks')
      if (response.ok) {
        const data = await response.json()
        setTasks(data.tasks || getDefaultTasks())
      } else {
        setTasks(getDefaultTasks())
      }
    } catch {
      // Fallback to default tasks if API unavailable
      setTasks(getDefaultTasks())
    }
  }

  const getDefaultTasks = (): BuildTask[] => {
    return [
      {
        id: 'gradle-build',
        name: 'Build All Modules (Gradle)',
        command: './gradlew build -x test',
        type: 'gradle',
        icon: 'ðŸ˜',
        description: 'Build all Kotlin modules (leviathan, mammon, hydra, etc.)',
      },
      {
        id: 'gradle-clean-build',
        name: 'Clean Build (Kotlin)',
        command: './gradlew clean build',
        type: 'gradle',
        icon: 'ðŸ§¹',
        description: 'Clean and rebuild all Kotlin modules',
      },
      {
        id: 'gradle-test',
        name: 'Test All Modules',
        command: './gradlew test',
        type: 'gradle',
        icon: 'ðŸ§ª',
        description: 'Run all Kotlin/JVM tests',
      },
      {
        id: 'gradle-leviathan',
        name: 'Build Leviathan',
        command: './gradlew :leviathan:build',
        type: 'gradle',
        icon: 'ðŸ”±',
        description: 'Build Leviathan service only',
      },
      {
        id: 'gradle-mammon',
        name: 'Build Mammon',
        command: './gradlew :mammon:build',
        type: 'gradle',
        icon: 'ðŸ’°',
        description: 'Build Mammon service only',
      },
      {
        id: 'gradle-hydra',
        name: 'Build Hydra',
        command: './gradlew :hydra:build',
        type: 'gradle',
        icon: 'ðŸ²',
        description: 'Build Hydra service only',
      },
      {
        id: 'gradle-bootrun',
        name: 'Run Spring Boot',
        command: './gradlew :leviathan:bootRun',
        type: 'gradle',
        icon: 'ðŸš€',
        description: 'Start Leviathan with Spring Boot',
      },
      {
        id: 'cargo-build',
        name: 'Build All Crates (Rust)',
        command: 'cargo build',
        type: 'custom',
        icon: 'ðŸ¦€',
        description: 'Build all Rust crates (eidolon, etc.)',
      },
      {
        id: 'cargo-build-release',
        name: 'Build Release (Rust)',
        command: 'cargo build --release',
        type: 'custom',
        icon: 'âš¡',
        description: 'Build optimized Rust release',
      },
      {
        id: 'cargo-test',
        name: 'Test (Rust)',
        command: 'cargo test',
        type: 'custom',
        icon: 'ðŸ§ª',
        description: 'Run all Rust tests',
      },
      {
        id: 'cargo-check',
        name: 'Check (Rust)',
        command: 'cargo check',
        type: 'custom',
        icon: 'âœ“',
        description: 'Quick Rust compilation check',
      },
      {
        id: 'cargo-clippy',
        name: 'Clippy (Rust Linter)',
        command: 'cargo clippy',
        type: 'custom',
        icon: 'ðŸ“Ž',
        description: 'Run Rust linter',
      },
      {
        id: 'cargo-fmt',
        name: 'Format (Rust)',
        command: 'cargo fmt',
        type: 'custom',
        icon: 'âœ¨',
        description: 'Format Rust code',
      },
      {
        id: 'npm-build',
        name: 'Build (npm)',
        command: 'cd bael && npm run build',
        type: 'npm',
        icon: 'ðŸ“¦',
        description: 'Build TypeScript frontend (bael)',
      },
      {
        id: 'npm-dev',
        name: 'Dev Server',
        command: 'cd bael && npm run dev',
        type: 'npm',
        icon: 'ðŸš€',
        description: 'Start Vite dev server',
      },
      {
        id: 'npm-test',
        name: 'Test (npm)',
        command: 'cd bael && npm test',
        type: 'npm',
        icon: 'ðŸ§ª',
        description: 'Run frontend tests',
      },
      {
        id: 'npm-lint',
        name: 'Lint (ESLint)',
        command: 'cd bael && npm run lint',
        type: 'npm',
        icon: 'âœ¨',
        description: 'Run ESLint on TypeScript',
      },
      {
        id: 'npm-typecheck',
        name: 'Type Check',
        command: 'cd bael && npm run type-check',
        type: 'npm',
        icon: 'ðŸ”',
        description: 'TypeScript type checking',
      },
      {
        id: 'make-all',
        name: 'Build All (Make)',
        command: 'make all',
        type: 'make',
        icon: 'âš™ï¸',
        description: 'Build entire project with Make',
      },
      {
        id: 'make-clean',
        name: 'Clean (Make)',
        command: 'make clean',
        type: 'make',
        icon: 'ðŸ§¹',
        description: 'Clean all build artifacts',
      },
      {
        id: 'docker-build',
        name: 'Docker Build',
        command: 'docker build -t persona-framework .',
        type: 'custom',
        icon: 'ðŸ³',
        description: 'Build Docker image',
      },
      {
        id: 'docker-compose-up',
        name: 'Docker Compose Up',
        command: 'docker-compose up -d',
        type: 'custom',
        icon: 'ðŸ³',
        description: 'Start all services with Docker Compose',
      },
      {
        id: 'docker-compose-down',
        name: 'Docker Compose Down',
        command: 'docker-compose down',
        type: 'custom',
        icon: 'ðŸ›‘',
        description: 'Stop all Docker Compose services',
      },
      {
        id: 'protoc-generate',
        name: 'Generate Protobuf',
        command: 'protoc --java_out=. --kotlin_out=. **/*.proto',
        type: 'custom',
        icon: 'ðŸ“¡',
        description: 'Generate code from .proto files',
      },
    ]
  }

  const loadBuildHistory = async () => {
    try {
      const response = await fetch('/api/build/history')
      if (response.ok) {
        const data = await response.json()
        setBuildHistory(data.history || [])
      }
    } catch {
      // Silent fail - history is non-critical
    }
  }

  const runTask = async (task: BuildTask) => {
    setBuildStatus('running')
    setCurrentTask(task)
    setBuildOutput([])
    setBuildProgress(0)
    setActiveTab('output')

    addOutput(`[INFO] Starting: ${task.name}`, 'info')
    addOutput(`[INFO] Command: ${task.command}`, 'info')
    addOutput('', 'default')

    const ws = new WebSocket('ws://localhost:8989/ws/build')
    wsRef.current = ws

    ws.onopen = () => {
      ws.send(
        JSON.stringify({
          type: 'execute',
          taskId: task.id,
          command: task.command,
          workingDirectory: '/home/user/persona-framework',
        })
      )
    }

    ws.onmessage = (event) => {
      const message = JSON.parse(event.data)
      handleBuildMessage(message)
    }

    ws.onerror = () => {
      addOutput('[ERROR] Failed to connect to build server', 'error')
      setBuildStatus('failed')
    }

    ws.onclose = () => {
      wsRef.current = null
    }
  }

  const handleBuildMessage = (message: any) => {
    switch (message.type) {
      case 'output':
        addOutput(message.content, detectOutputType(message.content))
        break

      case 'progress':
        setBuildProgress(message.progress)
        break

      case 'complete':
        const success = message.exitCode === 0
        setBuildStatus(success ? 'success' : 'failed')
        addOutput('', 'default')
        addOutput(
          success
            ? `[SUCCESS] Build completed in ${formatDuration(message.duration)}`
            : `[FAILED] Build failed with exit code ${message.exitCode}`,
          success ? 'success' : 'error'
        )

        if (currentTask) {
          const historyItem: BuildHistory = {
            id: `history-${Date.now()}`,
            taskId: currentTask.id,
            taskName: currentTask.name,
            status: success ? 'success' : 'failed',
            startTime: new Date(Date.now() - message.duration),
            endTime: new Date(),
            duration: message.duration,
            exitCode: message.exitCode,
          }
          setBuildHistory((prev) => [historyItem, ...prev.slice(0, 19)])
        }
        break

      case 'error':
        addOutput(`[ERROR] ${message.message}`, 'error')
        setBuildStatus('failed')
        break
    }
  }

  const addOutput = (line: string, type: 'default' | 'info' | 'success' | 'warning' | 'error') => {
    setBuildOutput((prev) => [...prev, `${type}:${line}`])
  }

  const detectOutputType = (line: string): 'default' | 'info' | 'success' | 'warning' | 'error' => {
    const lower = line.toLowerCase()
    if (lower.includes('error') || lower.includes('failed')) return 'error'
    if (lower.includes('warning') || lower.includes('warn')) return 'warning'
    if (lower.includes('success') || lower.includes('build successful')) return 'success'
    if (lower.includes('[info]') || lower.includes('building')) return 'info'
    return 'default'
  }

  const stopBuild = () => {
    if (wsRef.current) {
      wsRef.current.send(JSON.stringify({ type: 'cancel' }))
      wsRef.current.close()
    }
    setBuildStatus('cancelled')
    addOutput('', 'default')
    addOutput('[CANCELLED] Build cancelled by user', 'warning')
  }

  const restartBuild = () => {
    if (currentTask) {
      runTask(currentTask)
    }
  }

  const formatDuration = (ms: number): string => {
    const seconds = Math.floor(ms / 1000)
    const minutes = Math.floor(seconds / 60)
    const remainingSeconds = seconds % 60

    if (minutes > 0) {
      return `${minutes}m ${remainingSeconds}s`
    }
    return `${seconds}s`
  }

  const formatTime = (date: Date): string => {
    return date.toLocaleTimeString('en-US', {
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit',
    })
  }

  const getStatusIcon = (status: BuildStatus | 'success' | 'failed' | 'cancelled') => {
    switch (status) {
      case 'success':
        return <CheckCircle className="text-[#4ec9b0]" size={16} />
      case 'failed':
        return <XCircle className="text-[#f48771]" size={16} />
      case 'cancelled':
        return <AlertTriangle className="text-[#f59e0b]" size={16} />
      case 'running':
        return <RotateCw className="text-[#4fc3f7] animate-spin" size={16} />
      default:
        return null
    }
  }

  const renderOutputLine = (line: string, index: number) => {
    const [type, content] = line.split(':')

    const className =
      type === 'error'
        ? 'text-[#f48771] cursor-pointer hover:underline'
        : type === 'warning'
          ? 'text-[#f59e0b]'
          : type === 'success'
            ? 'text-[#4ec9b0]'
            : type === 'info'
              ? 'text-[#4fc3f7]'
              : ''

    return (
      <div key={index} className={`mb-0.5 ${className}`}>
        {content}
      </div>
    )
  }

  return (
    <div className="flex flex-col h-full bg-[#1e1e1e] text-[#cccccc]">
      <div className="flex items-center justify-between px-3 py-2 border-b border-[#3c3c3c] bg-[#2b2b2b]">
        <div className="flex items-center gap-2">
          <div className="text-[13px] font-semibold text-white">Build & Tasks</div>
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="ghost" size="sm" className="h-6 px-2 text-[11px] text-[#858585]">
                {selectedConfig}
                <ChevronDown className="ml-1" size={12} />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent>
              <DropdownMenuItem onClick={() => setSelectedConfig('Development')}>
                Development
              </DropdownMenuItem>
              <DropdownMenuItem onClick={() => setSelectedConfig('Production')}>
                Production
              </DropdownMenuItem>
              <DropdownMenuItem onClick={() => setSelectedConfig('Test')}>Test</DropdownMenuItem>
              <DropdownMenuItem onClick={() => setSelectedConfig('Staging')}>
                Staging
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        </div>

        <div className="flex items-center gap-1">
          {buildStatus === 'running' && (
            <>
              <Button
                variant="ghost"
                size="sm"
                className="h-6 w-6 p-0"
                onClick={restartBuild}
                title="Restart"
              >
                <RotateCw size={14} />
              </Button>
              <Button
                variant="ghost"
                size="sm"
                className="h-6 w-6 p-0"
                onClick={stopBuild}
                title="Stop Build"
              >
                <Square size={14} />
              </Button>
            </>
          )}
          <Button variant="ghost" size="sm" className="h-6 w-6 p-0" title="Build Settings">
            <Settings size={14} />
          </Button>
        </div>
      </div>

      <div className="px-3 py-2 border-b border-[#3c3c3c]">
        <Tabs value={activeTab} onValueChange={(value) => setActiveTab(value as TabValue)}>
          <TabsList className="h-8">
            <TabsTrigger value="tasks" className="text-xs">
              Tasks
            </TabsTrigger>
            <TabsTrigger value="output" className="text-xs">
              <span>Output</span>
              {buildStatus === 'running' && (
                <Badge className="ml-1.5 bg-blue-600">Running</Badge>
              )}
            </TabsTrigger>
            <TabsTrigger value="history" className="text-xs">
              History
            </TabsTrigger>
          </TabsList>
        </Tabs>
      </div>

      {buildStatus === 'running' && (
        <div className="px-3 pb-3">
          <Progress value={buildProgress} max={100} />
        </div>
      )}

      <div className="flex-1 overflow-auto flex flex-col">
        <Tabs value={activeTab}>
          <TabsContent value="tasks" className="p-3 space-y-2">
            {tasks.map((task) => (
              <div
                key={task.id}
                className="flex items-center gap-3 p-3 bg-[#252526] border border-[#3c3c3c] rounded cursor-pointer transition-all hover:bg-[#2b2b2b] hover:border-[#4fc3f7]"
                onClick={() => runTask(task)}
              >
                <div className="text-lg">{task.icon}</div>
                <div className="flex-1 min-w-0">
                  <div className="text-[13px] font-semibold text-white mb-1">{task.name}</div>
                  <div className="text-[11px] text-[#858585] font-mono">{task.command}</div>
                </div>
                <div className="flex items-center gap-1">
                  <Button
                    size="sm"
                    className="h-7"
                    onClick={(e) => {
                      e.stopPropagation()
                      runTask(task)
                    }}
                  >
                    <Play size={14} className="mr-1" />
                    Run
                  </Button>
                </div>
              </div>
            ))}
          </TabsContent>

          <TabsContent value="output" className="flex-1">
            <div
              ref={outputRef}
              className="flex-1 p-3 font-mono text-xs leading-relaxed overflow-auto bg-[#1e1e1e] text-[#cccccc] whitespace-pre-wrap break-all h-full"
            >
              {buildOutput.length === 0 ? (
                <div className="py-10 px-5 text-center text-[#858585] text-[13px]">
                  No build output yet. Select a task to run.
                </div>
              ) : (
                buildOutput.map((line, index) => renderOutputLine(line, index))
              )}
            </div>
          </TabsContent>

          <TabsContent value="history" className="p-3 space-y-2">
            {buildHistory.length === 0 ? (
              <div className="py-10 px-5 text-center text-[#858585] text-[13px]">
                No build history yet.
              </div>
            ) : (
              buildHistory.map((item) => (
                <div
                  key={item.id}
                  className="flex items-center gap-3 p-2.5 bg-[#252526] border border-[#3c3c3c] rounded"
                >
                  <div className="text-base">{getStatusIcon(item.status)}</div>
                  <div className="flex-1 min-w-0">
                    <div className="text-xs font-semibold text-white mb-1">{item.taskName}</div>
                    <div className="text-[10px] text-[#858585]">
                      {formatTime(item.startTime)} â€¢ {formatDuration(item.duration)}
                    </div>
                  </div>
                  <div className="text-[11px] text-[#858585] font-mono">
                    Exit Code: {item.exitCode}
                  </div>
                </div>
              ))
            )}
          </TabsContent>
        </Tabs>
      </div>

      <div className="flex items-center justify-between px-3 py-2 border-t border-[#3c3c3c] bg-[#252526] text-[11px]">
        <div className="flex items-center gap-3">
          <div className="flex items-center gap-1">
            {getStatusIcon(buildStatus)}
            <span>
              {buildStatus === 'idle' && 'Ready'}
              {buildStatus === 'running' && `Running: ${currentTask?.name}`}
              {buildStatus === 'success' && 'Build Successful'}
              {buildStatus === 'failed' && 'Build Failed'}
              {buildStatus === 'cancelled' && 'Build Cancelled'}
            </span>
          </div>
        </div>

        <div className="flex items-center gap-3">
          <div className="flex items-center gap-1">
            <span>{buildHistory.length} builds</span>
          </div>
          <div className="flex items-center gap-1">
            <span>Config: {selectedConfig}</span>
          </div>
        </div>
      </div>
    </div>
  )
}
