import React, { useState } from 'react'
import { Button, Input, Accordion, AccordionItem, AccordionTrigger, AccordionContent } from '@persona-framework/ui'
import {
  Play,
  Pause,
  Square,
  ArrowRightToLine,
  ArrowDownToLine,
  ArrowUpFromLine,
  RotateCcw,
  Trash2,
  Plus,
  Circle,
  ChevronRight,
  ChevronDown,
} from 'lucide-react'

interface Breakpoint {
  id: string
  file: string
  line: number
  enabled: boolean
}

interface Variable {
  name: string
  value: string
  type: string
  children?: Variable[]
}

interface StackFrame {
  id: string
  name: string
  file: string
  line: number
  column: number
}

interface WatchExpression {
  id: string
  expression: string
  value?: string
  error?: string
}

type DebugState = 'stopped' | 'running' | 'paused'

export function DebuggerPanel() {
  const [debugState, setDebugState] = useState<DebugState>('stopped')
  const [breakpoints, setBreakpoints] = useState<Breakpoint[]>([
    { id: '1', file: 'src/App.tsx', line: 42, enabled: true },
    { id: '2', file: 'src/utils/api.ts', line: 18, enabled: true },
  ])
  const [variables, setVariables] = useState<Variable[]>([
    { name: 'user', value: '{ id: 1, name: "Alice" }', type: 'Object' },
    { name: 'count', value: '42', type: 'number' },
    { name: 'isActive', value: 'true', type: 'boolean' },
  ])
  const [callStack, setCallStack] = useState<StackFrame[]>([
    { id: '1', name: 'handleClick', file: 'src/App.tsx', line: 42, column: 8 },
    { id: '2', name: 'onClick', file: 'src/Button.tsx', line: 15, column: 12 },
    { id: '3', name: 'dispatchEvent', file: 'react-dom.js', line: 2834, column: 5 },
  ])
  const [watchExpressions, setWatchExpressions] = useState<WatchExpression[]>([
    { id: '1', expression: 'user.name', value: '"Alice"' },
    { id: '2', expression: 'count * 2', value: '84' },
  ])
  const [newWatchExpression, setNewWatchExpression] = useState('')
  const [activeStackFrame, setActiveStackFrame] = useState('1')

  const handleStart = () => {
    setDebugState('running')
  }

  const handlePause = () => {
    setDebugState('paused')
  }

  const handleStop = () => {
    setDebugState('stopped')
  }

  const handleStepOver = () => {
  }

  const handleStepInto = () => {
  }

  const handleStepOut = () => {
  }

  const handleRestart = () => {
    setDebugState('running')
  }

  const toggleBreakpoint = (id: string) => {
    setBreakpoints(prev =>
      prev.map(bp => bp.id === id ? { ...bp, enabled: !bp.enabled } : bp)
    )
  }

  const removeBreakpoint = (id: string) => {
    setBreakpoints(prev => prev.filter(bp => bp.id !== id))
  }

  const addWatchExpression = () => {
    if (!newWatchExpression.trim()) return

    const newWatch: WatchExpression = {
      id: Date.now().toString(),
      expression: newWatchExpression,
      value: 'Evaluating...',
    }
    setWatchExpressions(prev => [...prev, newWatch])
    setNewWatchExpression('')
  }

  const removeWatchExpression = (id: string) => {
    setWatchExpressions(prev => prev.filter(w => w.id !== id))
  }

  const getStatusText = () => {
    switch (debugState) {
      case 'running':
        return 'Debugging...'
      case 'paused':
        return 'Paused at breakpoint'
      case 'stopped':
        return 'Not debugging'
    }
  }

  const getStatusClass = () => {
    switch (debugState) {
      case 'running':
        return 'text-[#4fc3f7]'
      case 'paused':
        return 'text-[#f59e0b]'
      case 'stopped':
        return 'text-[#858585]'
    }
  }

  return (
    <div className="flex flex-col h-full bg-[#1e1e1e] text-[#cccccc]">
      <div className="px-3 py-2 border-b border-[#3c3c3c] text-[13px] font-semibold text-white">
        Debugger
      </div>

      <div className={`flex items-center gap-2 px-3 py-2 bg-[#252526] border-b border-[#3c3c3c] text-[11px] ${getStatusClass()}`}>
        <Circle className="w-2 h-2 fill-current" />
        <span>{getStatusText()}</span>
      </div>

      <div className="flex gap-1 px-3 py-2 border-b border-[#3c3c3c] bg-[#252526]">
        {debugState === 'stopped' && (
          <Button
            size="sm"
            onClick={handleStart}
            title="Start Debugging (F5)"
          >
            <Play className="w-4 h-4" />
          </Button>
        )}
        {debugState === 'running' && (
          <Button
            size="sm"
            variant="ghost"
            onClick={handlePause}
            title="Pause"
          >
            <Pause className="w-4 h-4" />
          </Button>
        )}
        {debugState !== 'stopped' && (
          <>
            <Button
              size="sm"
              variant="ghost"
              onClick={handleStop}
              title="Stop (Shift+F5)"
            >
              <Square className="w-4 h-4" />
            </Button>
            <Button
              size="sm"
              variant="ghost"
              onClick={handleRestart}
              title="Restart (Ctrl+Shift+F5)"
            >
              <RotateCcw className="w-4 h-4" />
            </Button>
          </>
        )}
        {debugState === 'paused' && (
          <>
            <div className="w-px h-6 bg-[#3c3c3c] mx-1" />
            <Button
              size="sm"
              variant="ghost"
              onClick={handleStepOver}
              title="Step Over (F10)"
            >
              <ArrowRightToLine className="w-4 h-4" />
            </Button>
            <Button
              size="sm"
              variant="ghost"
              onClick={handleStepInto}
              title="Step Into (F11)"
            >
              <ArrowDownToLine className="w-4 h-4" />
            </Button>
            <Button
              size="sm"
              variant="ghost"
              onClick={handleStepOut}
              title="Step Out (Shift+F11)"
            >
              <ArrowUpFromLine className="w-4 h-4" />
            </Button>
          </>
        )}
      </div>

      <div className="flex-1 overflow-auto">
        <Accordion type="multiple" defaultValue={['variables', 'breakpoints', 'callstack', 'watch']} className="w-full">
          <AccordionItem value="variables">
            <AccordionTrigger className="px-3 py-2 text-[11px] font-semibold uppercase">
              Variables
            </AccordionTrigger>
            <AccordionContent>
              {debugState === 'paused' ? (
                variables.length > 0 ? (
                  variables.map((variable, index) => (
                    <div key={index} className="flex gap-2 px-3 py-1 text-xs">
                      <span className="text-[#9cdcfe] min-w-[100px]">{variable.name}</span>
                      <span className="text-[#ce9178] flex-1">{variable.value}</span>
                      <span className="text-[#4ec9b0] text-[10px]">{variable.type}</span>
                    </div>
                  ))
                ) : (
                  <div className="px-5 py-5 text-center text-[#858585] text-xs">No variables in current scope</div>
                )
              ) : (
                <div className="px-5 py-5 text-center text-[#858585] text-xs">
                  Pause debugging to inspect variables
                </div>
              )}
            </AccordionContent>
          </AccordionItem>

          <AccordionItem value="callstack">
            <AccordionTrigger className="px-3 py-2 text-[11px] font-semibold uppercase">
              Call Stack
            </AccordionTrigger>
            <AccordionContent>
              {debugState === 'paused' ? (
                callStack.length > 0 ? (
                  callStack.map(frame => (
                    <div
                      key={frame.id}
                      className={`px-3 py-1.5 text-xs flex flex-col gap-0.5 cursor-pointer border-l-[3px] hover:bg-[#2b2b2b] hover:border-l-[#4fc3f7] ${
                        frame.id === activeStackFrame ? 'bg-[#2b2b2b] border-l-[#4fc3f7]' : 'border-l-transparent'
                      }`}
                      onClick={() => setActiveStackFrame(frame.id)}
                    >
                      <div className="text-[#dcdcaa] font-medium">{frame.name}</div>
                      <div className="text-[#858585] text-[11px]">
                        {frame.file}:{frame.line}:{frame.column}
                      </div>
                    </div>
                  ))
                ) : (
                  <div className="px-5 py-5 text-center text-[#858585] text-xs">No call stack available</div>
                )
              ) : (
                <div className="px-5 py-5 text-center text-[#858585] text-xs">
                  Pause debugging to view call stack
                </div>
              )}
            </AccordionContent>
          </AccordionItem>

          <AccordionItem value="breakpoints">
            <AccordionTrigger className="px-3 py-2 text-[11px] font-semibold uppercase">
              Breakpoints ({breakpoints.length})
            </AccordionTrigger>
            <AccordionContent>
              {breakpoints.length > 0 ? (
                breakpoints.map(breakpoint => (
                  <div key={breakpoint.id} className="flex items-center gap-2 px-3 py-1.5 text-xs hover:bg-[#2b2b2b]">
                    <Circle
                      className={`w-2.5 h-2.5 cursor-pointer ${breakpoint.enabled ? 'text-[#f48771] fill-[#f48771]' : 'text-[#858585] fill-[#858585]'}`}
                      onClick={() => toggleBreakpoint(breakpoint.id)}
                    />
                    <div className="flex-1">
                      <div className="text-[#cccccc]">
                        Line {breakpoint.line}
                      </div>
                      <div className="text-[#858585] text-[11px]">{breakpoint.file}</div>
                    </div>
                    <Button
                      size="sm"
                      variant="ghost"
                      onClick={() => removeBreakpoint(breakpoint.id)}
                    >
                      <Trash2 className="w-3 h-3" />
                    </Button>
                  </div>
                ))
              ) : (
                <div className="px-5 py-5 text-center text-[#858585] text-xs">
                  No breakpoints set. Click in the gutter to add one.
                </div>
              )}
            </AccordionContent>
          </AccordionItem>

          <AccordionItem value="watch">
            <AccordionTrigger className="px-3 py-2 text-[11px] font-semibold uppercase">
              Watch ({watchExpressions.length})
            </AccordionTrigger>
            <AccordionContent>
              <div className="px-3 py-2 border-b border-[#3c3c3c]">
                <div className="flex gap-1">
                  <Input
                    size="sm"
                    placeholder="Add expression to watch"
                    value={newWatchExpression}
                    onChange={(e) => setNewWatchExpression(e.target.value)}
                    onKeyDown={(e) => e.key === 'Enter' && addWatchExpression()}
                    className="flex-1"
                  />
                  <Button
                    size="sm"
                    variant="ghost"
                    onClick={addWatchExpression}
                  >
                    <Plus className="w-3 h-3" />
                  </Button>
                </div>
              </div>
              {watchExpressions.length > 0 ? (
                watchExpressions.map(watch => (
                  <div key={watch.id} className="flex gap-2 px-3 py-1 text-xs items-center">
                    <span className="text-[#9cdcfe] min-w-[100px]">{watch.expression}</span>
                    <span className="text-[#ce9178] flex-1">
                      {watch.error ? `Error: ${watch.error}` : watch.value}
                    </span>
                    <Button
                      size="sm"
                      variant="ghost"
                      onClick={() => removeWatchExpression(watch.id)}
                    >
                      <Trash2 className="w-3 h-3" />
                    </Button>
                  </div>
                ))
              ) : (
                <div className="px-5 py-5 text-center text-[#858585] text-xs">
                  Add expressions to watch their values
                </div>
              )}
            </AccordionContent>
          </AccordionItem>
        </Accordion>
      </div>
    </div>
  )
}

