import React, { useState, useEffect } from 'react'
import {
  Button,
  Input,
  Checkbox,
  Badge,
  Spinner,
  Tabs,
  TabsList,
  TabsTrigger,
  TabsContent,
  DropdownMenu,
  DropdownMenuTrigger,
  DropdownMenuContent,
  DropdownMenuItem,
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from '@persona-framework/ui'
import {
  GitBranch,
  CheckCircle,
  XCircle,
  Save,
  Upload,
  Download,
  MoreHorizontal,
  AlertTriangle,
} from 'lucide-react'

interface GitFile {
  path: string
  status: 'M' | 'A' | 'D' | 'R' | 'U'
  staged: boolean
}

interface GitBranch {
  name: string
  current: boolean
  remote: boolean
  upstream?: string
  ahead?: number
  behind?: number
}

interface GitCommit {
  hash: string
  message: string
  author: string
  date: Date
  abbreviated: string
}

type TabValue = 'changes' | 'branches' | 'history'

export function SourceControlPanel() {
  const [activeTab, setActiveTab] = useState<TabValue>('changes')
  const [currentBranch, setCurrentBranch] = useState<string>('main')
  const [commitMessage, setCommitMessage] = useState('')
  const [files, setFiles] = useState<GitFile[]>([])
  const [branches, setBranches] = useState<GitBranch[]>([])
  const [commits, setCommits] = useState<GitCommit[]>([])
  const [loading, setLoading] = useState(false)
  const [ahead, setAhead] = useState(0)
  const [behind, setBehind] = useState(0)

  // Notification state
  const [notification, setNotification] = useState<{
    type: 'success' | 'error'
    message: string
  } | null>(null)

  // New branch dialog state
  const [newBranchDialogOpen, setNewBranchDialogOpen] = useState(false)
  const [newBranchName, setNewBranchName] = useState('')
  const [creatingBranch, setCreatingBranch] = useState(false)

  const showNotification = (type: 'success' | 'error', message: string) => {
    setNotification({ type, message })
    setTimeout(() => setNotification(null), 4000)
  }

  useEffect(() => {
    loadGitStatus()
    loadBranches()
    loadCommitHistory()
  }, [])

  const loadGitStatus = async () => {
    try {
      const response = await fetch('/api/git/status')
      if (response.ok) {
        const data = await response.json()
        setCurrentBranch(data.branch || 'main')
        setFiles(data.files || [])
        setAhead(data.ahead || 0)
        setBehind(data.behind || 0)
      }
    } catch {
      // Silent fail for background refresh
    }
  }

  const loadBranches = async () => {
    try {
      const response = await fetch('/api/git/branches')
      if (response.ok) {
        const data = await response.json()
        setBranches(data.branches || [])
      }
    } catch {
      // Silent fail for background refresh
    }
  }

  const loadCommitHistory = async () => {
    try {
      const response = await fetch('/api/git/log?limit=50')
      if (response.ok) {
        const data = await response.json()
        setCommits(data.commits || [])
      }
    } catch {
      // Silent fail for background refresh
    }
  }

  const stageFile = async (filePath: string) => {
    try {
      await fetch('/api/git/stage', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ files: [filePath] }),
      })
      loadGitStatus()
    } catch {
      showNotification('error', 'Failed to stage file')
    }
  }

  const unstageFile = async (filePath: string) => {
    try {
      await fetch('/api/git/unstage', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ files: [filePath] }),
      })
      loadGitStatus()
    } catch {
      showNotification('error', 'Failed to unstage file')
    }
  }

  const toggleFileStaging = (file: GitFile) => {
    if (file.staged) {
      unstageFile(file.path)
    } else {
      stageFile(file.path)
    }
  }

  const stageAll = async () => {
    try {
      await fetch('/api/git/stage', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ all: true }),
      })
      loadGitStatus()
    } catch {
      showNotification('error', 'Failed to stage all files')
    }
  }

  const commit = async () => {
    if (!commitMessage.trim()) {
      showNotification('error', 'Please enter a commit message')
      return
    }

    setLoading(true)
    try {
      const response = await fetch('/api/git/commit', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: commitMessage }),
      })

      if (response.ok) {
        setCommitMessage('')
        loadGitStatus()
        loadCommitHistory()
        showNotification('success', 'Changes committed successfully')
      } else {
        const error = await response.json()
        showNotification('error', `Commit failed: ${error.message}`)
      }
    } catch {
      showNotification('error', 'Failed to commit changes')
    } finally {
      setLoading(false)
    }
  }

  const push = async () => {
    setLoading(true)
    try {
      const response = await fetch('/api/git/push', {
        method: 'POST',
      })

      if (response.ok) {
        loadGitStatus()
        showNotification('success', 'Pushed successfully')
      } else {
        const error = await response.json()
        showNotification('error', `Push failed: ${error.message}`)
      }
    } catch {
      showNotification('error', 'Failed to push changes')
    } finally {
      setLoading(false)
    }
  }

  const pull = async () => {
    setLoading(true)
    try {
      const response = await fetch('/api/git/pull', {
        method: 'POST',
      })

      if (response.ok) {
        loadGitStatus()
        showNotification('success', 'Pulled successfully')
      } else {
        const error = await response.json()
        showNotification('error', `Pull failed: ${error.message}`)
      }
    } catch {
      showNotification('error', 'Failed to pull changes')
    } finally {
      setLoading(false)
    }
  }

  const checkoutBranch = async (branchName: string) => {
    setLoading(true)
    try {
      const response = await fetch('/api/git/checkout', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ branch: branchName }),
      })

      if (response.ok) {
        setCurrentBranch(branchName)
        loadGitStatus()
        loadBranches()
        showNotification('success', `Switched to branch: ${branchName}`)
      } else {
        const error = await response.json()
        showNotification('error', `Checkout failed: ${error.message}`)
      }
    } catch {
      showNotification('error', 'Failed to checkout branch')
    } finally {
      setLoading(false)
    }
  }

  const openNewBranchDialog = () => {
    setNewBranchName('')
    setNewBranchDialogOpen(true)
  }

  const createBranch = async () => {
    if (!newBranchName.trim()) {
      showNotification('error', 'Please enter a branch name')
      return
    }

    setCreatingBranch(true)
    try {
      const response = await fetch('/api/git/branch', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name: newBranchName.trim() }),
      })

      if (response.ok) {
        loadBranches()
        setNewBranchDialogOpen(false)
        setNewBranchName('')
        showNotification('success', `Branch "${newBranchName.trim()}" created`)
      } else {
        const error = await response.json()
        showNotification('error', `Create branch failed: ${error.message}`)
      }
    } catch {
      showNotification('error', 'Failed to create branch')
    } finally {
      setCreatingBranch(false)
    }
  }

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'M':
        return <span className="text-[#f59e0b]">M</span>
      case 'A':
        return <span className="text-[#4ec9b0]">A</span>
      case 'D':
        return <span className="text-[#f48771]">D</span>
      case 'R':
        return <span className="text-[#4fc3f7]">R</span>
      case 'U':
        return <span className="text-[#858585]">U</span>
      default:
        return <span>?</span>
    }
  }

  const stagedFiles = files.filter((f) => f.staged)
  const unstagedFiles = files.filter((f) => !f.staged)

  return (
    <div className="flex flex-col h-full bg-[#1e1e1e] text-[#cccccc] relative">
      <div className="flex items-center justify-between px-3 py-2 border-b border-[#3c3c3c] bg-[#2b2b2b]">
        <div className="flex items-center gap-2 text-xs font-medium text-white">
          <GitBranch className="w-3.5 h-3.5" />
          <span>{currentBranch}</span>
          {(ahead > 0 || behind > 0) && (
            <>
              {ahead > 0 && (
                <Badge variant="default" className="text-[10px]">
                  ↑{ahead}
                </Badge>
              )}
              {behind > 0 && (
                <Badge variant="default" className="text-[10px]">
                  ↓{behind}
                </Badge>
              )}
            </>
          )}
        </div>

        <div className="flex gap-1">
          <Button variant="ghost" size="sm" onClick={pull} title="Pull">
            <Download className="w-3.5 h-3.5" />
          </Button>
          <Button variant="ghost" size="sm" onClick={push} title="Push" disabled={ahead === 0}>
            <Upload className="w-3.5 h-3.5" />
          </Button>
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="ghost" size="sm">
                <MoreHorizontal className="w-3.5 h-3.5" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent>
              <DropdownMenuItem onClick={stageAll}>Stage All Changes</DropdownMenuItem>
              <DropdownMenuItem onClick={openNewBranchDialog}>New Branch</DropdownMenuItem>
              <DropdownMenuItem onClick={() => setActiveTab('branches')}>Switch Branch</DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      </div>

      <Tabs value={activeTab} onValueChange={(value) => setActiveTab(value as TabValue)} className="flex flex-col flex-1">
        <div className="px-3 py-2 border-b border-[#3c3c3c]">
          <TabsList>
            <TabsTrigger value="changes">
              Changes
              {files.length > 0 && (
                <Badge variant="secondary" className="ml-1.5 text-[10px]">{files.length}</Badge>
              )}
            </TabsTrigger>
            <TabsTrigger value="branches">Branches</TabsTrigger>
            <TabsTrigger value="history">History</TabsTrigger>
          </TabsList>
        </div>

        <div className="flex-1 overflow-auto">
          <TabsContent value="changes" className="h-full">
            <div className="px-3 py-3 border-b border-[#3c3c3c]">
              <textarea
                className="w-full bg-[#1e1e1e] border border-[#3c3c3c] rounded p-2 text-xs text-[#cccccc] resize-none focus:outline-none focus:border-[#4fc3f7]"
                placeholder="Commit message..."
                value={commitMessage}
                onChange={(e) => setCommitMessage(e.target.value)}
                rows={3}
              />
              <div className="flex gap-2 mt-2">
                <Button
                  size="sm"
                  onClick={commit}
                  disabled={stagedFiles.length === 0 || !commitMessage.trim() || loading}
                >
                  <Save className="w-3 h-3 mr-1" />
                  Commit ({stagedFiles.length})
                </Button>
                <Button
                  size="sm"
                  variant="outline"
                  onClick={stageAll}
                  disabled={unstagedFiles.length === 0}
                >
                  Stage All
                </Button>
              </div>
            </div>

            <div className="px-3 py-3">
              {loading && (
                <div className="flex flex-col items-center gap-3 px-5 py-10">
                  <Spinner size="md" />
                  <span>Loading...</span>
                </div>
              )}

              {!loading && files.length === 0 && (
                <div className="px-5 py-10 text-center text-[#858585] text-xs">No changes to commit</div>
              )}

              {!loading && stagedFiles.length > 0 && (
                <>
                  <div className="text-[11px] font-semibold text-[#858585] uppercase mb-2">
                    Staged Changes ({stagedFiles.length})
                  </div>
                  {stagedFiles.map((file) => (
                    <div key={file.path} className="flex items-center gap-2 px-2 py-1.5 bg-[#252526] border border-transparent rounded hover:border-[#4fc3f7] mb-1">
                      <Checkbox
                        checked={file.staged}
                        onCheckedChange={() => toggleFileStaging(file)}
                      />
                      <div className="text-xs font-semibold min-w-[16px]">{getStatusIcon(file.status)}</div>
                      <div className="flex-1 text-xs text-[#cccccc] truncate">{file.path}</div>
                    </div>
                  ))}
                </>
              )}

              {!loading && unstagedFiles.length > 0 && (
                <>
                  <div className="text-[11px] font-semibold text-[#858585] uppercase mb-2 mt-4">
                    Changes ({unstagedFiles.length})
                  </div>
                  {unstagedFiles.map((file) => (
                    <div key={file.path} className="flex items-center gap-2 px-2 py-1.5 bg-[#252526] border border-transparent rounded hover:border-[#4fc3f7] mb-1">
                      <Checkbox
                        checked={file.staged}
                        onCheckedChange={() => toggleFileStaging(file)}
                      />
                      <div className="text-xs font-semibold min-w-[16px]">{getStatusIcon(file.status)}</div>
                      <div className="flex-1 text-xs text-[#cccccc] truncate">{file.path}</div>
                    </div>
                  ))}
                </>
              )}
            </div>
          </TabsContent>

          <TabsContent value="branches" className="px-3 py-3">
            {branches.map((branch) => (
              <div
                key={branch.name}
                className="flex items-center gap-2 px-3 py-2 bg-[#252526] border border-[#3c3c3c] rounded mb-1 cursor-pointer hover:bg-[#2b2b2b] hover:border-[#4fc3f7]"
                onClick={() => !branch.current && checkoutBranch(branch.name)}
              >
                <GitBranch className="w-3.5 h-3.5" />
                <div className={`flex-1 text-xs ${branch.current ? 'font-semibold text-[#4fc3f7]' : 'text-[#cccccc]'}`}>
                  {branch.name}
                </div>
                {branch.current && <CheckCircle className="text-[#4fc3f7] w-3.5 h-3.5" />}
              </div>
            ))}
          </TabsContent>

          <TabsContent value="history" className="px-3 py-3">
            {commits.map((commit) => (
              <div key={commit.hash} className="px-2.5 py-2.5 bg-[#252526] border border-[#3c3c3c] rounded mb-2">
                <div className="text-[11px] text-[#4fc3f7] font-mono mb-1">{commit.abbreviated}</div>
                <div className="text-xs text-white font-medium mb-1">{commit.message}</div>
                <div className="text-[10px] text-[#858585]">
                  {commit.author} • {new Date(commit.date).toLocaleString()}
                </div>
              </div>
            ))}
          </TabsContent>
        </div>
      </Tabs>

      {/* Notification Toast */}
      {notification && (
        <div
          className={`absolute bottom-4 left-4 right-4 p-3 rounded-lg flex items-center gap-2 text-xs animate-in slide-in-from-bottom-2 ${
            notification.type === 'success'
              ? 'bg-[#1e3a2f] border border-[#4ec9b0] text-[#4ec9b0]'
              : 'bg-[#3c1f1f] border border-[#f48771] text-[#f48771]'
          }`}
        >
          {notification.type === 'success' ? (
            <CheckCircle className="w-4 h-4 flex-shrink-0" />
          ) : (
            <AlertTriangle className="w-4 h-4 flex-shrink-0" />
          )}
          <span className="flex-1">{notification.message}</span>
          <button
            onClick={() => setNotification(null)}
            className="text-current opacity-60 hover:opacity-100"
          >
            <XCircle className="w-4 h-4" />
          </button>
        </div>
      )}

      {/* New Branch Dialog */}
      <Dialog open={newBranchDialogOpen} onOpenChange={setNewBranchDialogOpen}>
        <DialogContent className="max-w-[400px] bg-[#252526] border-[#3c3c3c]">
          <DialogHeader>
            <DialogTitle className="text-white">Create New Branch</DialogTitle>
          </DialogHeader>
          <div className="py-4">
            <Input
              placeholder="Branch name (e.g., feature/my-feature)"
              value={newBranchName}
              onChange={(e) => setNewBranchName(e.target.value)}
              onKeyDown={(e) => e.key === 'Enter' && !creatingBranch && createBranch()}
              className="bg-[#1e1e1e] border-[#3c3c3c] text-[#cccccc]"
              autoFocus
              disabled={creatingBranch}
            />
            <div className="text-[11px] text-[#858585] mt-2">
              Branch will be created from: <span className="text-[#4fc3f7]">{currentBranch}</span>
            </div>
          </div>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => setNewBranchDialogOpen(false)}
              disabled={creatingBranch}
            >
              Cancel
            </Button>
            <Button
              onClick={createBranch}
              disabled={!newBranchName.trim() || creatingBranch}
            >
              {creatingBranch ? 'Creating...' : 'Create Branch'}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  )
}

