import React, { useState, useEffect } from 'react'
import { Button, Spinner } from '@persona-framework/ui'
import { AlertTriangle, RefreshCw, Flag } from 'lucide-react'
import { makeHttpClient } from '../../../data/https'

const http = makeHttpClient('/api')

const STORAGE_KEY = 'wraith_sprint_project_id'

function getStoredProjectId(): number {
  try {
    const stored = localStorage.getItem(STORAGE_KEY)
    if (stored) {
      const parsed = parseInt(stored, 10)
      if (!isNaN(parsed) && parsed > 0) return parsed
    }
  } catch {
    // Ignore storage errors
  }
  return 1 // Default project ID
}

interface SprintPanelProps {
  projectId?: number
}

interface Sprint {
  id: number
  projectId: number
  name: string
  goal?: string
  startDate: string
  endDate: string
  status: 'PLANNED' | 'ACTIVE' | 'COMPLETED'
}

interface SprintStats {
  sprintId: number
  sprintName: string
  status: 'PLANNED' | 'ACTIVE' | 'COMPLETED'
  totalTasks: number
  completedTasks: number
  remainingTasks: number
  completionPercentage: number
  totalStoryPoints: number
  completedStoryPoints: number
  remainingStoryPoints: number
  daysRemaining: number
  averageVelocity: number
  warnings: string[]
  isOverCommitted: boolean
  isUnderCommitted: boolean
}

interface Task {
  id: number
  title: string
  status: string
  storyPoints?: number
  assignee?: string
}

interface VelocityDataPoint {
  sprintId: number
  sprintName: string
  completedPoints: number
  plannedPoints: number
  completionRate: number
}

export function SprintPanel({ projectId: propProjectId }: SprintPanelProps = {}) {
  const [activeSprint, setActiveSprint] = useState<Sprint | null>(null)
  const [stats, setStats] = useState<SprintStats | null>(null)
  const [tasks, setTasks] = useState<Task[]>([])
  const [velocity, setVelocity] = useState<VelocityDataPoint[]>([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)

  // Use prop if provided, otherwise use stored/default
  const projectId = propProjectId ?? getStoredProjectId()

  const fetchActiveSprint = async () => {
    try {
      setLoading(true)
      setError(null)

      const sprintResponse = await http.get(`/nexus/sprints/project/${projectId}/active`)
      if (sprintResponse.ok) {
        const sprint = await sprintResponse.json()
        setActiveSprint(sprint)

        const statsResponse = await http.get(`/nexus/sprints/${sprint.id}/stats`)
        if (statsResponse.ok) {
          const sprintStats = await statsResponse.json()
          setStats(sprintStats)
        }

        const tasksResponse = await http.get(`/nexus/sprints/${sprint.id}/tasks`)
        if (tasksResponse.ok) {
          const sprintTasks = await tasksResponse.json()
          setTasks(sprintTasks)
        }

        const velocityResponse = await http.get(`/nexus/sprints/project/${projectId}/velocity?limit=5`)
        if (velocityResponse.ok) {
          const velocityData = await velocityResponse.json()
          setVelocity(velocityData)
        }
      } else if (sprintResponse.status === 404) {
        setActiveSprint(null)
        setStats(null)
        setTasks([])
      } else {
        setError('Failed to fetch sprint data')
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Unknown error')
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    fetchActiveSprint()
    const interval = setInterval(fetchActiveSprint, 30000)
    return () => clearInterval(interval)
  }, [projectId])

  if (loading && !activeSprint) {
    return (
      <div className="flex flex-col h-full bg-[#1e1e1e] text-[#cccccc] overflow-hidden">
        <div className="flex flex-col items-center justify-center h-full gap-3">
          <Spinner size="lg" />
          <div className="text-sm text-[#b0b0b0]">Loading sprint...</div>
        </div>
      </div>
    )
  }

  if (error) {
    return (
      <div className="flex flex-col h-full bg-[#1e1e1e] text-[#cccccc] overflow-hidden">
        <div className="flex flex-col items-center justify-center h-full p-6">
          <div className="p-3 bg-[#5a1d1d] rounded-full mb-4">
            <AlertTriangle className="w-8 h-8 text-[#f48771]" />
          </div>
          <div className="text-sm text-[#f48771] font-medium mb-2">Failed to load sprint</div>
          <div className="text-xs text-[#858585] text-center mb-4 max-w-[200px]">{error}</div>
          <Button size="sm" variant="outline" onClick={() => fetchActiveSprint()}>
            <RefreshCw className="w-3.5 h-3.5 mr-1.5" />
            Retry
          </Button>
        </div>
      </div>
    )
  }

  if (!activeSprint) {
    return (
      <div className="flex flex-col h-full bg-[#1e1e1e] text-[#cccccc] overflow-hidden">
        <div className="flex flex-col items-center justify-center h-full p-6">
          <div className="p-3 bg-[#252526] rounded-full mb-4">
            <Flag className="w-8 h-8 text-[#858585]" />
          </div>
          <div className="text-sm text-[#cccccc] font-medium mb-1">No active sprint</div>
          <div className="text-xs text-[#858585] text-center">Start a sprint to see progress here</div>
        </div>
      </div>
    )
  }

  const getStatusClass = (status: string) => {
    switch (status) {
      case 'ACTIVE':
        return 'bg-[rgba(0,255,127,0.2)] text-[#00ff7f] border border-[#00ff7f]'
      case 'PLANNED':
        return 'bg-[rgba(91,206,250,0.2)] text-[#5bcefa] border border-[#5bcefa]'
      case 'COMPLETED':
        return 'bg-[rgba(157,0,255,0.2)] text-[#9d00ff] border border-[#9d00ff]'
      default:
        return ''
    }
  }

  const maxVelocity = Math.max(...velocity.map(v => v.completedPoints), 1)

  return (
    <div className="flex flex-col h-full bg-[#1e1e1e] text-[#cccccc] overflow-hidden">
      <div className="p-4 border-b border-[#3c3c3c] bg-[#252526]">
        <div className="text-lg font-semibold text-white mb-2">
          üèÉ {activeSprint.name}
        </div>
        {activeSprint.goal && (
          <div className="text-[13px] text-[#b0b0b0] mb-3 italic">
            "{activeSprint.goal}"
          </div>
        )}
        <span className={`inline-block px-3 py-1 rounded-xl text-[11px] font-semibold uppercase ${getStatusClass(activeSprint.status)}`}>
          {activeSprint.status}
        </span>
        <div className="text-[11px] text-[#808080] mt-2">
          {new Date(activeSprint.startDate).toLocaleDateString()} - {new Date(activeSprint.endDate).toLocaleDateString()}
          {stats && ` ‚Ä¢ ${stats.daysRemaining} days remaining`}
        </div>
      </div>

      {stats && stats.warnings.length > 0 && (
        <div className="p-4 pb-0">
          {stats.warnings.map((warning, idx) => (
            <div key={idx} className="p-3 bg-[rgba(255,215,0,0.1)] border border-[#ffd700] rounded-md mb-4">
              <div className="text-xs text-[#ffd700]">‚ö†Ô∏è {warning}</div>
            </div>
          ))}
        </div>
      )}

      {stats && (
        <div className="grid grid-cols-2 gap-3 p-4 border-b border-[#3c3c3c]">
          <div className="p-3 bg-[#252526] rounded-md border border-[#3c3c3c]">
            <div className="text-[11px] text-[#808080] uppercase mb-1.5">Progress</div>
            <div className="text-2xl font-bold text-white">{stats.completionPercentage}%</div>
            <div className="text-[11px] text-[#b0b0b0] mt-1">
              {stats.completedTasks} of {stats.totalTasks} tasks
            </div>
            <div className="h-1.5 bg-[#3c3c3c] rounded-sm overflow-hidden mt-2">
              <div
                className="h-full bg-[#00ff7f] transition-all duration-300"
                style={{ width: `${stats.completionPercentage}%` }}
              />
            </div>
          </div>

          <div className="p-3 bg-[#252526] rounded-md border border-[#3c3c3c]">
            <div className="text-[11px] text-[#808080] uppercase mb-1.5">Story Points</div>
            <div className="text-2xl font-bold text-white">{stats.completedStoryPoints.toFixed(0)}</div>
            <div className="text-[11px] text-[#b0b0b0] mt-1">
              of {stats.totalStoryPoints.toFixed(0)} points
            </div>
            <div className="h-1.5 bg-[#3c3c3c] rounded-sm overflow-hidden mt-2">
              <div
                className="h-full bg-[#9d00ff] transition-all duration-300"
                style={{
                  width: `${(stats.completedStoryPoints / stats.totalStoryPoints) * 100}%`
                }}
              />
            </div>
          </div>
        </div>
      )}

      <div className="flex-1 overflow-auto p-4">
        <div className="mb-6">
          <div className="text-sm font-semibold text-white mb-3 flex items-center gap-2">
            üìã Sprint Tasks ({tasks.length})
          </div>
          {tasks.length > 0 ? (
            <div className="flex flex-col gap-2">
              {tasks.map(task => (
                <div key={task.id} className="p-3 bg-[#252526] rounded-md border border-[#3c3c3c] cursor-pointer transition-all hover:bg-[#2d2d30] hover:border-[#5bcefa]">
                  <div className="text-[13px] text-white mb-1">{task.title}</div>
                  <div className="text-[11px] text-[#808080]">
                    {task.status}
                    {task.storyPoints && ` ‚Ä¢ ${task.storyPoints} pts`}
                    {task.assignee && ` ‚Ä¢ ${task.assignee}`}
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center py-5 text-[#808080] text-xs">
              No tasks in this sprint
            </div>
          )}
        </div>

        {velocity.length > 0 && (
          <div className="mb-6">
            <div className="text-sm font-semibold text-white mb-3 flex items-center gap-2">
              üìà Team Velocity (Last 5 Sprints)
            </div>
            <div className="mt-4 p-4 bg-[#252526] rounded-md border border-[#3c3c3c]">
              <div className="flex items-end gap-3 h-[120px] mt-4">
                {velocity.map(point => (
                  <div key={point.sprintId} className="flex-1 flex flex-col items-center gap-2">
                    <div
                      className="w-full bg-[#5bcefa] rounded-t transition-all duration-300"
                      style={{
                        height: `${(point.completedPoints / maxVelocity) * 100}%`,
                        minHeight: '4px'
                      }}
                      title={`${point.completedPoints} pts completed`}
                    />
                    <div className="text-[10px] text-[#808080] text-center">
                      {point.sprintName.substring(0, 10)}...
                      <br />
                      {point.completedPoints.toFixed(0)} pts
                    </div>
                  </div>
                ))}
              </div>
              {stats && (
                <div className="mt-4 text-xs text-[#b0b0b0] text-center">
                  Average Velocity: {stats.averageVelocity.toFixed(1)} points/sprint
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  )
}
