import React, { useState, useEffect } from 'react'
import {
  Button,
  Input,
  Label,
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from '@persona-framework/ui'
import {
  Folder,
  ChevronRight,
  ChevronDown,
  ChevronUp,
  Plus,
  Trash2,
  Search,
  XCircle,
  Loader2,
} from 'lucide-react'
import { FileIcon } from '../../config/fileIcons'
import { makeHttpClient } from '../../../data/https'
import { ConfirmDialog } from '../ConfirmDialog'

const http = makeHttpClient('/api')

interface WorkspaceProject {
  id: string
  path: string
  name: string
  addedAt: string
  exists: boolean
}

interface FileNode {
  name: string
  path: string
  isDir: boolean
  size?: number | null
  children?: FileNode[] | null
}

interface ProjectTreeNode {
  projectId: string
  projectName: string
  path: string
  children?: FileNode[] | null
}

interface WorkspacePanelProps {
  onFileClick?: (filePath: string) => void
}

export function WorkspacePanel({ onFileClick }: WorkspacePanelProps = {}) {
  const [sessionId] = useState(() => {
    const stored = localStorage.getItem('workspace-session-id')
    if (stored) return stored
    const newId = `workspace-${Date.now()}`
    localStorage.setItem('workspace-session-id', newId)
    return newId
  })

  const [projects, setProjects] = useState<WorkspaceProject[]>([])
  const [projectTrees, setProjectTrees] = useState<Record<string, ProjectTreeNode>>({})
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)
  const [expanded, setExpanded] = useState<Record<string, boolean>>({})

  const [treeCollapsed, setTreeCollapsed] = useState<boolean>(() => {
    const stored = localStorage.getItem('workspace-tree-collapsed')
    return stored === 'true'
  })

  const [searchQuery, setSearchQuery] = useState('')

  const [addDialogOpen, setAddDialogOpen] = useState(false)
  const [newProjectPath, setNewProjectPath] = useState('')
  const [newProjectName, setNewProjectName] = useState('')
  const [adding, setAdding] = useState(false)
  const [addError, setAddError] = useState<string | null>(null)

  // Confirm dialog states
  const [removeConfirmOpen, setRemoveConfirmOpen] = useState(false)
  const [clearConfirmOpen, setClearConfirmOpen] = useState(false)
  const [projectToRemove, setProjectToRemove] = useState<{ id: string; name: string } | null>(null)

  useEffect(() => {
    loadWorkspace()
  }, [])

  const loadWorkspace = async () => {
    setLoading(true)
    setError(null)
    try {
      const projectsRes = await http.get('/workspace/projects', {
        headers: { 'x-session-id': sessionId },
      })
      const projectsList = projectsRes.data as WorkspaceProject[]
      setProjects(projectsList)

      if (projectsList.length > 0) {
        const treeRes = await http.get('/workspace/tree?depth=3', {
          headers: { 'x-session-id': sessionId },
        })
        setProjectTrees(treeRes.data.projects || {})
      } else {
        setProjectTrees({})
      }
    } catch (e: any) {
      setError(e?.response?.data?.message || e?.message || 'Failed to load workspace')
    } finally {
      setLoading(false)
    }
  }

  const handleAddProject = async () => {
    if (!newProjectPath.trim()) {
      setAddError('Please enter a project path')
      return
    }

    setAdding(true)
    setAddError(null)

    try {
      await http.post(
        '/workspace/projects',
        {
          path: newProjectPath.trim(),
          name: newProjectName.trim() || undefined,
        },
        {
          headers: { 'x-session-id': sessionId },
        }
      )

      await loadWorkspace()

      setAddDialogOpen(false)
      setNewProjectPath('')
      setNewProjectName('')
    } catch (e: any) {
      setAddError(e?.response?.data?.message || e?.message || 'Failed to add project')
    } finally {
      setAdding(false)
    }
  }

  const openRemoveConfirm = (projectId: string, projectName: string) => {
    setProjectToRemove({ id: projectId, name: projectName })
    setRemoveConfirmOpen(true)
  }

  const handleRemoveProject = async () => {
    if (!projectToRemove) return

    try {
      await http.delete(`/workspace/projects/${projectToRemove.id}`, {
        headers: { 'x-session-id': sessionId },
      })

      await loadWorkspace()
    } catch (e: any) {
      setError(e?.response?.data?.message || e?.message || 'Failed to remove project')
    } finally {
      setProjectToRemove(null)
    }
  }

  const handleClearWorkspace = async () => {
    try {
      await http.post('/workspace/clear', null, {
        headers: { 'x-session-id': sessionId },
      })

      await loadWorkspace()
    } catch (e: any) {
      setError(e?.response?.data?.message || e?.message || 'Failed to clear workspace')
    }
  }

  const handleToggleTreeCollapse = () => {
    const newCollapsed = !treeCollapsed
    setTreeCollapsed(newCollapsed)
    localStorage.setItem('workspace-tree-collapsed', String(newCollapsed))
  }

  const toggleExpand = (path: string) => {
    setExpanded(prev => ({ ...prev, [path]: !prev[path] }))
  }

  const filterNode = (node: FileNode, query: string): boolean => {
    if (!query) return true
    const lowerQuery = query.toLowerCase()

    if (node.name.toLowerCase().includes(lowerQuery)) {
      return true
    }

    if (node.isDir && node.children) {
      return node.children.some(child => filterNode(child, query))
    }

    return false
  }

  const filterTree = (nodes: FileNode[] | null | undefined, query: string): FileNode[] => {
    if (!nodes) return []
    if (!query) return nodes

    return nodes
      .filter(node => filterNode(node, query))
      .map(node => {
        if (node.isDir && node.children) {
          return {
            ...node,
            children: filterTree(node.children, query)
          }
        }
        return node
      })
  }

  const renderNode = (node: FileNode, depth: number = 0): React.ReactNode => {
    const isExpanded = expanded[node.path]
    const hasChildren = node.isDir && node.children && node.children.length > 0

    return (
      <div key={node.path}>
        <div
          className="flex items-center gap-1.5 py-0.5 px-2 text-[13px] text-[#cccccc] cursor-pointer hover:bg-[#2b2b2b] rounded"
          style={{ paddingLeft: `${depth * 12 + 8}px` }}
          onClick={() => {
            if (node.isDir) {
              toggleExpand(node.path)
            } else {
              if (onFileClick) {
                onFileClick(node.path)
              }
            }
          }}
        >
          {node.isDir && hasChildren && (
            <span className="flex-shrink-0">
              {isExpanded ? <ChevronDown size={14} /> : <ChevronRight size={14} />}
            </span>
          )}
          {node.isDir && !hasChildren && <span className="w-3.5" />}
          <span className="flex-shrink-0">
            {node.isDir ? <Folder size={14} /> : <FileIcon fileName={node.name} />}
          </span>
          <span className="truncate" title={node.path}>{node.name}</span>
        </div>
        {node.isDir && isExpanded && node.children && (
          <div>
            {node.children.map(child => renderNode(child, depth + 1))}
          </div>
        )}
      </div>
    )
  }

  const renderProjectTree = (projectTree: ProjectTreeNode): React.ReactNode => {
    const isExpanded = expanded[projectTree.projectId]
    const filteredChildren = filterTree(projectTree.children, searchQuery)

    if (searchQuery && filteredChildren.length === 0) {
      return null
    }

    return (
      <div key={projectTree.projectId}>
        <div
          className="flex items-center gap-1.5 py-1 px-2 text-[13px] text-[#cccccc] cursor-pointer hover:bg-[#2b2b2b] rounded"
          onClick={() => toggleExpand(projectTree.projectId)}
        >
          <span className="flex-shrink-0">
            {isExpanded ? <ChevronDown size={14} /> : <ChevronRight size={14} />}
          </span>
          <span className="flex-shrink-0">
            <Folder size={14} className="text-[#007acc]" />
          </span>
          <span className="font-semibold truncate" title={projectTree.path}>{projectTree.projectName}</span>
        </div>
        {isExpanded && filteredChildren.length > 0 && (
          <div>
            {filteredChildren.map(child => renderNode(child, 1))}
          </div>
        )}
      </div>
    )
  }

  if (loading) {
    return (
      <div className="flex flex-col h-full bg-[#1e1e1e] text-[#cccccc]">
        <div className="px-3 py-2 border-b border-[#3c3c3c] text-[13px] font-semibold">Workspace</div>
        <div className="p-4 flex justify-center">
          <div className="flex items-center gap-2 text-[13px]">
            <Loader2 size={16} className="animate-spin" />
            <span>Loading workspace...</span>
          </div>
        </div>
      </div>
    )
  }

  return (
    <div className="flex flex-col h-full bg-[#1e1e1e] text-[#cccccc]">
      <div className="px-3 py-2 border-b border-[#3c3c3c] text-[13px] font-semibold">Workspace</div>

      {projects.length > 0 && !treeCollapsed && (
        <div className="px-3 py-2 border-b border-[#3c3c3c]">
          <div className="relative">
            <Search className="absolute left-2 top-1/2 -translate-y-1/2 text-[#858585]" size={14} />
            <Input
              className="h-7 pl-7 pr-7 text-xs bg-[#252526] border-[#3c3c3c]"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              placeholder="Search files..."
            />
            {searchQuery && (
              <button
                className="absolute right-2 top-1/2 -translate-y-1/2 text-[#858585] hover:text-white"
                onClick={() => setSearchQuery('')}
              >
                <XCircle size={14} />
              </button>
            )}
          </div>
        </div>
      )}

      <div className="px-3 py-2 border-b border-[#3c3c3c] flex flex-wrap gap-1.5">
        <Button
          size="sm"
          className="h-7 text-xs"
          onClick={() => setAddDialogOpen(true)}
        >
          <Plus size={14} className="mr-1" />
          Add Project
        </Button>
        <Button
          size="sm"
          variant="outline"
          className="h-7 text-xs"
          onClick={loadWorkspace}
          disabled={loading}
        >
          Refresh
        </Button>
        {projects.length > 0 && (
          <>
            <Button
              size="sm"
              variant="outline"
              className="h-7 text-xs"
              onClick={() => setClearConfirmOpen(true)}
            >
              Clear All
            </Button>
            <Button
              size="sm"
              variant="outline"
              className="h-7 text-xs"
              onClick={handleToggleTreeCollapse}
              title={treeCollapsed ? 'Expand file tree' : 'Collapse file tree'}
            >
              {treeCollapsed ? <ChevronDown size={14} className="mr-1" /> : <ChevronUp size={14} className="mr-1" />}
              {treeCollapsed ? 'Expand Tree' : 'Collapse Tree'}
            </Button>
          </>
        )}
      </div>

      {error && (
        <div className="mx-2 my-2 p-3 bg-[#2b2b2b] border border-[#5a1d1d] rounded text-xs">
          <div className="flex items-start gap-2">
            <XCircle size={14} className="text-[#f48771] flex-shrink-0 mt-0.5" />
            <div className="flex-1">
              <div className="text-[#f48771] font-medium mb-1">Error</div>
              <div className="text-[#cccccc] mb-2">{error}</div>
              <div className="flex gap-2">
                <Button
                  size="sm"
                  variant="outline"
                  className="h-6 text-xs"
                  onClick={() => {
                    setError(null)
                    loadWorkspace()
                  }}
                >
                  Retry
                </Button>
                <Button
                  size="sm"
                  variant="ghost"
                  className="h-6 text-xs"
                  onClick={() => setError(null)}
                >
                  Dismiss
                </Button>
              </div>
            </div>
          </div>
        </div>
      )}

      {projects.length > 0 && (
        <div className="px-3 py-2 border-b border-[#3c3c3c] max-h-[200px] overflow-y-auto">
          <div className="text-[11px] text-[#858585] font-semibold mb-1.5">
            PROJECTS ({projects.length})
          </div>
          {projects.map(project => (
            <div
              key={project.id}
              className="mb-1.5 p-2 bg-[#2b2b2b] border border-[#3c3c3c] rounded flex justify-between items-center cursor-pointer hover:bg-[#323232] hover:border-[#007acc]"
            >
              <div className="flex-1 min-w-0">
                <div className="text-[13px] font-semibold text-[#cccccc] flex items-center gap-2">
                  <span className="truncate" title={project.name}>{project.name}</span>
                  <span className={`text-[10px] px-1.5 py-0.5 rounded ${project.exists ? 'bg-[#0e639c] text-white' : 'bg-[#5a1d1d] text-[#f48771]'}`}>
                    {project.exists ? 'EXISTS' : 'MISSING'}
                  </span>
                </div>
                <div className="text-[11px] text-[#858585] truncate" title={project.path}>{project.path}</div>
              </div>
              <Button
                size="sm"
                variant="ghost"
                className="h-6 w-6 p-0 ml-2 flex-shrink-0"
                onClick={() => openRemoveConfirm(project.id, project.name)}
                title="Remove from workspace"
              >
                <Trash2 size={14} />
              </Button>
            </div>
          ))}
        </div>
      )}

      {!treeCollapsed ? (
        <div className="flex-1 overflow-y-auto p-2">
          {projects.length === 0 ? (
            <div className="py-8 px-5 text-center text-[#858585] text-[13px]">
              <div className="text-4xl mb-4">
                <Folder className="w-12 h-12 mx-auto text-[#858585]" />
              </div>
              <div className="mb-2 font-semibold text-[#cccccc]">No projects yet</div>
              <div className="text-[11px] text-[#858585] mb-4">
                Add a project directory to get started
              </div>
              <Button
                size="sm"
                onClick={() => setAddDialogOpen(true)}
                className="h-8"
              >
                <Plus size={14} className="mr-1.5" />
                Add Project
              </Button>
              <div className="text-[10px] text-[#666] mt-4 space-y-1">
                <div>Quick tip: Enter the full path like</div>
                <code className="px-1.5 py-0.5 bg-[#2b2b2b] rounded text-[#4fc3f7]">/home/user/my-project</code>
              </div>
            </div>
          ) : Object.keys(projectTrees).length === 0 ? (
            <div className="py-10 px-5 text-center text-[#858585] text-[13px]">
              <div className="flex items-center justify-center gap-2">
                <Loader2 size={16} className="animate-spin" />
                <span>Loading file trees...</span>
              </div>
            </div>
          ) : (
            <div className="space-y-1">
              {Object.values(projectTrees).map(projectTree => renderProjectTree(projectTree))}
            </div>
          )}
        </div>
      ) : (
        <div className="flex-1 flex items-center justify-center text-center text-[#858585]">
          <div>
            <div className="text-3xl mb-2">üìÅ</div>
            <div className="text-[11px]">File tree collapsed</div>
            <div className="text-[10px] text-[#666] mt-1">
              Click "Expand Tree" to view files
            </div>
          </div>
        </div>
      )}

      <Dialog open={addDialogOpen} onOpenChange={setAddDialogOpen}>
        <DialogContent className="max-w-[500px] bg-[#2b2b2b] border-[#3c3c3c]">
          <DialogHeader>
            <DialogTitle>Add Project to Workspace</DialogTitle>
          </DialogHeader>
          <div className="flex flex-col gap-3 py-4">
            <div>
              <Label className="text-xs text-[#cccccc] mb-1.5 block">
                Project Path <span className="text-red-500">*</span>
              </Label>
              <Input
                className="bg-[#1e1e1e] border-[#3c3c3c] text-[#cccccc]"
                value={newProjectPath}
                onChange={(e) => setNewProjectPath(e.target.value)}
                placeholder="/home/user/my-project"
                disabled={adding}
                autoFocus
              />
              <div className="text-[11px] text-[#858585] mt-1">
                Enter the absolute path to the project directory
              </div>
            </div>

            <div>
              <Label className="text-xs text-[#cccccc] mb-1.5 block">
                Project Name (optional)
              </Label>
              <Input
                className="bg-[#1e1e1e] border-[#3c3c3c] text-[#cccccc]"
                value={newProjectName}
                onChange={(e) => setNewProjectName(e.target.value)}
                placeholder="My Project"
                disabled={adding}
              />
              <div className="text-[11px] text-[#858585] mt-1">
                If not provided, will use directory name
              </div>
            </div>

            {addError && (
              <div className="p-2 bg-[#2b2b2b] text-[#f48771] rounded text-xs">
                ‚ö†Ô∏è {addError}
              </div>
            )}

            <div className="text-[11px] text-[#007acc] p-2 bg-[#0e1f2b] rounded">
              <div className="font-semibold mb-1">Examples:</div>
              <div>‚Ä¢ /home/user/persona-framework</div>
              <div>‚Ä¢ /tmp/my-project</div>
              <div>‚Ä¢ /home/user/workspace/app</div>
            </div>
          </div>
          <DialogFooter>
            <Button
              onClick={handleAddProject}
              disabled={adding || !newProjectPath.trim()}
            >
              {adding ? 'Adding...' : 'Add to Workspace'}
            </Button>
            <Button variant="outline" onClick={() => setAddDialogOpen(false)} disabled={adding}>
              Cancel
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Remove project confirmation */}
      <ConfirmDialog
        open={removeConfirmOpen}
        onOpenChange={setRemoveConfirmOpen}
        title="Remove Project"
        description={`Remove "${projectToRemove?.name}" from workspace? This will not delete the project files.`}
        confirmLabel="Remove"
        onConfirm={handleRemoveProject}
        variant="destructive"
      />

      {/* Clear workspace confirmation */}
      <ConfirmDialog
        open={clearConfirmOpen}
        onOpenChange={setClearConfirmOpen}
        title="Clear Workspace"
        description="Remove all projects from workspace? This will not delete any project files."
        confirmLabel="Clear All"
        onConfirm={handleClearWorkspace}
        variant="destructive"
      />
    </div>
  )
}

