import React, { useRef } from 'react'
import { Plus, Trash2, Upload, File, Type } from 'lucide-react'
import type { FormDataItem } from './types'

interface FormDataEditorProps {
  items: FormDataItem[]
  onChange: (items: FormDataItem[]) => void
}

function createFormDataItem(): FormDataItem {
  return {
    id: crypto.randomUUID(),
    key: '',
    value: '',
    enabled: true,
    type: 'text',
  }
}

export function FormDataEditor({ items, onChange }: FormDataEditorProps) {
  const fileInputRefs = useRef<Record<string, HTMLInputElement | null>>({})

  const handleUpdate = (index: number, updates: Partial<FormDataItem>) => {
    onChange(
      items.map((item, i) => (i === index ? { ...item, ...updates } : item))
    )
  }

  const handleDelete = (index: number) => {
    onChange(items.filter((_, i) => i !== index))
  }

  const handleAdd = () => {
    onChange([...items, createFormDataItem()])
  }

  const handleTypeChange = (index: number, type: 'text' | 'file') => {
    onChange(
      items.map((item, i) =>
        i === index
          ? { ...item, type, value: type === 'file' ? '' : item.value, file: undefined }
          : item
      )
    )
  }

  const handleFileSelect = (index: number, file: File | null) => {
    if (!file) return
    onChange(
      items.map((item, i) =>
        i === index ? { ...item, file, value: file.name } : item
      )
    )
  }

  const formatFileSize = (bytes: number) => {
    if (bytes < 1024) return `${bytes} B`
    if (bytes < 1024 * 1024) return `${(bytes / 1024).toFixed(1)} KB`
    return `${(bytes / (1024 * 1024)).toFixed(1)} MB`
  }

  return (
    <div className="space-y-2">
      <div className="text-xs text-[#808080] mb-2">
        Form Data (multipart/form-data)
      </div>

      {items.map((item, i) => (
        <div key={item.id} className="flex items-center gap-2">
          <input
            type="checkbox"
            checked={item.enabled}
            onChange={(e) => handleUpdate(i, { enabled: e.target.checked })}
            className="w-4 h-4"
            aria-label={`Enable field ${item.key || 'unnamed'}`}
          />

          {/* Type selector */}
          <div className="flex bg-[#3c3c3c] rounded overflow-hidden">
            <button
              onClick={() => handleTypeChange(i, 'text')}
              className={`p-1.5 ${
                item.type === 'text'
                  ? 'bg-[#5bcefa] text-[#1e1e1e]'
                  : 'text-[#808080] hover:text-white'
              }`}
              aria-label="Text field"
              title="Text"
            >
              <Type className="w-3 h-3" />
            </button>
            <button
              onClick={() => handleTypeChange(i, 'file')}
              className={`p-1.5 ${
                item.type === 'file'
                  ? 'bg-[#5bcefa] text-[#1e1e1e]'
                  : 'text-[#808080] hover:text-white'
              }`}
              aria-label="File field"
              title="File"
            >
              <File className="w-3 h-3" />
            </button>
          </div>

          {/* Key input */}
          <input
            type="text"
            placeholder="Field name"
            value={item.key}
            onChange={(e) => handleUpdate(i, { key: e.target.value })}
            className="w-32 px-2 py-1.5 text-xs bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white font-mono focus:border-[#5bcefa] focus:outline-none"
            aria-label="Field name"
          />

          {/* Value input or file selector */}
          {item.type === 'text' ? (
            <input
              type="text"
              placeholder="Value"
              value={item.value}
              onChange={(e) => handleUpdate(i, { value: e.target.value })}
              className="flex-1 px-2 py-1.5 text-xs bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white font-mono focus:border-[#5bcefa] focus:outline-none"
              aria-label="Field value"
            />
          ) : (
            <div className="flex-1 flex items-center gap-2">
              <input
                ref={(el) => { fileInputRefs.current[item.id] = el }}
                type="file"
                onChange={(e) =>
                  handleFileSelect(i, e.target.files?.[0] || null)
                }
                className="hidden"
                aria-label="Select file"
              />
              <button
                onClick={() => fileInputRefs.current[item.id]?.click()}
                className="px-3 py-1.5 text-xs bg-[#3c3c3c] border border-[#3c3c3c] rounded text-[#5bcefa] hover:bg-[#4c4c4c] flex items-center gap-1.5"
              >
                <Upload className="w-3 h-3" />
                Choose File
              </button>
              {item.file ? (
                <div className="flex-1 flex items-center gap-2 text-xs text-[#b0b0b0] truncate">
                  <File className="w-3.5 h-3.5 text-[#5bcefa]" />
                  <span className="truncate">{item.file.name}</span>
                  <span className="text-[#808080]">
                    ({formatFileSize(item.file.size)})
                  </span>
                </div>
              ) : (
                <span className="text-xs text-[#808080]">No file selected</span>
              )}
            </div>
          )}

          {/* Delete button */}
          <button
            onClick={() => handleDelete(i)}
            className="p-1.5 hover:bg-[#5a1d1d] rounded"
            aria-label={`Delete field ${item.key || 'unnamed'}`}
          >
            <Trash2 className="w-3.5 h-3.5 text-[#f48771]" />
          </button>
        </div>
      ))}

      <button
        onClick={handleAdd}
        className="text-xs text-[#5bcefa] hover:text-[#7dd3fc] flex items-center gap-1"
      >
        <Plus className="w-3.5 h-3.5" />
        Add Field
      </button>
    </div>
  )
}

// Helper to build FormData from items
export function buildFormData(items: FormDataItem[]): FormData {
  const formData = new FormData()

  items
    .filter((item) => item.enabled && item.key)
    .forEach((item) => {
      if (item.type === 'file' && item.file) {
        formData.append(item.key, item.file)
      } else {
        formData.append(item.key, item.value)
      }
    })

  return formData
}

