import React from 'react'
import { MoreHorizontal } from 'lucide-react'
import { IconButton } from '@ui/components/atoms'

interface ResourceCardProps {
  title: string
  subtitle?: string
  icon?: React.ReactNode
  status?: React.ReactNode
  stats?: { label: string; value: string | number }[]
  actions?: React.ReactNode
  onClick?: () => void
  className?: string
}

export function ResourceCard({
  title,
  subtitle,
  icon,
  status,
  stats,
  actions,
  onClick,
  className = '',
}: ResourceCardProps) {
  return (
    <div
      onClick={onClick}
      className={`
        bg-[var(--bg-secondary)] rounded-lg border border-[var(--border-primary)]
        p-4 transition-all duration-150
        ${onClick ? 'cursor-pointer hover:border-[var(--accent-primary)] hover:bg-[var(--bg-hover)]' : ''}
        ${className}
      `}
    >
      <div className="flex items-start justify-between gap-3">
        <div className="flex items-start gap-3 min-w-0">
          {icon && (
            <div className="flex-shrink-0 text-[var(--accent-primary)]">
              {icon}
            </div>
          )}
          <div className="min-w-0">
            <div className="flex items-center gap-2">
              <h3 className="text-sm font-medium text-[var(--text-primary)] truncate">
                {title}
              </h3>
              {status}
            </div>
            {subtitle && (
              <p className="text-xs text-[var(--text-secondary)] mt-0.5 truncate">
                {subtitle}
              </p>
            )}
          </div>
        </div>
        {actions && (
          <div className="flex-shrink-0" onClick={(e) => e.stopPropagation()}>
            {actions}
          </div>
        )}
      </div>

      {stats && stats.length > 0 && (
        <div className="flex items-center gap-4 mt-3 pt-3 border-t border-[var(--border-subtle)]">
          {stats.map((stat, idx) => (
            <div key={idx} className="flex flex-col">
              <span className="text-xs text-[var(--text-muted)]">{stat.label}</span>
              <span className="text-sm font-medium text-[var(--text-primary)]">
                {stat.value}
              </span>
            </div>
          ))}
        </div>
      )}
    </div>
  )
}

interface ActionMenuProps {
  children: React.ReactNode
}

export function ActionMenu({ children }: ActionMenuProps) {
  return (
    <div className="relative group">
      <IconButton icon={<MoreHorizontal className="h-4 w-4" />} size="sm" />
      <div className="absolute right-0 top-full mt-1 z-10 hidden group-hover:block">
        <div className="bg-[var(--bg-primary)] border border-[var(--border-primary)] rounded-lg shadow-lg py-1 min-w-[120px]">
          {children}
        </div>
      </div>
    </div>
  )
}

interface ActionItemProps {
  icon?: React.ReactNode
  label: string
  onClick: () => void
  variant?: 'default' | 'danger'
}

export function ActionItem({ icon, label, onClick, variant = 'default' }: ActionItemProps) {
  return (
    <button
      onClick={onClick}
      className={`
        w-full flex items-center gap-2 px-3 py-1.5 text-xs text-left
        transition-colors hover:bg-[var(--bg-hover)]
        ${variant === 'danger' ? 'text-[var(--accent-error)]' : 'text-[var(--text-primary)]'}
      `}
    >
      {icon && <span className="flex-shrink-0">{icon}</span>}
      {label}
    </button>
  )
}
