import type { CloudProvider, PipelineProvider, IaCProvider, SecretsProvider } from '../../types'

interface ProviderIconProps {
  provider: CloudProvider | PipelineProvider | IaCProvider | SecretsProvider
  size?: 'sm' | 'md' | 'lg'
}

const providerConfig: Record<string, { color: string; abbrev: string }> = {
  // Cloud Providers
  aws: { color: '#FF9900', abbrev: 'AWS' },
  gcp: { color: '#4285F4', abbrev: 'GCP' },
  azure: { color: '#0078D4', abbrev: 'AZ' },
  digitalocean: { color: '#0080FF', abbrev: 'DO' },
  linode: { color: '#00B050', abbrev: 'LN' },
  vultr: { color: '#007BFC', abbrev: 'VL' },
  hetzner: { color: '#D50C2D', abbrev: 'HZ' },
  cloudflare: { color: '#F48120', abbrev: 'CF' },
  vercel: { color: '#000000', abbrev: 'VC' },
  netlify: { color: '#00C7B7', abbrev: 'NL' },
  heroku: { color: '#430098', abbrev: 'HK' },
  railway: { color: '#0B0D0E', abbrev: 'RW' },
  render: { color: '#46E3B7', abbrev: 'RD' },
  fly: { color: '#7B3FF2', abbrev: 'FY' },

  // CI/CD Providers
  'github-actions': { color: '#2088FF', abbrev: 'GH' },
  'gitlab-ci': { color: '#FC6D26', abbrev: 'GL' },
  jenkins: { color: '#D33833', abbrev: 'JK' },
  circleci: { color: '#343434', abbrev: 'CI' },
  'travis-ci': { color: '#3EAAAF', abbrev: 'TR' },
  'azure-pipelines': { color: '#0078D4', abbrev: 'AZ' },
  bitbucket: { color: '#0052CC', abbrev: 'BB' },
  drone: { color: '#212121', abbrev: 'DR' },
  tekton: { color: '#FD495C', abbrev: 'TK' },
  'argo-cd': { color: '#EF7B4D', abbrev: 'AR' },

  // IaC Providers
  terraform: { color: '#7B42BC', abbrev: 'TF' },
  pulumi: { color: '#8A3391', abbrev: 'PU' },
  cloudformation: { color: '#FF9900', abbrev: 'CF' },
  cdk: { color: '#FF9900', abbrev: 'CDK' },

  // Secrets Providers
  vault: { color: '#000000', abbrev: 'VT' },
  'aws-secrets-manager': { color: '#FF9900', abbrev: 'SM' },
  'gcp-secret-manager': { color: '#4285F4', abbrev: 'GS' },
  doppler: { color: '#000000', abbrev: 'DP' },
  local: { color: '#666666', abbrev: 'LC' },
}

const sizeClasses = {
  sm: 'w-5 h-5 text-[10px]',
  md: 'w-7 h-7 text-xs',
  lg: 'w-9 h-9 text-sm',
}

export function ProviderIcon({ provider, size = 'md' }: ProviderIconProps) {
  const config = providerConfig[provider] || { color: '#666666', abbrev: provider.slice(0, 2).toUpperCase() }

  return (
    <div
      className={`${sizeClasses[size]} rounded flex items-center justify-center font-bold text-white`}
      style={{ backgroundColor: config.color }}
      title={provider}
    >
      {config.abbrev}
    </div>
  )
}
