import { Badge } from '@ui/components/atoms'
import type { ContainerState, PipelineStatus, ComposeServiceStatus } from '../../types'

type StatusType = ContainerState | PipelineStatus | ComposeServiceStatus | 'connected' | 'error' | 'pending' | 'active' | 'updating' | 'failed' | 'firing' | 'resolved'

interface StatusBadgeProps {
  status: StatusType
  size?: 'sm' | 'md'
}

const statusConfig: Record<StatusType, { label: string; variant: 'default' | 'success' | 'warning' | 'error' | 'accent' }> = {
  // Container states
  created: { label: 'Created', variant: 'default' },
  restarting: { label: 'Restarting', variant: 'warning' },
  running: { label: 'Running', variant: 'success' },
  paused: { label: 'Paused', variant: 'warning' },
  exited: { label: 'Exited', variant: 'default' },
  dead: { label: 'Dead', variant: 'error' },

  // Pipeline states
  pending: { label: 'Pending', variant: 'default' },
  success: { label: 'Success', variant: 'success' },
  failure: { label: 'Failed', variant: 'error' },
  cancelled: { label: 'Cancelled', variant: 'default' },
  skipped: { label: 'Skipped', variant: 'default' },

  // Compose states
  stopped: { label: 'Stopped', variant: 'default' },
  partial: { label: 'Partial', variant: 'warning' },

  // Provider states
  connected: { label: 'Connected', variant: 'success' },
  error: { label: 'Error', variant: 'error' },

  // Infrastructure states
  active: { label: 'Active', variant: 'success' },
  updating: { label: 'Updating', variant: 'accent' },
  failed: { label: 'Failed', variant: 'error' },

  // Alert states
  firing: { label: 'Firing', variant: 'error' },
  resolved: { label: 'Resolved', variant: 'success' },
}

export function StatusBadge({ status, size = 'sm' }: StatusBadgeProps) {
  const config = statusConfig[status] || { label: status, variant: 'default' as const }

  return (
    <Badge variant={config.variant} size={size}>
      {config.label}
    </Badge>
  )
}
