import React from 'react'
import { ChevronRight, FileText } from 'lucide-react'

interface BreadcrumbsProps {
  filePath?: string | null
  onNavigate?: (path: string) => void
}

export function Breadcrumbs({ filePath, onNavigate }: BreadcrumbsProps) {
  if (!filePath) {
    return (
      <div className="flex items-center gap-1 px-3 py-1.5 bg-[#252526] border-b border-[#3c3c3c] text-xs text-[#cccccc] overflow-hidden whitespace-nowrap">
        <FileText className="w-3.5 h-3.5 mr-1 text-[#007acc]" />
        <span className="text-[#858585]">No file selected</span>
      </div>
    )
  }

  const segments = filePath.split('/').filter(Boolean)
  const paths = segments.map((_, index) => '/' + segments.slice(0, index + 1).join('/'))

  return (
    <div className="flex items-center gap-1 px-3 py-1.5 bg-[#252526] border-b border-[#3c3c3c] text-xs text-[#cccccc] overflow-hidden whitespace-nowrap">
      <FileText className="w-3.5 h-3.5 mr-1 text-[#007acc]" />

      {segments.map((segment, index) => {
        const isLast = index === segments.length - 1
        return (
          <React.Fragment key={index}>
            {index > 0 && <ChevronRight className="w-3 h-3 text-[#858585]" />}
            <div
              className={`inline-flex items-center px-1.5 py-0.5 rounded transition-colors ${
                isLast
                  ? 'font-semibold cursor-default'
                  : 'cursor-pointer hover:bg-[#2a2d2e]'
              }`}
              onClick={() => !isLast && onNavigate?.(paths[index])}
              title={paths[index]}
            >
              {segment}
            </div>
          </React.Fragment>
        )
      })}
    </div>
  )
}

