import { useState } from 'react'
import {
  Boxes, Plus, Play, RotateCcw, Trash2, FileCode,
  ChevronDown, ChevronRight, ExternalLink, Copy
} from 'lucide-react'
import { IconButton } from '@ui/components/atoms'
import { StatusBadge, SectionHeader, ProviderIcon, ActionMenu, ActionItem } from '../shared'
import { mockInfraStacks } from '../../mock-data'
import type { InfraStack } from '../../types'

function formatRelativeTime(dateStr: string): string {
  const diff = Date.now() - new Date(dateStr).getTime()
  const hours = Math.floor(diff / 3600000)
  const days = Math.floor(diff / 86400000)
  if (days > 0) return `${days}d ago`
  if (hours > 0) return `${hours}h ago`
  return 'Just now'
}

function StackCard({ stack }: { stack: InfraStack }) {
  const [expanded, setExpanded] = useState(false)

  return (
    <div className="bg-[var(--bg-secondary)] rounded-lg border border-[var(--border-primary)] overflow-hidden">
      <div
        className="flex items-center justify-between p-3 cursor-pointer hover:bg-[var(--bg-hover)]"
        onClick={() => setExpanded(!expanded)}
      >
        <div className="flex items-center gap-3">
          <button className="text-[var(--text-secondary)]">
            {expanded ? <ChevronDown className="h-4 w-4" /> : <ChevronRight className="h-4 w-4" />}
          </button>
          <ProviderIcon provider={stack.provider} />
          <div>
            <div className="flex items-center gap-2">
              <span className="font-medium text-[var(--text-primary)]">{stack.name}</span>
              <StatusBadge status={stack.status} />
            </div>
            <div className="flex items-center gap-2 text-xs text-[var(--text-muted)] mt-0.5">
              <Boxes className="h-3 w-3" />
              <span>{stack.resourceCount} resources</span>
              <span className="text-[var(--border-primary)]">•</span>
              <span>Updated {formatRelativeTime(stack.lastUpdated)}</span>
            </div>
          </div>
        </div>
        <div className="flex items-center gap-1" onClick={(e) => e.stopPropagation()}>
          <IconButton icon={<Play className="h-3 w-3" />} size="sm" tooltip="Apply" onClick={() => {}} />
          <IconButton icon={<FileCode className="h-3 w-3" />} size="sm" tooltip="View plan" onClick={() => {}} />
          <ActionMenu>
            <ActionItem icon={<Play className="h-3 w-3" />} label="Apply" onClick={() => {}} />
            <ActionItem icon={<FileCode className="h-3 w-3" />} label="Plan" onClick={() => {}} />
            <ActionItem icon={<RotateCcw className="h-3 w-3" />} label="Refresh" onClick={() => {}} />
            <ActionItem icon={<Trash2 className="h-3 w-3" />} label="Destroy" onClick={() => {}} variant="danger" />
          </ActionMenu>
        </div>
      </div>

      {expanded && Object.keys(stack.outputs).length > 0 && (
        <div className="border-t border-[var(--border-subtle)] bg-[var(--bg-primary)] p-4">
          <h4 className="text-xs font-medium text-[var(--text-secondary)] mb-3">Outputs</h4>
          <div className="space-y-2">
            {Object.entries(stack.outputs).map(([key, value]) => (
              <div key={key} className="flex items-center justify-between bg-[var(--bg-secondary)] rounded p-2">
                <div>
                  <span className="text-xs font-medium text-[var(--text-primary)]">{key}</span>
                  <span className="text-xs font-mono text-[var(--text-muted)] ml-2">{value}</span>
                </div>
                <IconButton
                  icon={<Copy className="h-3 w-3" />}
                  size="sm"
                  tooltip="Copy"
                  onClick={() => navigator.clipboard.writeText(value)}
                />
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  )
}

export function InfrastructureSection() {
  return (
    <div className="h-full flex flex-col">
      <SectionHeader
        title="Infrastructure"
        subtitle={`${mockInfraStacks.length} stack${mockInfraStacks.length !== 1 ? 's' : ''}`}
        icon={<Boxes className="h-5 w-5" />}
        onRefresh={() => {}}
        actions={
          <IconButton
            icon={<Plus className="h-3.5 w-3.5" />}
            size="sm"
            tooltip="New stack"
            onClick={() => {}}
          />
        }
      />

      <div className="flex-1 overflow-auto space-y-3 pr-1">
        {mockInfraStacks.map((stack) => (
          <StackCard key={stack.id} stack={stack} />
        ))}
      </div>
    </div>
  )
}
