import React, { useState, useEffect } from 'react'
import { Button, Input, Switch, Badge, Spinner, Tabs, TabsList, TabsTrigger, TabsContent } from '@persona-framework/ui'
import {
  Search,
  Plug,
  Settings,
  Trash2,
  Download,
  CheckCircle,
  XCircle,
  Star,
} from 'lucide-react'

interface Extension {
  id: string
  name: string
  author: string
  version: string
  description: string
  icon?: string
  rating: number
  downloads: number
  category: string
  tags: string[]
  installed: boolean
  enabled: boolean
  featured?: boolean
}

type TabValue = 'installed' | 'marketplace' | 'featured'

export function ExtensionManagerPanel() {
  const [searchQuery, setSearchQuery] = useState('')
  const [activeTab, setActiveTab] = useState<TabValue>('installed')
  const [extensions, setExtensions] = useState<Extension[]>([])
  const [loading, setLoading] = useState(false)

  useEffect(() => {
    loadExtensions()
  }, [activeTab])

  const loadExtensions = async () => {
    setLoading(true)

    try {
      const endpoint =
        activeTab === 'installed'
          ? '/api/extensions/installed'
          : activeTab === 'featured'
            ? '/api/extensions/featured'
            : '/api/extensions/marketplace'

      const response = await fetch(endpoint)
      if (response.ok) {
        const data = await response.json()
        setExtensions(data.extensions || getMockExtensions())
      } else {
        setExtensions(getMockExtensions())
      }
    } catch (error) {
      console.error('Failed to load extensions:', error)
      setExtensions(getMockExtensions())
    } finally {
      setLoading(false)
    }
  }

  const getMockExtensions = (): Extension[] => {
    const mockExtensions: Extension[] = [
      {
        id: 'theme-dracula',
        name: 'Dracula Theme',
        author: 'Dracula Theme',
        version: '2.24.2',
        description: 'Official Dracula Theme. A dark theme for many editors, shells, and more.',
        rating: 4.8,
        downloads: 5420000,
        category: 'Themes',
        tags: ['theme', 'dark', 'dracula'],
        installed: true,
        enabled: true,
        featured: true,
      },
      {
        id: 'prettier',
        name: 'Prettier - Code formatter',
        author: 'Prettier',
        version: '10.1.0',
        description: 'Code formatter using prettier. Supports many languages.',
        rating: 4.7,
        downloads: 28500000,
        category: 'Formatters',
        tags: ['formatter', 'prettier', 'javascript'],
        installed: true,
        enabled: true,
        featured: true,
      },
      {
        id: 'eslint',
        name: 'ESLint',
        author: 'Microsoft',
        version: '2.4.2',
        description: 'Integrates ESLint JavaScript into VS Code.',
        rating: 4.6,
        downloads: 24800000,
        category: 'Linters',
        tags: ['linter', 'eslint', 'javascript'],
        installed: true,
        enabled: false,
      },
      {
        id: 'git-graph',
        name: 'Git Graph',
        author: 'mhutchie',
        version: '1.30.0',
        description: 'View a Git Graph of your repository, and perform Git actions from the graph.',
        rating: 4.9,
        downloads: 4100000,
        category: 'Source Control',
        tags: ['git', 'graph', 'visualization'],
        installed: false,
        enabled: false,
        featured: true,
      },
      {
        id: 'docker',
        name: 'Docker',
        author: 'Microsoft',
        version: '1.26.0',
        description: 'Makes it easy to create, manage, and debug containerized applications.',
        rating: 4.5,
        downloads: 15300000,
        category: 'Containers',
        tags: ['docker', 'containers', 'devops'],
        installed: false,
        enabled: false,
      },
      {
        id: 'copilot',
        name: 'GitHub Copilot',
        author: 'GitHub',
        version: '1.120.0',
        description: 'Your AI pair programmer. Get suggestions for whole lines or entire functions.',
        rating: 4.4,
        downloads: 12000000,
        category: 'AI',
        tags: ['ai', 'copilot', 'autocomplete'],
        installed: false,
        enabled: false,
        featured: true,
      },
    ]

    if (activeTab === 'installed') {
      return mockExtensions.filter((ext) => ext.installed)
    } else if (activeTab === 'featured') {
      return mockExtensions.filter((ext) => ext.featured)
    }

    return mockExtensions
  }

  const installExtension = async (extensionId: string) => {
    try {
      await fetch('/api/extensions/install', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ extensionId }),
      })

      setExtensions((prev) =>
        prev.map((ext) =>
          ext.id === extensionId
            ? { ...ext, installed: true, enabled: true }
            : ext
        )
      )
    } catch (error) {
      console.error('Failed to install extension:', error)
      alert('Failed to install extension')
    }
  }

  const uninstallExtension = async (extensionId: string) => {
    try {
      await fetch('/api/extensions/uninstall', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ extensionId }),
      })

      setExtensions((prev) =>
        prev.map((ext) =>
          ext.id === extensionId
            ? { ...ext, installed: false, enabled: false }
            : ext
        )
      )
    } catch (error) {
      console.error('Failed to uninstall extension:', error)
      alert('Failed to uninstall extension')
    }
  }

  const toggleExtension = async (extensionId: string) => {
    const extension = extensions.find((ext) => ext.id === extensionId)
    if (!extension) return

    try {
      await fetch('/api/extensions/toggle', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ extensionId, enabled: !extension.enabled }),
      })

      setExtensions((prev) =>
        prev.map((ext) =>
          ext.id === extensionId ? { ...ext, enabled: !ext.enabled } : ext
        )
      )
    } catch (error) {
      console.error('Failed to toggle extension:', error)
    }
  }

  const filteredExtensions = extensions.filter((ext) => {
    if (!searchQuery.trim()) return true

    const query = searchQuery.toLowerCase()
    return (
      ext.name.toLowerCase().includes(query) ||
      ext.description.toLowerCase().includes(query) ||
      ext.tags.some((tag) => tag.toLowerCase().includes(query))
    )
  })

  const renderRating = (rating: number) => {
    return (
      <div className="flex items-center gap-1">
        {[...Array(5)].map((_, i) => (
          <Star
            key={i}
            className={`w-3 h-3 ${i < Math.floor(rating) ? 'fill-[#f59e0b] text-[#f59e0b]' : 'text-[#858585]'}`}
          />
        ))}
        <span className="ml-1">{rating.toFixed(1)}</span>
      </div>
    )
  }

  const formatDownloads = (count: number): string => {
    if (count >= 1000000) {
      return `${(count / 1000000).toFixed(1)}M`
    } else if (count >= 1000) {
      return `${(count / 1000).toFixed(1)}K`
    }
    return count.toString()
  }

  return (
    <div className="flex flex-col h-full bg-[#1e1e1e] text-[#cccccc]">
      <div className="flex items-center gap-2 px-3 py-3 border-b border-[#3c3c3c]">
        <Plug className="w-[18px] h-[18px]" />
        <span className="text-sm font-semibold text-white">Extension Manager</span>
      </div>

      <div className="px-3 py-3 border-b border-[#3c3c3c]">
        <div className="relative">
          <Search className="absolute left-2 top-1/2 -translate-y-1/2 w-4 h-4 text-[#858585]" />
          <Input
            placeholder="Search extensions..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="w-full pl-8"
          />
        </div>
      </div>

      <Tabs value={activeTab} onValueChange={(value) => setActiveTab(value as TabValue)} className="flex flex-col flex-1">
        <div className="px-3 py-2 border-b border-[#3c3c3c]">
          <TabsList>
            <TabsTrigger value="installed">Installed</TabsTrigger>
            <TabsTrigger value="marketplace">Marketplace</TabsTrigger>
            <TabsTrigger value="featured">Featured</TabsTrigger>
          </TabsList>
        </div>

        <TabsContent value={activeTab} className="flex-1 overflow-auto px-3 py-3">
          {loading && (
            <div className="flex flex-col items-center gap-3 px-5 py-10">
              <Spinner size="md" />
              <span>Loading extensions...</span>
            </div>
          )}

          {!loading && filteredExtensions.length === 0 && (
            <div className="px-5 py-10 text-center text-[#858585] text-[13px]">
              {searchQuery
                ? `No extensions found for "${searchQuery}"`
                : activeTab === 'installed'
                  ? 'No extensions installed. Browse the marketplace to discover extensions.'
                  : 'No extensions available.'}
            </div>
          )}

          {!loading &&
            filteredExtensions.map((extension) => (
              <div
                key={extension.id}
                className="bg-[#252526] border border-[#3c3c3c] rounded p-4 mb-3 hover:border-[#4fc3f7] hover:bg-[#2b2b2b] transition-colors"
              >
                <div className="flex items-start gap-3 mb-3">
                  <div className="w-12 h-12 min-w-[48px] rounded bg-[#4fc3f7] flex items-center justify-center text-2xl text-white">
                    <Plug />
                  </div>

                  <div className="flex-1 min-w-0">
                    <div className="text-sm font-semibold text-white mb-1">{extension.name}</div>
                    <div className="text-[11px] text-[#858585] mb-1">
                      {extension.author} • v{extension.version}
                    </div>
                  </div>

                  {extension.installed && (
                    <Switch
                      checked={extension.enabled}
                      onCheckedChange={() => toggleExtension(extension.id)}
                    />
                  )}
                </div>

                <div className="text-xs text-[#cccccc] leading-relaxed mt-2">
                  {extension.description}
                </div>

                <div className="flex items-center gap-3 text-[11px] text-[#858585] mt-2">
                  {renderRating(extension.rating)}
                  <span>•</span>
                  <span>{formatDownloads(extension.downloads)} downloads</span>
                  <span>•</span>
                  <Badge variant="secondary" className="text-[10px]">
                    {extension.category}
                  </Badge>
                </div>

                <div className="flex gap-2 mt-3">
                  {!extension.installed ? (
                    <Button
                      size="sm"
                      onClick={() => installExtension(extension.id)}
                    >
                      <Download className="w-3 h-3 mr-1" />
                      Install
                    </Button>
                  ) : (
                    <>
                      <Button
                        size="sm"
                        variant="ghost"
                      >
                        <Settings className="w-3 h-3 mr-1" />
                        Settings
                      </Button>
                      <Button
                        size="sm"
                        variant="ghost"
                        onClick={() => uninstallExtension(extension.id)}
                      >
                        <Trash2 className="w-3 h-3 mr-1" />
                        Uninstall
                      </Button>
                    </>
                  )}
                </div>
              </div>
            ))}
        </TabsContent>
      </Tabs>
    </div>
  )
}
