import React, { useState, useEffect } from 'react'
import { Button, Input, Spinner } from '@persona-framework/ui'
import { Search, XCircle, ChevronRight, ChevronDown, FileText } from 'lucide-react'

interface SearchMatch {
  line: number
  column: number
  text: string
  matchText: string
}

interface FileMatches {
  filePath: string
  fileName: string
  matches: SearchMatch[]
  expanded: boolean
}

interface SearchPanelProps {
  onFileClick?: (filePath: string, line?: number) => void
}

export function SearchPanel({ onFileClick }: SearchPanelProps) {
  const [searchQuery, setSearchQuery] = useState('')
  const [replaceQuery, setReplaceQuery] = useState('')
  const [showReplace, setShowReplace] = useState(false)
  const [caseSensitive, setCaseSensitive] = useState(false)
  const [wholeWord, setWholeWord] = useState(false)
  const [useRegex, setUseRegex] = useState(false)
  const [includePattern, setIncludePattern] = useState('')
  const [excludePattern, setExcludePattern] = useState('**/node_modules/**, **/.git/**, **/dist/**, **/build/**')
  const [showFilters, setShowFilters] = useState(false)
  const [results, setResults] = useState<FileMatches[]>([])
  const [searching, setSearching] = useState(false)
  const [totalMatches, setTotalMatches] = useState(0)

  const handleSearch = async () => {
    if (!searchQuery.trim()) {
      setResults([])
      return
    }

    setSearching(true)

    try {
      // Call backend search API
      const params = new URLSearchParams({
        query: searchQuery,
        caseSensitive: caseSensitive.toString(),
        wholeWord: wholeWord.toString(),
        useRegex: useRegex.toString(),
        include: includePattern,
        exclude: excludePattern,
      })

      const response = await fetch(`/api/workspace/search?${params}`)
      if (!response.ok) {
        throw new Error('Search failed')
      }

      const data = await response.json()

      // Transform results
      const fileMatches: FileMatches[] = data.files.map((file: any) => ({
        filePath: file.path,
        fileName: file.path.split('/').pop() || file.path,
        matches: file.matches.map((match: any) => ({
          line: match.line,
          column: match.column,
          text: match.lineText,
          matchText: match.matchText,
        })),
        expanded: true,
      }))

      setResults(fileMatches)
      setTotalMatches(fileMatches.reduce((sum, f) => sum + f.matches.length, 0))
    } catch (error) {
      console.error('Search error:', error)
      // Fallback: simple client-side search (limited)
      setResults([])
      setTotalMatches(0)
    } finally {
      setSearching(false)
    }
  }

  const handleReplace = async (filePath: string, match: SearchMatch) => {
    try {
      const response = await fetch('/api/workspace/replace', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          filePath,
          line: match.line,
          column: match.column,
          searchText: match.matchText,
          replaceText: replaceQuery,
        }),
      })

      if (response.ok) {
        // Refresh search results
        handleSearch()
      }
    } catch (error) {
      console.error('Replace error:', error)
    }
  }

  const handleReplaceAll = async () => {
    try {
      const response = await fetch('/api/workspace/replace-all', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          query: searchQuery,
          replaceText: replaceQuery,
          caseSensitive,
          wholeWord,
          useRegex,
          include: includePattern,
          exclude: excludePattern,
        }),
      })

      if (response.ok) {
        // Refresh search results
        handleSearch()
      }
    } catch (error) {
      console.error('Replace all error:', error)
    }
  }

  const toggleFileExpanded = (index: number) => {
    setResults(prev => prev.map((file, i) =>
      i === index ? { ...file, expanded: !file.expanded } : file
    ))
  }

  const highlightMatch = (text: string, matchText: string) => {
    const index = caseSensitive
      ? text.indexOf(matchText)
      : text.toLowerCase().indexOf(matchText.toLowerCase())

    if (index === -1) return <span>{text}</span>

    return (
      <>
        {text.substring(0, index)}
        <span className="bg-[#f59e0b44] text-[#f59e0b] font-semibold">
          {text.substring(index, index + matchText.length)}
        </span>
        {text.substring(index + matchText.length)}
      </>
    )
  }

  const handleMatchClick = (filePath: string, line: number) => {
    onFileClick?.(filePath, line)
  }

  // Auto-search on Enter
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if (e.key === 'Enter' && document.activeElement?.tagName === 'INPUT') {
        handleSearch()
      }
    }
    window.addEventListener('keydown', handleKeyDown)
    return () => window.removeEventListener('keydown', handleKeyDown)
  }, [searchQuery, caseSensitive, wholeWord, useRegex, includePattern, excludePattern])

  return (
    <div className="flex flex-col h-full bg-[#1e1e1e] text-[#cccccc]">
      {/* Search Section */}
      <div className="p-3 border-b border-[#3c3c3c] flex flex-col gap-2">
        {/* Search Input */}
        <div className="flex items-center gap-1">
          <div className="relative flex-1">
            <Search className="absolute left-2 top-1/2 -translate-y-1/2 h-3.5 w-3.5 text-[#858585]" />
            <Input
              placeholder="Search"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="h-7 text-xs pl-7 pr-7 bg-[#3c3c3c] border-[#3c3c3c]"
            />
            {searchQuery && (
              <XCircle
                className="absolute right-2 top-1/2 -translate-y-1/2 h-3.5 w-3.5 text-[#858585] cursor-pointer hover:text-[#cccccc]"
                onClick={() => setSearchQuery('')}
              />
            )}
          </div>
          <div className="flex gap-0.5 ml-1">
            <Button
              size="sm"
              variant={caseSensitive ? 'secondary' : 'ghost'}
              onClick={() => setCaseSensitive(!caseSensitive)}
              title="Match Case"
              className="h-7 w-7 p-0 text-xs"
            >
              Aa
            </Button>
            <Button
              size="sm"
              variant={wholeWord ? 'secondary' : 'ghost'}
              onClick={() => setWholeWord(!wholeWord)}
              title="Match Whole Word"
              className="h-7 w-7 p-0 text-xs"
            >
              ab
            </Button>
            <Button
              size="sm"
              variant={useRegex ? 'secondary' : 'ghost'}
              onClick={() => setUseRegex(!useRegex)}
              title="Use Regular Expression"
              className="h-7 w-7 p-0 text-xs"
            >
              .*
            </Button>
          </div>
        </div>

        {/* Replace Toggle */}
        <div className="flex gap-2 items-center text-[11px]">
          <Button
            size="sm"
            variant={showReplace ? 'secondary' : 'ghost'}
            onClick={() => setShowReplace(!showReplace)}
            className="h-6 text-[11px]"
          >
            Replace
          </Button>
          <Button
            size="sm"
            variant={showFilters ? 'secondary' : 'ghost'}
            onClick={() => setShowFilters(!showFilters)}
            className="h-6 text-[11px]"
          >
            Filters
          </Button>
          <Button
            size="sm"
            onClick={handleSearch}
            disabled={!searchQuery.trim()}
            className="h-6 text-[11px]"
          >
            Search
          </Button>
        </div>

        {/* Replace Input */}
        {showReplace && (
          <div className="flex items-center gap-1">
            <Input
              placeholder="Replace"
              value={replaceQuery}
              onChange={(e) => setReplaceQuery(e.target.value)}
              className="h-7 text-xs flex-1 bg-[#3c3c3c] border-[#3c3c3c]"
            />
            <Button
              size="sm"
              variant="ghost"
              disabled={results.length === 0}
              onClick={handleReplaceAll}
              className="h-7 text-[11px]"
            >
              Replace All
            </Button>
          </div>
        )}
      </div>

      {/* Filters Section */}
      {showFilters && (
        <div className="border-t border-[#3c3c3c] p-2 flex flex-col gap-2">
          <Input
            placeholder="Files to include (e.g., **/*.ts, **/*.tsx)"
            value={includePattern}
            onChange={(e) => setIncludePattern(e.target.value)}
            className="h-7 text-xs bg-[#3c3c3c] border-[#3c3c3c]"
          />
          <Input
            placeholder="Files to exclude"
            value={excludePattern}
            onChange={(e) => setExcludePattern(e.target.value)}
            className="h-7 text-xs bg-[#3c3c3c] border-[#3c3c3c]"
          />
        </div>
      )}

      {/* Results Summary */}
      {results.length > 0 && (
        <div className="px-3 py-2 border-b border-[#3c3c3c] text-xs text-[#858585] flex justify-between items-center">
          <span>
            {totalMatches} {totalMatches === 1 ? 'result' : 'results'} in {results.length} {results.length === 1 ? 'file' : 'files'}
          </span>
        </div>
      )}

      {/* Results */}
      <div className="flex-1 overflow-auto px-3 py-2">
        {searching && (
          <div className="flex flex-col items-center gap-3 py-10 px-5">
            <Spinner className="h-6 w-6" />
            <span className="text-xs text-[#858585]">Searching...</span>
          </div>
        )}

        {!searching && searchQuery && results.length === 0 && (
          <div className="py-10 px-5 text-center text-[#858585] text-[13px]">
            No results found for "{searchQuery}"
          </div>
        )}

        {!searching && results.map((file, fileIndex) => (
          <div key={fileIndex} className="mb-3">
            <div
              className="flex items-center gap-1.5 px-2 py-1.5 bg-[#2b2b2b] rounded cursor-pointer text-xs hover:bg-[#333333]"
              onClick={() => toggleFileExpanded(fileIndex)}
            >
              {file.expanded ? (
                <ChevronDown className="h-3 w-3" />
              ) : (
                <ChevronRight className="h-3 w-3" />
              )}
              <FileText className="h-3.5 w-3.5 text-[#4fc3f7]" />
              <span className="flex-1 text-[#cccccc] font-medium">{file.fileName}</span>
              <span className="text-[11px] text-[#858585] bg-[#3c3c3c] px-1.5 py-0.5 rounded-full">
                {file.matches.length}
              </span>
            </div>

            {file.expanded && file.matches.map((match, matchIndex) => (
              <div
                key={matchIndex}
                className="py-1 px-2 pl-7 text-xs cursor-pointer border-l-2 border-transparent hover:bg-[#2b2b2b] hover:border-[#4fc3f7]"
                onClick={() => handleMatchClick(file.filePath, match.line)}
              >
                <div className="flex gap-2 items-baseline">
                  <span className="text-[#858585] text-[11px] min-w-[40px]">
                    {match.line}:{match.column}
                  </span>
                  <span className="flex-1 font-mono text-[#d4d4d4] whitespace-pre overflow-hidden text-ellipsis">
                    {highlightMatch(match.text, match.matchText)}
                  </span>
                </div>
              </div>
            ))}
          </div>
        ))}
      </div>
    </div>
  )
}

