import React, { useState } from 'react'
import { Button } from '@persona-framework/ui'
import {
  Plus, Trash2, X, Check, Eye, EyeOff, Globe, Edit2
} from 'lucide-react'
import type { Environment, KeyValuePair } from './types'

interface EnvironmentPanelProps {
  environments: Environment[]
  onEnvironmentsChange: (environments: Environment[]) => void
  onClose: () => void
}

function createKeyValuePair(): KeyValuePair {
  return { id: crypto.randomUUID(), key: '', value: '', enabled: true }
}

export function EnvironmentPanel({
  environments,
  onEnvironmentsChange,
  onClose,
}: EnvironmentPanelProps) {
  const [editingEnvId, setEditingEnvId] = useState<string | null>(null)
  const [newEnvName, setNewEnvName] = useState('')
  const [revealedSecrets, setRevealedSecrets] = useState<Set<string>>(new Set())
  const [showNewEnvInput, setShowNewEnvInput] = useState(false)

  const activeEnv = environments.find(e => e.isActive)

  const handleSetActive = (envId: string) => {
    onEnvironmentsChange(
      environments.map(env => ({
        ...env,
        isActive: env.id === envId,
      }))
    )
  }

  const handleAddEnvironment = () => {
    if (!newEnvName.trim()) return

    const newEnv: Environment = {
      id: crypto.randomUUID(),
      name: newEnvName.trim(),
      variables: [],
      isActive: environments.length === 0,
    }

    onEnvironmentsChange([...environments, newEnv])
    setNewEnvName('')
    setShowNewEnvInput(false)
    setEditingEnvId(newEnv.id)
  }

  const handleDeleteEnvironment = (envId: string) => {
    const updated = environments.filter(e => e.id !== envId)
    // If we deleted the active one, make the first one active
    if (updated.length > 0 && !updated.some(e => e.isActive)) {
      updated[0].isActive = true
    }
    onEnvironmentsChange(updated)
    if (editingEnvId === envId) {
      setEditingEnvId(null)
    }
  }

  const handleUpdateVariable = (
    envId: string,
    varIndex: number,
    updates: Partial<KeyValuePair>
  ) => {
    onEnvironmentsChange(
      environments.map(env =>
        env.id === envId
          ? {
              ...env,
              variables: env.variables.map((v, i) =>
                i === varIndex ? { ...v, ...updates } : v
              ),
            }
          : env
      )
    )
  }

  const handleAddVariable = (envId: string) => {
    onEnvironmentsChange(
      environments.map(env =>
        env.id === envId
          ? { ...env, variables: [...env.variables, createKeyValuePair()] }
          : env
      )
    )
  }

  const handleDeleteVariable = (envId: string, varIndex: number) => {
    onEnvironmentsChange(
      environments.map(env =>
        env.id === envId
          ? { ...env, variables: env.variables.filter((_, i) => i !== varIndex) }
          : env
      )
    )
  }

  const handleRenameEnvironment = (envId: string, newName: string) => {
    onEnvironmentsChange(
      environments.map(env =>
        env.id === envId ? { ...env, name: newName } : env
      )
    )
  }

  const toggleSecretReveal = (varId: string) => {
    setRevealedSecrets(prev => {
      const next = new Set(prev)
      if (next.has(varId)) {
        next.delete(varId)
      } else {
        next.add(varId)
      }
      return next
    })
  }

  const isSecretKey = (key: string) => {
    const secretPatterns = /(?:key|token|secret|password|auth|credential)/i
    return secretPatterns.test(key)
  }

  const editingEnv = editingEnvId
    ? environments.find(e => e.id === editingEnvId)
    : null

  return (
    <div
      className="fixed inset-0 bg-black/50 flex items-center justify-center z-50"
      role="dialog"
      aria-modal="true"
      aria-labelledby="env-panel-title"
      onClick={(e) => {
        if (e.target === e.currentTarget) onClose()
      }}
    >
      <div className="bg-[#252526] rounded-lg w-[700px] max-h-[80vh] flex flex-col shadow-2xl">
        {/* Header */}
        <div className="flex items-center justify-between p-4 border-b border-[#3c3c3c]">
          <h3 id="env-panel-title" className="text-white font-medium flex items-center gap-2">
            <Globe className="w-4 h-4 text-[#5bcefa]" />
            Manage Environments
          </h3>
          <button
            onClick={onClose}
            className="p-1 hover:bg-[#3c3c3c] rounded"
            aria-label="Close environment panel"
          >
            <X className="w-4 h-4" />
          </button>
        </div>

        <div className="flex flex-1 overflow-hidden">
          {/* Environment List */}
          <div className="w-48 border-r border-[#3c3c3c] flex flex-col">
            <div className="p-2 border-b border-[#3c3c3c]">
              {showNewEnvInput ? (
                <div className="flex gap-1">
                  <input
                    type="text"
                    value={newEnvName}
                    onChange={(e) => setNewEnvName(e.target.value)}
                    onKeyDown={(e) => {
                      if (e.key === 'Enter') handleAddEnvironment()
                      if (e.key === 'Escape') setShowNewEnvInput(false)
                    }}
                    placeholder="Environment name"
                    className="flex-1 px-2 py-1 text-xs bg-[#3c3c3c] border border-[#5bcefa] rounded text-white"
                    autoFocus
                    aria-label="New environment name"
                  />
                  <button
                    onClick={handleAddEnvironment}
                    className="p-1 hover:bg-[#3c3c3c] rounded text-[#00ff7f]"
                    aria-label="Confirm new environment"
                  >
                    <Check className="w-3.5 h-3.5" />
                  </button>
                  <button
                    onClick={() => setShowNewEnvInput(false)}
                    className="p-1 hover:bg-[#3c3c3c] rounded text-[#f48771]"
                    aria-label="Cancel new environment"
                  >
                    <X className="w-3.5 h-3.5" />
                  </button>
                </div>
              ) : (
                <Button
                  size="sm"
                  variant="outline"
                  onClick={() => setShowNewEnvInput(true)}
                  className="w-full text-xs"
                >
                  <Plus className="w-3.5 h-3.5 mr-1" />
                  New Environment
                </Button>
              )}
            </div>

            <div className="flex-1 overflow-auto p-2">
              {environments.length === 0 ? (
                <div className="text-center text-xs text-[#808080] py-4">
                  No environments yet
                </div>
              ) : (
                environments.map((env) => (
                  <div
                    key={env.id}
                    className={`flex items-center gap-2 p-2 rounded cursor-pointer mb-1 group ${
                      editingEnvId === env.id
                        ? 'bg-[#2d2d30] border border-[#5bcefa]'
                        : 'hover:bg-[#2d2d30]'
                    }`}
                    onClick={() => setEditingEnvId(env.id)}
                    role="button"
                    aria-pressed={editingEnvId === env.id}
                    aria-label={`Edit environment ${env.name}`}
                  >
                    <input
                      type="radio"
                      checked={env.isActive}
                      onChange={() => handleSetActive(env.id)}
                      onClick={(e) => e.stopPropagation()}
                      className="w-3 h-3"
                      aria-label={`Set ${env.name} as active`}
                    />
                    <span className="flex-1 text-xs text-white truncate">
                      {env.name}
                    </span>
                    {env.isActive && (
                      <span className="text-[9px] text-[#00ff7f] bg-[rgba(0,255,127,0.15)] px-1 rounded">
                        ACTIVE
                      </span>
                    )}
                    <button
                      onClick={(e) => {
                        e.stopPropagation()
                        handleDeleteEnvironment(env.id)
                      }}
                      className="p-1 hover:bg-[#5a1d1d] rounded opacity-0 group-hover:opacity-100"
                      aria-label={`Delete environment ${env.name}`}
                    >
                      <Trash2 className="w-3 h-3 text-[#f48771]" />
                    </button>
                  </div>
                ))
              )}
            </div>
          </div>

          {/* Variable Editor */}
          <div className="flex-1 flex flex-col overflow-hidden">
            {editingEnv ? (
              <>
                <div className="p-3 border-b border-[#3c3c3c] flex items-center gap-2">
                  <input
                    type="text"
                    value={editingEnv.name}
                    onChange={(e) =>
                      handleRenameEnvironment(editingEnv.id, e.target.value)
                    }
                    className="flex-1 px-2 py-1 text-sm bg-transparent border-b border-transparent hover:border-[#3c3c3c] focus:border-[#5bcefa] text-white font-medium focus:outline-none"
                    aria-label="Environment name"
                  />
                  <span className="text-xs text-[#808080]">
                    {editingEnv.variables.length} variables
                  </span>
                </div>

                <div className="flex-1 overflow-auto p-3">
                  <div className="text-xs text-[#808080] mb-3">
                    Variables can be used in requests with {'{{VARIABLE_NAME}}'} or ${'{VARIABLE_NAME}'}
                  </div>

                  <div className="space-y-2">
                    {editingEnv.variables.map((variable, i) => {
                      const isSecret = isSecretKey(variable.key)
                      const isRevealed = revealedSecrets.has(variable.id)

                      return (
                        <div
                          key={variable.id}
                          className="flex items-center gap-2"
                        >
                          <input
                            type="checkbox"
                            checked={variable.enabled}
                            onChange={(e) =>
                              handleUpdateVariable(editingEnv.id, i, {
                                enabled: e.target.checked,
                              })
                            }
                            className="w-4 h-4"
                            aria-label={`Enable variable ${variable.key || 'unnamed'}`}
                          />
                          <input
                            type="text"
                            placeholder="Variable name"
                            value={variable.key}
                            onChange={(e) =>
                              handleUpdateVariable(editingEnv.id, i, {
                                key: e.target.value,
                              })
                            }
                            className="w-40 px-2 py-1.5 text-xs bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white font-mono focus:border-[#5bcefa] focus:outline-none"
                            aria-label="Variable name"
                          />
                          <div className="flex-1 relative">
                            <input
                              type={isSecret && !isRevealed ? 'password' : 'text'}
                              placeholder="Value"
                              value={variable.value}
                              onChange={(e) =>
                                handleUpdateVariable(editingEnv.id, i, {
                                  value: e.target.value,
                                })
                              }
                              className="w-full px-2 py-1.5 text-xs bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white font-mono pr-8 focus:border-[#5bcefa] focus:outline-none"
                              aria-label="Variable value"
                            />
                            {isSecret && (
                              <button
                                onClick={() => toggleSecretReveal(variable.id)}
                                className="absolute right-2 top-1/2 -translate-y-1/2 text-[#808080] hover:text-white"
                                aria-label={isRevealed ? 'Hide value' : 'Show value'}
                              >
                                {isRevealed ? (
                                  <EyeOff className="w-3.5 h-3.5" />
                                ) : (
                                  <Eye className="w-3.5 h-3.5" />
                                )}
                              </button>
                            )}
                          </div>
                          <button
                            onClick={() =>
                              handleDeleteVariable(editingEnv.id, i)
                            }
                            className="p-1.5 hover:bg-[#5a1d1d] rounded"
                            aria-label={`Delete variable ${variable.key || 'unnamed'}`}
                          >
                            <Trash2 className="w-3.5 h-3.5 text-[#f48771]" />
                          </button>
                        </div>
                      )
                    })}
                  </div>

                  <button
                    onClick={() => handleAddVariable(editingEnv.id)}
                    className="mt-3 text-xs text-[#5bcefa] hover:text-[#7dd3fc] flex items-center gap-1"
                  >
                    <Plus className="w-3.5 h-3.5" />
                    Add Variable
                  </button>
                </div>
              </>
            ) : (
              <div className="flex-1 flex flex-col items-center justify-center text-center p-4">
                <Globe className="w-8 h-8 text-[#808080] mb-3" />
                <div className="text-sm text-[#cccccc] font-medium">
                  Select an Environment
                </div>
                <div className="text-xs text-[#808080] mt-1">
                  Choose an environment from the list to edit its variables
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Footer */}
        <div className="flex justify-end gap-2 p-4 border-t border-[#3c3c3c]">
          <Button size="sm" onClick={onClose}>
            Done
          </Button>
        </div>
      </div>
    </div>
  )
}

