import { useState, useEffect, useRef } from 'react'
import Editor from '@monaco-editor/react'
import { Button } from '@persona-framework/ui'
import { Save, Loader2, Check } from 'lucide-react'
import { getDefaultApiClient } from '@shared/api/client'
import { AICodeAssistant } from './AICodeAssistant'
import { CollaborativeEditor } from './CollaborativeEditor'
import { getMonacoLanguage } from '../config/languages'
import type * as Monaco from 'monaco-editor'

interface EditorSettings {
  theme?: 'dark' | 'light' | 'high-contrast'
  fontSize?: number
  fontFamily?: string
  tabSize?: number
  insertSpaces?: boolean
  wordWrap?: boolean
  lineNumbers?: boolean
  minimap?: boolean
  renderWhitespace?: boolean
  cursorBlinking?: 'blink' | 'smooth' | 'phase' | 'expand' | 'solid'
  cursorStyle?: 'line' | 'block' | 'underline'
}

interface CodeEditorProps {
  filePath: string | null
  settings?: EditorSettings
}

export function CodeEditor({ filePath, settings }: CodeEditorProps) {
  const [content, setContent] = useState<string>('')
  const [loading, setLoading] = useState(false)
  const [saving, setSaving] = useState(false)
  const [saveStatus, setSaveStatus] = useState<'idle' | 'saved' | 'error'>('idle')
  const [error, setError] = useState<string | null>(null)
  const [isDirty, setIsDirty] = useState(false)
  const editorRef = useRef<Monaco.editor.IStandaloneCodeEditor | null>(null)

  const handleSave = async () => {
    if (!filePath || !isDirty || saving) return

    setSaving(true)
    setSaveStatus('idle')

    try {
      const client = getDefaultApiClient()
      await client.post('/project/file', { path: filePath, content })
      setIsDirty(false)
      setError(null)
      setSaveStatus('saved')
      // Reset saved indicator after 2 seconds
      setTimeout(() => setSaveStatus('idle'), 2000)
    } catch (err: any) {
      setError(err.message || 'Failed to save file')
      setSaveStatus('error')
    } finally {
      setSaving(false)
    }
  }

  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if ((e.ctrlKey || e.metaKey) && e.key === 's') {
        e.preventDefault()
        handleSave()
      }
    }
    window.addEventListener('keydown', handleKeyDown)
    return () => window.removeEventListener('keydown', handleKeyDown)
  }, [filePath, isDirty, content])

  useEffect(() => {
    if (!filePath) {
      setContent('')
      setError(null)
      setIsDirty(false)
      return
    }

    setLoading(true)
    setError(null)
    setIsDirty(false)

    const absolutePath = filePath.startsWith('/') ? filePath : `/workspace/persona-framework/${filePath}`

    fetch(`/api/project/read?path=${encodeURIComponent(absolutePath)}`)
      .then(response => {
        if (!response.ok) {
          if (response.status === 404) {
            throw new Error('API endpoint /api/project/read not found. Backend support needed.')
          }
          throw new Error(`Failed to read file: ${response.statusText}`)
        }
        return response.json()
      })
      .then(data => {
        setContent(data.content || '')
        setLoading(false)
      })
      .catch(err => {
        setError(err.message)
        setLoading(false)
      })
  }, [filePath])

  const getLanguage = (path: string | null): string => {
    if (!path) return 'plaintext'
    return getMonacoLanguage(path)
  }

  if (!filePath) {
    return (
      <div className="w-full h-full flex flex-col bg-[#1e1e1e]">
        <div className="p-5 text-center text-[#858585]">
          Select a file from the workspace to view
        </div>
      </div>
    )
  }

  if (loading) {
    return (
      <div className="w-full h-full flex flex-col bg-[#1e1e1e]">
        <div className="flex items-center gap-2 px-3 py-2 bg-[#2b2b2b] border-b border-[#3c3c3c] text-[#cccccc] text-xs font-mono">
          {filePath}
        </div>
        <div className="p-5 text-center text-[#858585]">Loading...</div>
      </div>
    )
  }

  if (error) {
    return (
      <div className="w-full h-full flex flex-col bg-[#1e1e1e]">
        <div className="flex items-center gap-2 px-3 py-2 bg-[#2b2b2b] border-b border-[#3c3c3c] text-[#cccccc] text-xs font-mono">
          {filePath}
        </div>
        <div className="p-5 text-[#f48771]">{error}</div>
      </div>
    )
  }

  return (
    <div className="w-full h-full flex flex-col bg-[#1e1e1e]">
      <div className="flex items-center gap-2 px-3 py-2 bg-[#2b2b2b] border-b border-[#3c3c3c] text-[#cccccc] text-xs font-mono">
        <span>{filePath}</span>
        {isDirty && <span className="text-[#f59e0b] ml-2" title="Unsaved changes">●</span>}

        {/* Save status indicator */}
        <div className="ml-auto flex items-center gap-2">
          {saveStatus === 'saved' && (
            <span className="flex items-center gap-1 text-[#4ade80] text-xs animate-in fade-in duration-200">
              <Check className="w-3 h-3" />
              Saved
            </span>
          )}
          {saveStatus === 'error' && (
            <span className="text-[#f48771] text-xs">Save failed</span>
          )}

          <Button
            variant="ghost"
            size="sm"
            onClick={handleSave}
            disabled={!isDirty || saving}
            title="Save (Ctrl+S)"
          >
            {saving ? (
              <Loader2 className="w-4 h-4 animate-spin" />
            ) : (
              <Save className="w-4 h-4" />
            )}
          </Button>
        </div>
      </div>
      <div className="flex-1 min-h-0 relative">
        <Editor
          height="100%"
          language={getLanguage(filePath)}
          value={content}
          theme={settings?.theme === 'light' ? 'vs-light' : settings?.theme === 'high-contrast' ? 'hc-black' : 'vs-dark'}
          onChange={(value) => {
            setContent(value || '')
            setIsDirty(true)
          }}
          onMount={(editor, monaco) => {
            editorRef.current = editor

            editor.addCommand(monaco.KeyMod.CtrlCmd | monaco.KeyCode.KeyD, () => {
              editor.trigger('keyboard', 'editor.action.addSelectionToNextFindMatch', {})
            })

            editor.addCommand(monaco.KeyMod.CtrlCmd | monaco.KeyMod.Shift | monaco.KeyCode.KeyL, () => {
              editor.trigger('keyboard', 'editor.action.selectHighlights', {})
            })

            editor.addCommand(monaco.KeyMod.CtrlCmd | monaco.KeyMod.Shift | monaco.KeyCode.Backslash, () => {
              editor.trigger('keyboard', 'editor.action.jumpToBracket', {})
            })

            editor.addCommand(monaco.KeyMod.CtrlCmd | monaco.KeyMod.Shift | monaco.KeyCode.BracketLeft, () => {
              editor.trigger('keyboard', 'editor.fold', {})
            })

            editor.addCommand(monaco.KeyMod.CtrlCmd | monaco.KeyMod.Shift | monaco.KeyCode.BracketRight, () => {
              editor.trigger('keyboard', 'editor.unfold', {})
            })

            editor.addCommand(monaco.KeyMod.CtrlCmd | monaco.KeyCode.KeyK, () => {
              setTimeout(() => {
                if (editor.getModel()) {
                  editor.trigger('keyboard', 'editor.foldAll', {})
                }
              }, 10)
            })

            editor.addCommand(monaco.KeyMod.Alt | monaco.KeyCode.Enter, () => {
              editor.trigger('keyboard', 'editor.action.selectAllMatches', {})
            })

            editor.addCommand(monaco.KeyMod.CtrlCmd | monaco.KeyMod.Shift | monaco.KeyCode.KeyR, () => {
              editor.trigger('keyboard', 'editor.action.refactor', {})
            })

            editor.addCommand(monaco.KeyMod.CtrlCmd | monaco.KeyCode.Period, () => {
              editor.trigger('keyboard', 'editor.action.quickFix', {})
            })

            editor.addCommand(monaco.KeyMod.Shift | monaco.KeyMod.Alt | monaco.KeyCode.KeyO, () => {
              editor.trigger('keyboard', 'editor.action.organizeImports', {})
            })
          }}
          options={{
            readOnly: false,
            minimap: { enabled: settings?.minimap ?? true },
            fontSize: settings?.fontSize ?? 14,
            fontFamily: settings?.fontFamily ?? "'Fira Code', 'Cascadia Code', 'JetBrains Mono', Consolas, monospace",
            tabSize: settings?.tabSize ?? 2,
            insertSpaces: settings?.insertSpaces ?? true,
            multiCursorModifier: 'alt',
            multiCursorMergeOverlapping: true,
            multiCursorPaste: 'spread',
            matchBrackets: 'always',
            autoClosingBrackets: 'languageDefined',
            autoClosingQuotes: 'languageDefined',
            autoSurround: 'languageDefined',
            bracketPairColorization: {
              enabled: true,
            },
            guides: {
              bracketPairs: true,
              indentation: true,
            },
            folding: true,
            foldingStrategy: 'indentation',
            showFoldingControls: 'mouseover',
            foldingHighlight: true,
            unfoldOnClickAfterEndOfLine: true,
            find: {
              addExtraSpaceOnTop: false,
              autoFindInSelection: 'never',
              seedSearchStringFromSelection: 'selection',
              globalFindClipboard: false,
              loop: true,
            },
            wordWrap: settings?.wordWrap ? 'on' : 'off',
            lineNumbers: settings?.lineNumbers ?? true ? 'on' : 'off',
            renderWhitespace: settings?.renderWhitespace ? 'all' : 'none',
            cursorBlinking: settings?.cursorBlinking ?? 'blink',
            cursorStyle: settings?.cursorStyle ?? 'line',
            scrollBeyondLastLine: false,
            automaticLayout: true,
            suggest: {
              showKeywords: true,
              showSnippets: true,
              showClasses: true,
              showFunctions: true,
              showVariables: true,
              showModules: true,
              showProperties: true,
              showMethods: true,
            },
            quickSuggestions: {
              other: true,
              comments: false,
              strings: false,
            },
            suggestOnTriggerCharacters: true,
            acceptSuggestionOnCommitCharacter: true,
            acceptSuggestionOnEnter: 'on',
            tabCompletion: 'on',
            wordBasedSuggestions: 'matchingDocuments',
            parameterHints: {
              enabled: true,
              cycle: true,
            },
            hover: {
              enabled: true,
              delay: 300,
            },
            lightbulb: {
              enabled: 'on',
            },
            codeActionsOnSave: {},
            gotoLocation: {
              multipleDefinitions: 'goto',
              multipleTypeDefinitions: 'goto',
              multipleDeclarations: 'goto',
              multipleImplementations: 'goto',
              multipleReferences: 'goto',
            },
            referenceInfos: true,
            formatOnType: true,
            formatOnPaste: false,
            renderValidationDecorations: 'on',
            'semanticHighlighting.enabled': true,
            rename: {
              enablePreview: true,
            },
            codeActionsOnSave: {
              'source.organizeImports': 'explicit',
              'source.fixAll': 'explicit',
            },
            formatOnSave: false,
            formatOnType: true,
            formatOnPaste: true,
          }}
        />
        <AICodeAssistant editor={editorRef.current} enabled={true} />
        <CollaborativeEditor
          editor={editorRef.current}
          filePath={filePath}
          userId={localStorage.getItem('wraith_user_id') || `user-${Date.now()}`}
          userName={localStorage.getItem('wraith_user_name') || 'Anonymous'}
          enabled={true}
        />
      </div>
    </div>
  )
}

