import React, { useRef, useEffect, useCallback, ReactNode } from 'react'

interface FocusTrapProps {
  children: ReactNode
  active?: boolean
  returnFocusOnDeactivate?: boolean
  autoFocus?: boolean
}

const FOCUSABLE_SELECTORS = [
  'a[href]:not([disabled])',
  'button:not([disabled])',
  'textarea:not([disabled])',
  'input:not([disabled])',
  'select:not([disabled])',
  '[tabindex]:not([tabindex="-1"])',
].join(', ')

export function FocusTrap({
  children,
  active = true,
  returnFocusOnDeactivate = true,
  autoFocus = true,
}: FocusTrapProps) {
  const containerRef = useRef<HTMLDivElement>(null)
  const previouslyFocusedRef = useRef<Element | null>(null)

  const getFocusableElements = useCallback(() => {
    if (!containerRef.current) return []
    return Array.from(containerRef.current.querySelectorAll(FOCUSABLE_SELECTORS))
  }, [])

  const focusFirst = useCallback(() => {
    const elements = getFocusableElements()
    if (elements.length > 0) {
      (elements[0] as HTMLElement).focus()
    }
  }, [getFocusableElements])

  const handleKeyDown = useCallback((e: KeyboardEvent) => {
    if (!active || e.key !== 'Tab') return

    const elements = getFocusableElements()
    if (elements.length === 0) return

    const firstElement = elements[0] as HTMLElement
    const lastElement = elements[elements.length - 1] as HTMLElement

    if (e.shiftKey) {
      // Shift+Tab: if on first element, move to last
      if (document.activeElement === firstElement) {
        e.preventDefault()
        lastElement.focus()
      }
    } else {
      // Tab: if on last element, move to first
      if (document.activeElement === lastElement) {
        e.preventDefault()
        firstElement.focus()
      }
    }
  }, [active, getFocusableElements])

  useEffect(() => {
    if (!active) return

    // Store the currently focused element
    previouslyFocusedRef.current = document.activeElement

    // Auto-focus the first focusable element
    if (autoFocus) {
      // Use a small delay to ensure the DOM is ready
      const timeoutId = setTimeout(() => {
        focusFirst()
      }, 50)
      return () => clearTimeout(timeoutId)
    }
  }, [active, autoFocus, focusFirst])

  useEffect(() => {
    if (!active) return

    document.addEventListener('keydown', handleKeyDown)
    return () => {
      document.removeEventListener('keydown', handleKeyDown)

      // Return focus to previously focused element
      if (returnFocusOnDeactivate && previouslyFocusedRef.current) {
        (previouslyFocusedRef.current as HTMLElement).focus?.()
      }
    }
  }, [active, handleKeyDown, returnFocusOnDeactivate])

  // Handle focus escaping the trap
  useEffect(() => {
    if (!active) return

    const handleFocusIn = (e: FocusEvent) => {
      if (!containerRef.current) return

      // If focus moved outside the trap, bring it back
      if (!containerRef.current.contains(e.target as Node)) {
        focusFirst()
      }
    }

    document.addEventListener('focusin', handleFocusIn)
    return () => document.removeEventListener('focusin', handleFocusIn)
  }, [active, focusFirst])

  return (
    <div ref={containerRef} className="contents">
      {children}
    </div>
  )
}
