import React, { useState, useEffect } from 'react'
import { Button } from '@persona-framework/ui'
import { DiffEditor } from '@monaco-editor/react'
import { RefreshCw, X, Loader2 } from 'lucide-react'

interface GitDiffViewerProps {
  filePath: string | null
  onClose?: () => void
}

interface DiffStats {
  additions: number
  deletions: number
  status: 'modified' | 'added' | 'deleted' | 'renamed'
}

export function GitDiffViewer({ filePath, onClose }: GitDiffViewerProps) {
  const [originalContent, setOriginalContent] = useState<string>('')
  const [modifiedContent, setModifiedContent] = useState<string>('')
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState<string | null>(null)
  const [diffStats, setDiffStats] = useState<DiffStats>({ additions: 0, deletions: 0, status: 'modified' })

  const fetchGitDiff = async (path: string) => {
    setLoading(true)
    setError(null)

    try {
      const absolutePath = path.startsWith('/') ? path : `/workspace/persona-framework/${path}`

      const currentResponse = await fetch(`/api/project/read?path=${encodeURIComponent(absolutePath)}`)
      if (!currentResponse.ok) {
        throw new Error(`Failed to read current file: ${currentResponse.statusText}`)
      }
      const currentData = await currentResponse.json()
      setModifiedContent(currentData.content || '')

      const gitResponse = await fetch(`/api/git/show?path=${encodeURIComponent(path)}&ref=HEAD`)
      if (gitResponse.ok) {
        const gitData = await gitResponse.json()
        setOriginalContent(gitData.content || '')

        const stats = calculateDiffStats(gitData.content || '', currentData.content || '')
        setDiffStats(stats)
      } else {
        setOriginalContent('')
        setDiffStats({ additions: currentData.content.split('\n').length, deletions: 0, status: 'added' })
      }

      setLoading(false)
    } catch (err: any) {
      setError(err.message || 'Failed to load git diff')
      setLoading(false)
    }
  }

  const calculateDiffStats = (original: string, modified: string): DiffStats => {
    const originalLines = original.split('\n')
    const modifiedLines = modified.split('\n')

    let additions = 0
    let deletions = 0

    const maxLines = Math.max(originalLines.length, modifiedLines.length)

    for (let i = 0; i < maxLines; i++) {
      const origLine = originalLines[i]
      const modLine = modifiedLines[i]

      if (origLine === undefined && modLine !== undefined) {
        additions++
      } else if (origLine !== undefined && modLine === undefined) {
        deletions++
      } else if (origLine !== modLine) {
        additions++
        deletions++
      }
    }

    let status: 'modified' | 'added' | 'deleted' = 'modified'
    if (original === '' && modified !== '') status = 'added'
    if (original !== '' && modified === '') status = 'deleted'

    return { additions, deletions, status }
  }

  useEffect(() => {
    if (filePath) {
      fetchGitDiff(filePath)
    }
  }, [filePath])

  const handleRefresh = () => {
    if (filePath) {
      fetchGitDiff(filePath)
    }
  }

  const getLanguage = (path: string | null): string => {
    if (!path) return 'plaintext'

    const ext = path.split('.').pop()?.toLowerCase()
    const languageMap: Record<string, string> = {
      ts: 'typescript',
      tsx: 'typescript',
      js: 'javascript',
      jsx: 'javascript',
      json: 'json',
      md: 'markdown',
      java: 'java',
      kt: 'kotlin',
      py: 'python',
      css: 'css',
      html: 'html',
      xml: 'xml',
      yaml: 'yaml',
      yml: 'yaml',
      sh: 'shell',
      sql: 'sql',
    }
    return languageMap[ext || ''] || 'plaintext'
  }

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'modified':
        return 'bg-[#f59e0b33] text-[#f59e0b]'
      case 'added':
        return 'bg-[#10b98133] text-[#10b981]'
      case 'deleted':
        return 'bg-[#ef444433] text-[#ef4444]'
      default:
        return 'bg-[#3c3c3c] text-[#cccccc]'
    }
  }

  if (!filePath) {
    return (
      <div className="w-full h-full flex flex-col bg-[#1e1e1e]">
        <div className="p-10 text-center text-[#858585]">
          Select a file to view git diff
        </div>
      </div>
    )
  }

  if (loading) {
    return (
      <div className="w-full h-full flex flex-col bg-[#1e1e1e]">
        <div className="flex items-center gap-2 px-3 py-2 bg-[#2b2b2b] border-b border-[#3c3c3c] text-[#cccccc] text-xs">
          <span>{filePath}</span>
        </div>
        <div className="flex flex-col items-center justify-center gap-3 p-10">
          <Loader2 className="w-6 h-6 animate-spin" />
          <span className="text-sm">Loading git diff...</span>
        </div>
      </div>
    )
  }

  if (error) {
    return (
      <div className="w-full h-full flex flex-col bg-[#1e1e1e]">
        <div className="flex items-center gap-2 px-3 py-2 bg-[#2b2b2b] border-b border-[#3c3c3c] text-[#cccccc] text-xs">
          <span>{filePath}</span>
          {onClose && (
            <Button
              variant="ghost"
              size="sm"
              onClick={onClose}
              className="ml-auto"
            >
              <X className="w-4 h-4" />
            </Button>
          )}
        </div>
        <div className="p-5 text-[#f48771]">{error}</div>
      </div>
    )
  }

  return (
    <div className="w-full h-full flex flex-col bg-[#1e1e1e]">
      <div className="flex items-center justify-between px-3 py-2 bg-[#2b2b2b] border-b border-[#3c3c3c] text-[#cccccc] text-xs">
        <div className="flex items-center gap-3">
          <span>{filePath}</span>
          <div className={`px-2 py-0.5 rounded-xl text-[11px] font-semibold uppercase ${getStatusColor(diffStats.status)}`}>
            {diffStats.status}
          </div>
          <div className="flex gap-4 text-[11px]">
            <div className="flex items-center gap-1">
              <span className="font-semibold text-[#10b981]">
                +{diffStats.additions}
              </span>
              <span>additions</span>
            </div>
            <div className="flex items-center gap-1">
              <span className="font-semibold text-[#ef4444]">
                -{diffStats.deletions}
              </span>
              <span>deletions</span>
            </div>
          </div>
        </div>
        <div className="flex items-center gap-2">
          <Button
            variant="ghost"
            size="sm"
            onClick={handleRefresh}
            title="Refresh diff"
          >
            <RefreshCw className="w-4 h-4" />
          </Button>
          {onClose && (
            <Button
              variant="ghost"
              size="sm"
              onClick={onClose}
              title="Close diff view"
            >
              <X className="w-4 h-4" />
            </Button>
          )}
        </div>
      </div>

      <div className="flex-1 min-h-0">
        <DiffEditor
          height="100%"
          language={getLanguage(filePath)}
          original={originalContent}
          modified={modifiedContent}
          theme="vs-dark"
          options={{
            readOnly: true,
            renderSideBySide: true,
            minimap: { enabled: false },
            fontSize: 13,
            lineNumbers: 'on',
            scrollBeyondLastLine: false,
            automaticLayout: true,
            renderOverviewRuler: true,
            diffWordWrap: 'on',
          }}
        />
      </div>
    </div>
  )
}
