import React, { useState, useEffect } from 'react'
import { Button, Spinner } from '@persona-framework/ui'
import {
  Database, Table, Key, Hash, ChevronDown, ChevronRight, Play, Plus, Trash2,
  RefreshCw, AlertTriangle, Settings, Check, X, Eye
} from 'lucide-react'
import { makeHttpClient } from '../../../data/https'

const http = makeHttpClient('/api')
const CONNECTIONS_KEY = 'wraith_db_connections'

type DatabaseType = 'postgresql' | 'mysql' | 'sqlite' | 'mongodb'

interface DatabaseConnection {
  id: string
  name: string
  type: DatabaseType
  host: string
  port: number
  database: string
  username: string
  password: string
  isConnected: boolean
}

interface TableInfo {
  name: string
  columns: ColumnInfo[]
  rowCount?: number
}

interface ColumnInfo {
  name: string
  type: string
  nullable: boolean
  isPrimaryKey: boolean
}

interface QueryResult {
  columns: string[]
  rows: Record<string, unknown>[]
  rowCount: number
  duration: number
}

const DB_TYPE_CONFIG: Record<DatabaseType, { color: string; defaultPort: number }> = {
  postgresql: { color: 'text-[#336791]', defaultPort: 5432 },
  mysql: { color: 'text-[#4479a1]', defaultPort: 3306 },
  sqlite: { color: 'text-[#003b57]', defaultPort: 0 },
  mongodb: { color: 'text-[#00ed64]', defaultPort: 27017 },
}

function loadConnections(): DatabaseConnection[] {
  try {
    const stored = localStorage.getItem(CONNECTIONS_KEY)
    if (stored) {
      const parsed = JSON.parse(stored)
      if (Array.isArray(parsed)) {
        return parsed.map(c => ({ ...c, isConnected: false }))
      }
    }
  } catch {
    // Invalid storage
  }
  return []
}

function saveConnections(connections: DatabaseConnection[]) {
  try {
    const toSave = connections.map(c => ({ ...c, isConnected: false, password: '' }))
    localStorage.setItem(CONNECTIONS_KEY, JSON.stringify(toSave))
  } catch {
    // Storage unavailable
  }
}

export function DatabaseExplorerPanel() {
  const [connections, setConnections] = useState<DatabaseConnection[]>(() => loadConnections())
  const [activeConnection, setActiveConnection] = useState<DatabaseConnection | null>(null)
  const [tables, setTables] = useState<TableInfo[]>([])
  const [expandedTables, setExpandedTables] = useState<Set<string>>(new Set())
  const [query, setQuery] = useState('SELECT * FROM ')
  const [queryResult, setQueryResult] = useState<QueryResult | null>(null)
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState<string | null>(null)
  const [showAddConnection, setShowAddConnection] = useState(false)
  const [newConnection, setNewConnection] = useState<Partial<DatabaseConnection>>({
    type: 'postgresql',
    host: 'localhost',
    port: 5432,
    database: '',
    username: '',
    password: '',
    name: '',
  })
  const [activeTab, setActiveTab] = useState<'schema' | 'query' | 'results'>('schema')

  useEffect(() => {
    saveConnections(connections)
  }, [connections])

  const connectToDatabase = async (connection: DatabaseConnection) => {
    try {
      setLoading(true)
      setError(null)

      const response = await http.post('/nexus/database/connect', {
        type: connection.type,
        host: connection.host,
        port: connection.port,
        database: connection.database,
        username: connection.username,
        password: connection.password,
      })

      if (response.ok) {
        setActiveConnection({ ...connection, isConnected: true })
        await fetchTables(connection.id)
      } else {
        // For demo, simulate connection
        setActiveConnection({ ...connection, isConnected: true })
        setTables(getDemoTables())
      }
    } catch {
      // For demo, simulate connection
      setActiveConnection({ ...connection, isConnected: true })
      setTables(getDemoTables())
    } finally {
      setLoading(false)
    }
  }

  const fetchTables = async (connectionId: string) => {
    try {
      const response = await http.get(`/nexus/database/${connectionId}/tables`)
      if (response.ok) {
        const data = await response.json()
        setTables(data)
      } else {
        setTables(getDemoTables())
      }
    } catch {
      setTables(getDemoTables())
    }
  }

  const executeQuery = async () => {
    if (!query.trim() || !activeConnection) return

    try {
      setLoading(true)
      setError(null)
      setQueryResult(null)

      const startTime = Date.now()
      const response = await http.post('/nexus/database/query', {
        connectionId: activeConnection.id,
        query,
      })

      if (response.ok) {
        const data = await response.json()
        setQueryResult(data)
        setActiveTab('results')
      } else {
        // For demo, show sample results
        setQueryResult(getDemoQueryResult(query))
        setActiveTab('results')
      }
    } catch {
      // For demo, show sample results
      setQueryResult(getDemoQueryResult(query))
      setActiveTab('results')
    } finally {
      setLoading(false)
    }
  }

  const getDemoTables = (): TableInfo[] => [
    {
      name: 'users',
      rowCount: 1234,
      columns: [
        { name: 'id', type: 'uuid', nullable: false, isPrimaryKey: true },
        { name: 'email', type: 'varchar(255)', nullable: false, isPrimaryKey: false },
        { name: 'name', type: 'varchar(100)', nullable: true, isPrimaryKey: false },
        { name: 'created_at', type: 'timestamp', nullable: false, isPrimaryKey: false },
      ],
    },
    {
      name: 'posts',
      rowCount: 5678,
      columns: [
        { name: 'id', type: 'uuid', nullable: false, isPrimaryKey: true },
        { name: 'user_id', type: 'uuid', nullable: false, isPrimaryKey: false },
        { name: 'title', type: 'varchar(200)', nullable: false, isPrimaryKey: false },
        { name: 'content', type: 'text', nullable: true, isPrimaryKey: false },
        { name: 'published', type: 'boolean', nullable: false, isPrimaryKey: false },
      ],
    },
    {
      name: 'comments',
      rowCount: 9012,
      columns: [
        { name: 'id', type: 'uuid', nullable: false, isPrimaryKey: true },
        { name: 'post_id', type: 'uuid', nullable: false, isPrimaryKey: false },
        { name: 'author', type: 'varchar(100)', nullable: false, isPrimaryKey: false },
        { name: 'body', type: 'text', nullable: false, isPrimaryKey: false },
      ],
    },
  ]

  const getDemoQueryResult = (q: string): QueryResult => {
    const duration = Math.floor(Math.random() * 50) + 5
    if (q.toLowerCase().includes('users')) {
      return {
        columns: ['id', 'email', 'name', 'created_at'],
        rows: [
          { id: '1', email: 'alice@example.com', name: 'Alice', created_at: '2024-01-15' },
          { id: '2', email: 'bob@example.com', name: 'Bob', created_at: '2024-02-20' },
          { id: '3', email: 'carol@example.com', name: 'Carol', created_at: '2024-03-10' },
        ],
        rowCount: 3,
        duration,
      }
    }
    return {
      columns: ['id', 'title', 'published'],
      rows: [
        { id: '1', title: 'First Post', published: true },
        { id: '2', title: 'Draft Post', published: false },
      ],
      rowCount: 2,
      duration,
    }
  }

  const addConnection = () => {
    if (!newConnection.name?.trim() || !newConnection.database?.trim()) return

    const connection: DatabaseConnection = {
      id: crypto.randomUUID(),
      name: newConnection.name.trim(),
      type: newConnection.type || 'postgresql',
      host: newConnection.host || 'localhost',
      port: newConnection.port || 5432,
      database: newConnection.database.trim(),
      username: newConnection.username || '',
      password: newConnection.password || '',
      isConnected: false,
    }

    setConnections([...connections, connection])
    setNewConnection({
      type: 'postgresql',
      host: 'localhost',
      port: 5432,
      database: '',
      username: '',
      password: '',
      name: '',
    })
    setShowAddConnection(false)
  }

  const deleteConnection = (id: string) => {
    if (activeConnection?.id === id) {
      setActiveConnection(null)
      setTables([])
    }
    setConnections(connections.filter(c => c.id !== id))
  }

  const toggleTable = (tableName: string) => {
    setExpandedTables(prev => {
      const next = new Set(prev)
      if (next.has(tableName)) {
        next.delete(tableName)
      } else {
        next.add(tableName)
      }
      return next
    })
  }

  return (
    <div className="flex h-full bg-[#1e1e1e] text-[#cccccc] overflow-hidden">
      {/* Sidebar - Connections & Schema */}
      <div className="w-56 border-r border-[#3c3c3c] flex flex-col">
        <div className="p-2 border-b border-[#3c3c3c] bg-[#252526]">
          <div className="flex items-center justify-between mb-2">
            <div className="flex items-center gap-2">
              <Database className="w-4 h-4 text-[#5bcefa]" />
              <span className="text-xs font-medium text-white">Connections</span>
            </div>
            <Button
              size="sm"
              variant="ghost"
              onClick={() => setShowAddConnection(!showAddConnection)}
              className="h-6 w-6 p-0"
              aria-label="Add connection"
            >
              {showAddConnection ? <X className="w-3.5 h-3.5" /> : <Plus className="w-3.5 h-3.5" />}
            </Button>
          </div>

          {showAddConnection && (
            <div className="flex flex-col gap-1.5 mt-2 p-2 bg-[#2d2d30] rounded">
              <input
                type="text"
                placeholder="Connection name"
                value={newConnection.name || ''}
                onChange={(e) => setNewConnection({ ...newConnection, name: e.target.value })}
                className="w-full px-2 py-1 text-xs bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white placeholder-[#808080] focus:outline-none focus:border-[#5bcefa]"
              />
              <select
                value={newConnection.type}
                onChange={(e) => {
                  const type = e.target.value as DatabaseType
                  setNewConnection({
                    ...newConnection,
                    type,
                    port: DB_TYPE_CONFIG[type].defaultPort,
                  })
                }}
                className="w-full px-2 py-1 text-xs bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white focus:outline-none focus:border-[#5bcefa]"
              >
                <option value="postgresql">PostgreSQL</option>
                <option value="mysql">MySQL</option>
                <option value="sqlite">SQLite</option>
                <option value="mongodb">MongoDB</option>
              </select>
              <div className="flex gap-1.5">
                <input
                  type="text"
                  placeholder="Host"
                  value={newConnection.host || ''}
                  onChange={(e) => setNewConnection({ ...newConnection, host: e.target.value })}
                  className="flex-1 px-2 py-1 text-xs bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white placeholder-[#808080] focus:outline-none focus:border-[#5bcefa]"
                />
                <input
                  type="number"
                  placeholder="Port"
                  value={newConnection.port || ''}
                  onChange={(e) => setNewConnection({ ...newConnection, port: parseInt(e.target.value) })}
                  className="w-16 px-2 py-1 text-xs bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white placeholder-[#808080] focus:outline-none focus:border-[#5bcefa]"
                />
              </div>
              <input
                type="text"
                placeholder="Database name"
                value={newConnection.database || ''}
                onChange={(e) => setNewConnection({ ...newConnection, database: e.target.value })}
                className="w-full px-2 py-1 text-xs bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white placeholder-[#808080] focus:outline-none focus:border-[#5bcefa]"
              />
              <input
                type="text"
                placeholder="Username"
                value={newConnection.username || ''}
                onChange={(e) => setNewConnection({ ...newConnection, username: e.target.value })}
                className="w-full px-2 py-1 text-xs bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white placeholder-[#808080] focus:outline-none focus:border-[#5bcefa]"
              />
              <input
                type="password"
                placeholder="Password"
                value={newConnection.password || ''}
                onChange={(e) => setNewConnection({ ...newConnection, password: e.target.value })}
                className="w-full px-2 py-1 text-xs bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white placeholder-[#808080] focus:outline-none focus:border-[#5bcefa]"
              />
              <Button size="sm" onClick={addConnection} disabled={!newConnection.name?.trim()}>
                <Plus className="w-3 h-3 mr-1" />
                Add
              </Button>
            </div>
          )}
        </div>

        {/* Connection list */}
        <div className="p-2 border-b border-[#3c3c3c] max-h-32 overflow-auto">
          {connections.length === 0 ? (
            <div className="text-center text-xs text-[#808080] py-2">No connections</div>
          ) : (
            connections.map(conn => (
              <div
                key={conn.id}
                className={`flex items-center gap-2 p-1.5 rounded cursor-pointer transition-colors mb-1 ${
                  activeConnection?.id === conn.id
                    ? 'bg-[rgba(91,206,250,0.2)] border border-[#5bcefa]'
                    : 'hover:bg-[#2d2d30]'
                }`}
                onClick={() => connectToDatabase(conn)}
              >
                <Database className={`w-3.5 h-3.5 ${DB_TYPE_CONFIG[conn.type].color}`} />
                <span className="text-xs text-white flex-1 truncate">{conn.name}</span>
                {activeConnection?.id === conn.id && conn.isConnected && (
                  <Check className="w-3 h-3 text-[#00ff7f]" />
                )}
                <button
                  onClick={(e) => {
                    e.stopPropagation()
                    deleteConnection(conn.id)
                  }}
                  className="p-0.5 rounded hover:bg-[#5a1d1d] opacity-0 group-hover:opacity-100 transition-opacity"
                  aria-label="Delete connection"
                >
                  <Trash2 className="w-3 h-3 text-[#f48771]" />
                </button>
              </div>
            ))
          )}
        </div>

        {/* Schema browser */}
        <div className="flex-1 overflow-auto p-2">
          {activeConnection ? (
            tables.length === 0 ? (
              <div className="text-center text-xs text-[#808080] py-4">No tables found</div>
            ) : (
              tables.map(table => (
                <div key={table.name} className="mb-1">
                  <div
                    className="flex items-center gap-1.5 p-1.5 rounded cursor-pointer hover:bg-[#2d2d30]"
                    onClick={() => toggleTable(table.name)}
                  >
                    {expandedTables.has(table.name) ? (
                      <ChevronDown className="w-3.5 h-3.5 text-[#808080]" />
                    ) : (
                      <ChevronRight className="w-3.5 h-3.5 text-[#808080]" />
                    )}
                    <Table className="w-3.5 h-3.5 text-[#ffd700]" />
                    <span className="text-xs text-white">{table.name}</span>
                    {table.rowCount !== undefined && (
                      <span className="text-[10px] text-[#808080]">({table.rowCount})</span>
                    )}
                  </div>
                  {expandedTables.has(table.name) && (
                    <div className="ml-5 border-l border-[#3c3c3c] pl-2">
                      {table.columns.map(col => (
                        <div
                          key={col.name}
                          className="flex items-center gap-1.5 py-0.5 text-[11px]"
                        >
                          {col.isPrimaryKey ? (
                            <Key className="w-3 h-3 text-[#ffd700]" />
                          ) : (
                            <Hash className="w-3 h-3 text-[#808080]" />
                          )}
                          <span className="text-[#b0b0b0]">{col.name}</span>
                          <span className="text-[#606060]">{col.type}</span>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              ))
            )
          ) : (
            <div className="text-center text-xs text-[#808080] py-4">
              Connect to a database to browse schema
            </div>
          )}
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col overflow-hidden">
        {/* Query Editor */}
        <div className="p-3 border-b border-[#3c3c3c] bg-[#252526]">
          <div className="flex items-center gap-2 mb-2">
            <span className="text-xs text-[#808080]">Query</span>
            <div className="flex-1" />
            <Button
              size="sm"
              onClick={executeQuery}
              disabled={loading || !activeConnection || !query.trim()}
            >
              {loading ? (
                <Spinner size="sm" />
              ) : (
                <>
                  <Play className="w-3.5 h-3.5 mr-1.5" />
                  Execute
                </>
              )}
            </Button>
          </div>
          <textarea
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="SELECT * FROM table_name WHERE ..."
            className="w-full h-20 p-2 text-sm bg-[#2d2d30] border border-[#3c3c3c] rounded text-white font-mono placeholder-[#808080] focus:outline-none focus:border-[#5bcefa] resize-none"
            onKeyDown={(e) => {
              if ((e.ctrlKey || e.metaKey) && e.key === 'Enter') {
                executeQuery()
              }
            }}
          />
          <div className="text-[10px] text-[#606060] mt-1">Ctrl+Enter to execute</div>
        </div>

        {/* Results */}
        <div className="flex-1 overflow-auto">
          {loading && (
            <div className="flex flex-col items-center justify-center h-full gap-3">
              <Spinner size="lg" />
              <div className="text-sm text-[#b0b0b0]">Executing query...</div>
            </div>
          )}

          {error && (
            <div className="p-4 m-3 bg-[rgba(244,135,113,0.1)] border border-[#f48771] rounded">
              <div className="flex items-center gap-2 text-sm text-[#f48771] font-medium">
                <AlertTriangle className="w-4 h-4" />
                Query Error
              </div>
              <div className="text-xs text-[#b0b0b0] mt-1">{error}</div>
            </div>
          )}

          {queryResult && !loading && (
            <div className="p-3">
              <div className="flex items-center gap-4 mb-3 text-xs text-[#808080]">
                <span>{queryResult.rowCount} rows</span>
                <span>{queryResult.duration}ms</span>
              </div>
              <div className="overflow-auto rounded border border-[#3c3c3c]">
                <table className="w-full text-xs">
                  <thead className="bg-[#252526] sticky top-0">
                    <tr>
                      {queryResult.columns.map(col => (
                        <th
                          key={col}
                          className="px-3 py-2 text-left text-[#b0b0b0] font-medium border-b border-[#3c3c3c]"
                        >
                          {col}
                        </th>
                      ))}
                    </tr>
                  </thead>
                  <tbody>
                    {queryResult.rows.map((row, idx) => (
                      <tr key={idx} className="hover:bg-[#2d2d30]">
                        {queryResult.columns.map(col => (
                          <td
                            key={col}
                            className="px-3 py-1.5 text-white font-mono border-b border-[#3c3c3c]"
                          >
                            {row[col] === null ? (
                              <span className="text-[#606060] italic">NULL</span>
                            ) : (
                              String(row[col])
                            )}
                          </td>
                        ))}
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}

          {!loading && !error && !queryResult && (
            <div className="flex flex-col items-center justify-center h-full text-center">
              <Database className="w-8 h-8 text-[#858585] mb-3" />
              <div className="text-sm text-[#cccccc] font-medium">No results yet</div>
              <div className="text-xs text-[#858585]">
                {activeConnection ? 'Write a query and click Execute' : 'Connect to a database first'}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  )
}

