import React, { useState, useEffect, useRef } from 'react'
import { Button, Badge, Spinner, Progress } from '@persona-framework/ui'
import {
  Play,
  Square,
  Download,
} from 'lucide-react'

interface PerformanceMetrics {
  cpu: number
  memory: number
  fps: number
  networkRequests: number
}

interface HotSpot {
  name: string
  time: number
  percentage: number
  file: string
  line: number
}

export function PerformanceMonitorPanel() {
  const [profiling, setProfiling] = useState(false)
  const [metrics, setMetrics] = useState<PerformanceMetrics>({
    cpu: 0,
    memory: 0,
    fps: 0,
    networkRequests: 0,
  })
  const [cpuHistory, setCpuHistory] = useState<number[]>([])
  const [memoryHistory, setMemoryHistory] = useState<number[]>([])
  const [hotspots, setHotspots] = useState<HotSpot[]>([])
  const canvasRef = useRef<HTMLCanvasElement>(null)

  useEffect(() => {
    if (!profiling) return

    const interval = setInterval(() => {
      const newCpu = Math.random() * 100
      const newMemory = 40 + Math.random() * 40
      const newFps = 55 + Math.random() * 10

      setMetrics({
        cpu: Math.round(newCpu),
        memory: Math.round(newMemory),
        fps: Math.round(newFps),
        networkRequests: Math.floor(Math.random() * 20),
      })

      setCpuHistory(prev => [...prev.slice(-50), newCpu])
      setMemoryHistory(prev => [...prev.slice(-50), newMemory])
    }, 1000)

    return () => clearInterval(interval)
  }, [profiling])

  useEffect(() => {
    if (!canvasRef.current || cpuHistory.length === 0) return

    const canvas = canvasRef.current
    const ctx = canvas.getContext('2d')
    if (!ctx) return

    canvas.width = canvas.offsetWidth
    canvas.height = canvas.offsetHeight

    ctx.clearRect(0, 0, canvas.width, canvas.height)

    ctx.strokeStyle = '#3c3c3c'
    ctx.lineWidth = 1
    for (let i = 0; i <= 4; i++) {
      const y = (canvas.height / 4) * i
      ctx.beginPath()
      ctx.moveTo(0, y)
      ctx.lineTo(canvas.width, y)
      ctx.stroke()
    }

    ctx.strokeStyle = '#4fc3f7'
    ctx.lineWidth = 2
    ctx.beginPath()

    const pointWidth = canvas.width / (cpuHistory.length - 1 || 1)
    cpuHistory.forEach((value, index) => {
      const x = index * pointWidth
      const y = canvas.height - (value / 100) * canvas.height

      if (index === 0) {
        ctx.moveTo(x, y)
      } else {
        ctx.lineTo(x, y)
      }
    })

    ctx.stroke()

    ctx.lineTo(canvas.width, canvas.height)
    ctx.lineTo(0, canvas.height)
    ctx.closePath()
    ctx.fillStyle = 'rgba(79, 195, 247, 0.1)'
    ctx.fill()
  }, [cpuHistory])

  const startProfiling = async () => {
    setProfiling(true)

    try {
      const response = await fetch('/api/performance/start', {
        method: 'POST',
      })

      if (response.ok) {
        setHotspots([
          { name: 'renderComponent', time: 245.3, percentage: 42.1, file: 'App.tsx', line: 156 },
          { name: 'fetchData', time: 123.8, percentage: 21.3, file: 'api.ts', line: 45 },
          { name: 'processResults', time: 89.2, percentage: 15.3, file: 'utils.ts', line: 78 },
          { name: 'updateDOM', time: 67.5, percentage: 11.6, file: 'render.ts', line: 234 },
          { name: 'validateInput', time: 45.1, percentage: 7.7, file: 'validator.ts', line: 12 },
        ])
      }
    } catch (error) {
      console.error('Failed to start profiling:', error)
    }
  }

  const stopProfiling = async () => {
    setProfiling(false)

    try {
      await fetch('/api/performance/stop', {
        method: 'POST',
      })
    } catch (error) {
      console.error('Failed to stop profiling:', error)
    }
  }

  const exportProfile = () => {
    const data = {
      metrics,
      cpuHistory,
      memoryHistory,
      hotspots,
      timestamp: new Date().toISOString(),
    }

    const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json' })
    const url = URL.createObjectURL(blob)
    const a = document.createElement('a')
    a.href = url
    a.download = `profile-${Date.now()}.json`
    a.click()
    URL.revokeObjectURL(url)
  }

  const getCpuColor = (cpu: number) => {
    if (cpu < 50) return 'default'
    if (cpu < 80) return 'default'
    return 'destructive'
  }

  const getMemoryColor = (memory: number) => {
    if (memory < 60) return 'default'
    if (memory < 85) return 'default'
    return 'destructive'
  }

  return (
    <div className="flex flex-col h-full bg-[#1e1e1e] text-[#cccccc]">
      <div className="flex items-center justify-between px-3 py-2 border-b border-[#3c3c3c] bg-[#2b2b2b]">
        <div className="text-[13px] font-semibold text-white">Performance Monitor</div>

        <div className="flex gap-1">
          {!profiling ? (
            <Button
              size="sm"
              onClick={startProfiling}
            >
              <Play className="w-3.5 h-3.5 mr-1" />
              Start Profiling
            </Button>
          ) : (
            <Button
              size="sm"
              onClick={stopProfiling}
            >
              <Square className="w-3.5 h-3.5 mr-1" />
              Stop Profiling
            </Button>
          )}
          <Button
            variant="ghost"
            size="sm"
            onClick={exportProfile}
            disabled={!profiling && cpuHistory.length === 0}
            title="Export Profile"
          >
            <Download className="w-3.5 h-3.5" />
          </Button>
        </div>
      </div>

      {profiling && (
        <div className="grid grid-cols-2 gap-3 p-3 border-b border-[#3c3c3c]">
          <div className="p-3 bg-[#252526] border border-[#3c3c3c] rounded">
            <div className="text-[10px] text-[#858585] uppercase mb-1.5">CPU Usage</div>
            <div className="text-2xl font-bold text-white mb-1">{metrics.cpu}%</div>
            <div className="mb-2">
              <Badge variant={getCpuColor(metrics.cpu)} className="text-[10px]">
                {metrics.cpu < 50 ? 'Normal' : metrics.cpu < 80 ? 'High' : 'Critical'}
              </Badge>
            </div>
            <Progress value={metrics.cpu} className="h-1.5" />
          </div>

          <div className="p-3 bg-[#252526] border border-[#3c3c3c] rounded">
            <div className="text-[10px] text-[#858585] uppercase mb-1.5">Memory</div>
            <div className="text-2xl font-bold text-white mb-1">{metrics.memory}%</div>
            <div className="mb-2">
              <Badge variant={getMemoryColor(metrics.memory)} className="text-[10px]">
                {metrics.memory < 60 ? 'Normal' : metrics.memory < 85 ? 'High' : 'Critical'}
              </Badge>
            </div>
            <Progress value={metrics.memory} className="h-1.5" />
          </div>

          <div className="p-3 bg-[#252526] border border-[#3c3c3c] rounded">
            <div className="text-[10px] text-[#858585] uppercase mb-1.5">Frame Rate</div>
            <div className="text-2xl font-bold text-white">{metrics.fps} FPS</div>
            <div className="text-[10px] text-[#b0b0b0] mt-1">
              {metrics.fps >= 55 ? 'âœ“ Smooth' : 'âš  Lagging'}
            </div>
          </div>

          <div className="p-3 bg-[#252526] border border-[#3c3c3c] rounded">
            <div className="text-[10px] text-[#858585] uppercase mb-1.5">Network</div>
            <div className="text-2xl font-bold text-white">{metrics.networkRequests}</div>
            <div className="text-[10px] text-[#b0b0b0] mt-1">Active requests</div>
          </div>
        </div>
      )}

      <div className="flex-1 overflow-auto p-3">
        {!profiling && cpuHistory.length === 0 && (
          <div className="flex flex-col items-center justify-center h-full text-center text-[#858585] text-xs">
            <div className="text-5xl mb-3">ðŸ“Š</div>
            <div>Click "Start Profiling" to begin monitoring performance</div>
          </div>
        )}

        {profiling && (
          <>
            <div className="mb-5">
              <div className="text-[11px] font-semibold text-[#858585] uppercase mb-2">CPU Usage Over Time</div>
              <div className="h-[120px] bg-[#252526] border border-[#3c3c3c] rounded p-2 relative">
                <canvas ref={canvasRef} className="w-full h-full" />
              </div>
            </div>

            <div className="mb-5">
              <div className="text-[11px] font-semibold text-[#858585] uppercase mb-2">Performance Hotspots</div>
              <div className="flex flex-col gap-1">
                {hotspots.map((hotspot, index) => (
                  <div key={index} className="flex items-center justify-between px-3 py-2 bg-[#252526] border border-[#3c3c3c] rounded text-[11px]">
                    <div className="flex-1">
                      <div className="text-[#dcdcaa] font-medium">{hotspot.name}</div>
                      <div className="text-[#858585] text-[10px]">
                        {hotspot.file}:{hotspot.line}
                      </div>
                    </div>
                    <div className="text-[#f48771] font-semibold ml-3">{hotspot.time.toFixed(1)}ms</div>
                    <div className="text-[#858585] text-[10px] ml-2">{hotspot.percentage.toFixed(1)}%</div>
                  </div>
                ))}
              </div>
            </div>

            <div className="mb-5">
              <div className="text-[11px] font-semibold text-[#858585] uppercase mb-2">Call Stack Flame Graph</div>
              <div className="bg-[#252526] border border-[#3c3c3c] rounded p-3 min-h-[200px]">
                {hotspots.map((hotspot, index) => (
                  <div
                    key={index}
                    className="h-5 mb-0.5 flex items-center pl-2 text-[10px] text-white cursor-pointer rounded hover:opacity-80"
                    style={{
                      width: `${hotspot.percentage}%`,
                      backgroundColor: `hsl(${200 - index * 30}, 70%, 50%)`,
                      marginLeft: `${index * 20}px`,
                    }}
                  >
                    {hotspot.name} ({hotspot.time.toFixed(1)}ms)
                  </div>
                ))}
              </div>
            </div>
          </>
        )}
      </div>
    </div>
  )
}
