import { useState } from 'react'
import {
  Cloud, Server, Database, Globe, Cpu, HardDrive,
  Plus, Settings, RefreshCw, Trash2, ChevronDown, ChevronRight
} from 'lucide-react'
import { IconButton } from '@ui/components/atoms'
import { StatusBadge, SectionHeader, ProviderIcon, DataTable, ResourceCard, ActionMenu, ActionItem } from '../shared'
import { mockProviders, mockEC2Instances, mockDroplets } from '../../mock-data'
import type { CloudProviderConfig, EC2Instance, Droplet } from '../../types'

function formatRelativeTime(dateStr?: string): string {
  if (!dateStr) return '-'
  const diff = Date.now() - new Date(dateStr).getTime()
  const mins = Math.floor(diff / 60000)
  const hours = Math.floor(diff / 3600000)
  if (hours > 0) return `${hours}h ago`
  if (mins > 0) return `${mins}m ago`
  return 'Just now'
}

function ProviderCard({ provider }: { provider: CloudProviderConfig }) {
  const [expanded, setExpanded] = useState(false)

  const getProviderResources = () => {
    switch (provider.provider) {
      case 'aws':
        return (
          <div className="space-y-4">
            <div>
              <h4 className="text-xs font-medium text-[var(--text-secondary)] mb-2 flex items-center gap-2">
                <Server className="h-3 w-3" /> EC2 Instances
              </h4>
              <DataTable
                data={mockEC2Instances}
                columns={[
                  {
                    key: 'name',
                    header: 'Name',
                    render: (i: EC2Instance) => (
                      <span className="text-[var(--text-primary)]">{i.tags.Name || i.instanceId}</span>
                    ),
                  },
                  {
                    key: 'type',
                    header: 'Type',
                    render: (i: EC2Instance) => (
                      <span className="text-xs font-mono text-[var(--text-secondary)]">{i.instanceType}</span>
                    ),
                  },
                  {
                    key: 'state',
                    header: 'State',
                    width: '100px',
                    render: (i: EC2Instance) => <StatusBadge status={i.state} />,
                  },
                  {
                    key: 'ip',
                    header: 'IP',
                    render: (i: EC2Instance) => (
                      <span className="text-xs font-mono text-[var(--text-muted)]">
                        {i.publicIp || i.privateIp}
                      </span>
                    ),
                  },
                ]}
                keyExtractor={(i) => i.instanceId}
                compact
              />
            </div>
          </div>
        )
      case 'digitalocean':
        return (
          <div className="space-y-4">
            <div>
              <h4 className="text-xs font-medium text-[var(--text-secondary)] mb-2 flex items-center gap-2">
                <HardDrive className="h-3 w-3" /> Droplets
              </h4>
              <DataTable
                data={mockDroplets}
                columns={[
                  {
                    key: 'name',
                    header: 'Name',
                    render: (d: Droplet) => (
                      <span className="text-[var(--text-primary)]">{d.name}</span>
                    ),
                  },
                  {
                    key: 'specs',
                    header: 'Specs',
                    render: (d: Droplet) => (
                      <span className="text-xs text-[var(--text-secondary)]">
                        {d.vcpus} vCPU / {d.memory / 1024}GB RAM / {d.disk}GB
                      </span>
                    ),
                  },
                  {
                    key: 'state',
                    header: 'Status',
                    width: '100px',
                    render: (d: Droplet) => <StatusBadge status={d.status} />,
                  },
                  {
                    key: 'ip',
                    header: 'IP',
                    render: (d: Droplet) => (
                      <span className="text-xs font-mono text-[var(--text-muted)]">{d.ipAddress}</span>
                    ),
                  },
                ]}
                keyExtractor={(d) => String(d.id)}
                compact
              />
            </div>
          </div>
        )
      default:
        return (
          <div className="text-center py-8 text-[var(--text-muted)] text-sm">
            Resources view not yet implemented for {provider.provider}
          </div>
        )
    }
  }

  return (
    <div className="bg-[var(--bg-secondary)] rounded-lg border border-[var(--border-primary)] overflow-hidden">
      <div
        className="flex items-center justify-between p-3 cursor-pointer hover:bg-[var(--bg-hover)]"
        onClick={() => setExpanded(!expanded)}
      >
        <div className="flex items-center gap-3">
          <button className="text-[var(--text-secondary)]">
            {expanded ? <ChevronDown className="h-4 w-4" /> : <ChevronRight className="h-4 w-4" />}
          </button>
          <ProviderIcon provider={provider.provider} size="lg" />
          <div>
            <div className="flex items-center gap-2">
              <span className="font-medium text-[var(--text-primary)]">{provider.name}</span>
              <StatusBadge status={provider.status} />
            </div>
            <div className="flex items-center gap-2 text-xs text-[var(--text-muted)] mt-0.5">
              {provider.region && (
                <>
                  <Globe className="h-3 w-3" />
                  <span>{provider.region}</span>
                  <span className="text-[var(--border-primary)]">•</span>
                </>
              )}
              <span>Synced {formatRelativeTime(provider.lastSync)}</span>
            </div>
          </div>
        </div>
        <div className="flex items-center gap-1" onClick={(e) => e.stopPropagation()}>
          <IconButton icon={<RefreshCw className="h-3 w-3" />} size="sm" tooltip="Sync" onClick={() => {}} />
          <IconButton icon={<Settings className="h-3 w-3" />} size="sm" tooltip="Settings" onClick={() => {}} />
          <ActionMenu>
            <ActionItem icon={<RefreshCw className="h-3 w-3" />} label="Sync" onClick={() => {}} />
            <ActionItem icon={<Settings className="h-3 w-3" />} label="Configure" onClick={() => {}} />
            <ActionItem icon={<Trash2 className="h-3 w-3" />} label="Disconnect" onClick={() => {}} variant="danger" />
          </ActionMenu>
        </div>
      </div>

      {expanded && (
        <div className="border-t border-[var(--border-subtle)] bg-[var(--bg-primary)] p-4">
          {getProviderResources()}
        </div>
      )}
    </div>
  )
}

export function ProvidersSection() {
  return (
    <div className="h-full flex flex-col">
      <SectionHeader
        title="Cloud Providers"
        subtitle={`${mockProviders.length} provider${mockProviders.length !== 1 ? 's' : ''} connected`}
        icon={<Cloud className="h-5 w-5" />}
        onRefresh={() => {}}
        actions={
          <IconButton
            icon={<Plus className="h-3.5 w-3.5" />}
            size="sm"
            tooltip="Add provider"
            onClick={() => {}}
          />
        }
      />

      <div className="flex-1 overflow-auto space-y-3 pr-1">
        {mockProviders.map((provider) => (
          <ProviderCard key={provider.id} provider={provider} />
        ))}
      </div>
    </div>
  )
}
