import React, { useState, useEffect, useCallback, useRef } from 'react'
import { Button, Spinner } from '@persona-framework/ui'
import {
  Send, Plus, Trash2, Save, Clock, ChevronDown, ChevronRight, Copy, Check, X,
  FolderOpen, Code, FileJson, Zap, Download, RefreshCw,
  Globe, Folder, AlertCircle, StopCircle
} from 'lucide-react'
import type {
  HttpMethod, HttpRequest, HttpResponse, KeyValuePair, AuthType,
  BodyType, Collection, SavedRequest,
  DiscoveredEndpoint, Environment, FormDataItem
} from './types'
import { getAllCodeSnippets } from './codeGenerator'
import { discoverEndpoints, groupEndpointsByTag } from './endpointDiscovery'
import { generateFromEndpoints, specToJson } from './openApiGenerator'
import { EnvironmentPanel } from './EnvironmentPanel'
import { FormDataEditor, buildFormData } from './FormDataEditor'
import { JsonEditor, validateJson } from './JsonEditor'
import { useDebouncedCallback, useAbortController, useKeyboardShortcut, loadFromStorage, saveToStorage } from './hooks'

// Constants
const STORAGE_KEYS = {
  history: 'wraith_rest_client_history_v2',
  collections: 'wraith_rest_client_collections_v2',
  environments: 'wraith_rest_client_environments',
}

const DEFAULT_TIMEOUT = 30000 // 30 seconds

const METHOD_COLORS: Record<HttpMethod, string> = {
  GET: 'text-[#00ff7f] bg-[rgba(0,255,127,0.15)]',
  POST: 'text-[#ffd700] bg-[rgba(255,215,0,0.15)]',
  PUT: 'text-[#5bcefa] bg-[rgba(91,206,250,0.15)]',
  PATCH: 'text-[#9d00ff] bg-[rgba(157,0,255,0.15)]',
  DELETE: 'text-[#f48771] bg-[rgba(244,135,113,0.15)]',
  HEAD: 'text-[#808080] bg-[rgba(128,128,128,0.15)]',
  OPTIONS: 'text-[#808080] bg-[rgba(128,128,128,0.15)]',
}

// Helper functions
function createDefaultRequest(): HttpRequest {
  return {
    id: crypto.randomUUID(),
    name: 'New Request',
    method: 'GET',
    url: '',
    queryParams: [],
    pathVariables: [],
    headers: [{ id: crypto.randomUUID(), key: 'Content-Type', value: 'application/json', enabled: true }],
    auth: { type: 'none' },
    body: { type: 'none' },
    timeout: DEFAULT_TIMEOUT,
  }
}

function createKeyValuePair(): KeyValuePair {
  return { id: crypto.randomUUID(), key: '', value: '', enabled: true }
}

function getStatusColor(status: number): string {
  if (status >= 200 && status < 300) return 'text-[#00ff7f]'
  if (status >= 300 && status < 400) return 'text-[#5bcefa]'
  if (status >= 400 && status < 500) return 'text-[#ffd700]'
  return 'text-[#f48771]'
}

function formatBytes(bytes: number): string {
  if (bytes < 1024) return `${bytes} B`
  if (bytes < 1024 * 1024) return `${(bytes / 1024).toFixed(1)} KB`
  return `${(bytes / (1024 * 1024)).toFixed(1)} MB`
}

export function RestClientPanel() {
  // Core state
  const [request, setRequest] = useState<HttpRequest>(createDefaultRequest)
  const [response, setResponse] = useState<HttpResponse | null>(null)
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState<string | null>(null)

  // Collections & History
  const [collections, setCollections] = useState<Collection[]>(() =>
    loadFromStorage(STORAGE_KEYS.collections, [])
  )
  const [history, setHistory] = useState<{ request: HttpRequest; response?: HttpResponse; timestamp: number }[]>(() =>
    loadFromStorage(STORAGE_KEYS.history, [])
  )

  // Environments
  const [environments, setEnvironments] = useState<Environment[]>(() =>
    loadFromStorage(STORAGE_KEYS.environments, [
      { id: 'dev', name: 'Development', variables: [{ id: '1', key: 'BASE_URL', value: 'http://localhost:3000', enabled: true }], isActive: true },
    ])
  )

  // Discovered endpoints
  const [endpoints, setEndpoints] = useState<DiscoveredEndpoint[]>([])
  const [loadingEndpoints, setLoadingEndpoints] = useState(false)

  // UI State
  const [sideTab, setSideTab] = useState<'history' | 'collections' | 'discover'>('collections')
  const [requestTab, setRequestTab] = useState<'params' | 'headers' | 'auth' | 'body'>('params')
  const [responseTab, setResponseTab] = useState<'body' | 'headers' | 'cookies' | 'code'>('body')
  const [expandedFolders, setExpandedFolders] = useState<Set<string>>(new Set())
  const [showSaveDialog, setShowSaveDialog] = useState(false)
  const [showCodeGen, setShowCodeGen] = useState(false)
  const [showOpenApiGen, setShowOpenApiGen] = useState(false)
  const [showEnvPanel, setShowEnvPanel] = useState(false)
  const [copied, setCopied] = useState<string | null>(null)
  const [saveName, setSaveName] = useState('')
  const [selectedSnippet, setSelectedSnippet] = useState(0)

  // Request cancellation
  const { getController, abort: abortRequest } = useAbortController()

  // URL input ref for focus management
  const urlInputRef = useRef<HTMLInputElement>(null)

  // Debounced storage writes
  const debouncedSaveCollections = useDebouncedCallback(
    (data: Collection[]) => saveToStorage(STORAGE_KEYS.collections, data),
    500
  )
  const debouncedSaveHistory = useDebouncedCallback(
    (data: typeof history) => saveToStorage(STORAGE_KEYS.history, data.slice(0, 100)),
    500
  )
  const debouncedSaveEnvironments = useDebouncedCallback(
    (data: Environment[]) => saveToStorage(STORAGE_KEYS.environments, data),
    500
  )

  // Persist data with debounce
  useEffect(() => { debouncedSaveCollections(collections) }, [collections, debouncedSaveCollections])
  useEffect(() => { debouncedSaveHistory(history) }, [history, debouncedSaveHistory])
  useEffect(() => { debouncedSaveEnvironments(environments) }, [environments, debouncedSaveEnvironments])

  // Get active environment
  const activeEnv = environments.find(e => e.isActive)

  // Variable substitution
  const substituteVariables = useCallback((text: string): string => {
    if (!activeEnv) return text
    let result = text
    activeEnv.variables.filter(v => v.enabled).forEach(v => {
      result = result.replace(new RegExp(`{{${v.key}}}`, 'g'), v.value)
      result = result.replace(new RegExp(`\\\${${v.key}}`, 'g'), v.value)
    })
    return result
  }, [activeEnv])

  // Build final URL with query params
  const buildUrl = useCallback((): string => {
    let url = substituteVariables(request.url)

    // Replace path variables
    request.pathVariables.filter(p => p.enabled && p.key).forEach(p => {
      url = url.replace(`:${p.key}`, substituteVariables(p.value))
      url = url.replace(`{${p.key}}`, substituteVariables(p.value))
    })

    // Add query params
    const enabledParams = request.queryParams.filter(p => p.enabled && p.key)
    if (enabledParams.length > 0) {
      const params = new URLSearchParams()
      enabledParams.forEach(p => params.append(p.key, substituteVariables(p.value)))
      url += (url.includes('?') ? '&' : '?') + params.toString()
    }

    return url
  }, [request, substituteVariables])

  // Send request with AbortController and timeout
  const sendRequest = useCallback(async () => {
    const url = buildUrl()
    if (!url.trim()) return

    // Validate JSON body if applicable
    if (request.body.type === 'json' && request.body.json) {
      const { valid, error: jsonError } = validateJson(request.body.json)
      if (!valid) {
        setError(`Invalid JSON body: ${jsonError}`)
        return
      }
    }

    if (request.body.type === 'graphql' && request.body.graphql?.variables) {
      const { valid, error: jsonError } = validateJson(request.body.graphql.variables)
      if (!valid) {
        setError(`Invalid GraphQL variables: ${jsonError}`)
        return
      }
    }

    setLoading(true)
    setError(null)
    setResponse(null)

    const startTime = Date.now()
    const controller = getController()
    const timeout = request.timeout || DEFAULT_TIMEOUT

    // Set up timeout
    const timeoutId = setTimeout(() => {
      controller.abort()
    }, timeout)

    try {
      // Build headers
      const headers: Record<string, string> = {}
      request.headers.filter(h => h.enabled && h.key).forEach(h => {
        headers[substituteVariables(h.key)] = substituteVariables(h.value)
      })

      // Add auth
      if (request.auth.type === 'bearer' && request.auth.bearer?.token) {
        headers['Authorization'] = `Bearer ${substituteVariables(request.auth.bearer.token)}`
      } else if (request.auth.type === 'basic' && request.auth.basic) {
        const encoded = btoa(`${request.auth.basic.username}:${request.auth.basic.password}`)
        headers['Authorization'] = `Basic ${encoded}`
      } else if (request.auth.type === 'api-key' && request.auth.apiKey?.addTo === 'header') {
        headers[request.auth.apiKey.key] = substituteVariables(request.auth.apiKey.value)
      }

      // Build body
      let body: string | FormData | undefined
      if (['POST', 'PUT', 'PATCH'].includes(request.method)) {
        if (request.body.type === 'json' && request.body.json) {
          body = substituteVariables(request.body.json)
          if (!headers['Content-Type']) headers['Content-Type'] = 'application/json'
        } else if (request.body.type === 'raw' && request.body.raw) {
          body = substituteVariables(request.body.raw)
        } else if (request.body.type === 'x-www-form-urlencoded' && request.body.urlencoded) {
          const params = new URLSearchParams()
          request.body.urlencoded.filter(p => p.enabled).forEach(p => {
            params.append(p.key, substituteVariables(p.value))
          })
          body = params.toString()
          if (!headers['Content-Type']) headers['Content-Type'] = 'application/x-www-form-urlencoded'
        } else if (request.body.type === 'form-data' && request.body.formData) {
          body = buildFormData(request.body.formData)
          // Don't set Content-Type for FormData - browser will set it with boundary
          delete headers['Content-Type']
        } else if (request.body.type === 'graphql' && request.body.graphql) {
          body = JSON.stringify({
            query: request.body.graphql.query,
            variables: request.body.graphql.variables ? JSON.parse(request.body.graphql.variables) : undefined,
            operationName: request.body.graphql.operationName || undefined,
          })
          if (!headers['Content-Type']) headers['Content-Type'] = 'application/json'
        }
      }

      const res = await fetch(url, {
        method: request.method,
        headers,
        body,
        signal: controller.signal,
      })

      clearTimeout(timeoutId)
      const duration = Date.now() - startTime

      // Parse response headers
      const responseHeaders: Record<string, string> = {}
      res.headers.forEach((value, key) => { responseHeaders[key] = value })

      // Note: Browser CORS restrictions prevent reading Set-Cookie headers
      // Cookies will only be populated in non-CORS or same-origin requests

      // Parse body
      let responseBody = await res.text()
      const bodySize = new Blob([responseBody]).size
      try {
        const parsed = JSON.parse(responseBody)
        responseBody = JSON.stringify(parsed, null, 2)
      } catch { /* not JSON */ }

      const httpResponse: HttpResponse = {
        status: res.status,
        statusText: res.statusText,
        headers: responseHeaders,
        cookies: [], // CORS limitation - cookies can't be read from headers
        body: responseBody,
        bodySize,
        duration,
        timestamp: Date.now(),
      }

      setResponse(httpResponse)
      setResponseTab('body')

      // Add to history
      setHistory(prev => [{ request: { ...request }, response: httpResponse, timestamp: Date.now() }, ...prev].slice(0, 100))
    } catch (err) {
      clearTimeout(timeoutId)
      if (err instanceof Error) {
        if (err.name === 'AbortError') {
          setError('Request was cancelled or timed out')
        } else {
          setError(err.message)
        }
      } else {
        setError('Request failed')
      }
    } finally {
      setLoading(false)
    }
  }, [buildUrl, request, substituteVariables, getController])

  // Cancel request handler
  const handleCancelRequest = useCallback(() => {
    abortRequest()
    setLoading(false)
    setError('Request cancelled')
  }, [abortRequest])

  // Keyboard shortcut: Ctrl+Enter to send request
  useKeyboardShortcut('Enter', sendRequest, { ctrl: true })

  // Keyboard shortcut: Escape to cancel request
  useKeyboardShortcut('Escape', handleCancelRequest)

  // Discover endpoints
  const handleDiscoverEndpoints = async () => {
    setLoadingEndpoints(true)
    try {
      const discovered = await discoverEndpoints()
      setEndpoints(discovered)
    } finally {
      setLoadingEndpoints(false)
    }
  }

  // Load endpoint into request
  const loadEndpoint = (ep: DiscoveredEndpoint) => {
    const baseUrl = activeEnv?.variables.find(v => v.key === 'BASE_URL')?.value || 'http://localhost:3000'
    setRequest({
      ...createDefaultRequest(),
      method: ep.method,
      url: `${baseUrl}${ep.path}`,
      name: ep.handler || `${ep.method} ${ep.path}`,
      pathVariables: (ep.params || []).map(p => ({ id: crypto.randomUUID(), key: p, value: '', enabled: true })),
      queryParams: (ep.queryParams || []).map(p => ({ id: crypto.randomUUID(), key: p, value: '', enabled: true })),
    })
  }

  // Save to collection
  const saveToCollection = () => {
    if (!saveName.trim()) return

    const saved: SavedRequest = {
      id: crypto.randomUUID(),
      name: saveName.trim(),
      request: { ...request, name: saveName.trim() },
      createdAt: Date.now(),
      updatedAt: Date.now(),
    }

    if (collections.length === 0) {
      setCollections([{
        id: crypto.randomUUID(),
        name: 'My Collection',
        items: [saved],
      }])
    } else {
      setCollections(prev => prev.map((c, i) =>
        i === 0 ? { ...c, items: [saved, ...c.items] } : c
      ))
    }

    setSaveName('')
    setShowSaveDialog(false)
  }

  // Load from collection
  const loadFromCollection = (saved: SavedRequest) => {
    setRequest({ ...saved.request })
    setResponse(null)
  }

  // Delete from collection
  const deleteFromCollection = (collectionId: string, itemId: string) => {
    setCollections(prev => prev.map(c =>
      c.id === collectionId
        ? { ...c, items: c.items.filter(item => 'id' in item && item.id !== itemId) }
        : c
    ))
  }

  // Delete collection
  const deleteCollection = (collectionId: string) => {
    setCollections(prev => prev.filter(c => c.id !== collectionId))
  }

  // Load from history
  const loadFromHistory = (entry: typeof history[0]) => {
    setRequest({ ...entry.request })
    if (entry.response) setResponse(entry.response)
  }

  // Delete from history
  const deleteFromHistory = (index: number) => {
    setHistory(prev => prev.filter((_, i) => i !== index))
  }

  // Clear all history
  const clearHistory = () => {
    setHistory([])
  }

  // Generate OpenAPI spec
  const generateOpenApiSpec = () => {
    const spec = generateFromEndpoints(endpoints, {
      title: 'API Documentation',
      version: '1.0.0',
      description: 'Auto-generated from discovered endpoints',
      serverUrl: activeEnv?.variables.find(v => v.key === 'BASE_URL')?.value,
    })
    return specToJson(spec)
  }

  // Copy to clipboard
  const copyToClipboard = (text: string, id: string) => {
    navigator.clipboard.writeText(text)
    setCopied(id)
    setTimeout(() => setCopied(null), 2000)
  }

  // Get code snippets
  const codeSnippets = getAllCodeSnippets(request)

  // Update key-value pair helper
  const updateKV = (
    list: KeyValuePair[],
    index: number,
    updates: Partial<KeyValuePair>,
    field: 'queryParams' | 'headers' | 'pathVariables'
  ) => {
    setRequest(r => ({
      ...r,
      [field]: r[field].map((item, i) => i === index ? { ...item, ...updates } : item),
    }))
  }

  return (
    <div className="flex h-full bg-[#1e1e1e] text-[#cccccc] overflow-hidden">
      {/* Sidebar */}
      <div className="w-56 border-r border-[#3c3c3c] flex flex-col" role="navigation" aria-label="Request navigation">
        {/* Sidebar Tabs */}
        <div className="flex border-b border-[#3c3c3c]" role="tablist" aria-label="Sidebar tabs">
          {[
            { id: 'collections', icon: FolderOpen, label: 'Collections' },
            { id: 'history', icon: Clock, label: 'History' },
            { id: 'discover', icon: Zap, label: 'Discover' },
          ].map(tab => (
            <button
              key={tab.id}
              onClick={() => setSideTab(tab.id as typeof sideTab)}
              className={`flex-1 py-2 text-xs font-medium transition-colors flex flex-col items-center gap-0.5 ${
                sideTab === tab.id ? 'bg-[#2d2d30] text-white' : 'text-[#808080] hover:text-white'
              }`}
              role="tab"
              aria-selected={sideTab === tab.id}
              aria-controls={`${tab.id}-panel`}
              id={`${tab.id}-tab`}
            >
              <tab.icon className="w-3.5 h-3.5" aria-hidden="true" />
              <span className="text-[9px]">{tab.label}</span>
            </button>
          ))}
        </div>

        {/* Sidebar Content */}
        <div className="flex-1 overflow-auto p-2" role="tabpanel" id={`${sideTab}-panel`} aria-labelledby={`${sideTab}-tab`}>
          {/* Collections Tab */}
          {sideTab === 'collections' && (
            collections.length === 0 ? (
              <div className="text-center text-xs text-[#808080] py-4">
                <FolderOpen className="w-6 h-6 mx-auto mb-2 opacity-50" aria-hidden="true" />
                <p>No saved requests</p>
                <p className="mt-1 text-[10px]">Save a request to create a collection</p>
              </div>
            ) : (
              collections.map(collection => (
                <div key={collection.id} className="mb-2">
                  <div
                    className="flex items-center gap-1.5 p-1.5 rounded cursor-pointer hover:bg-[#2d2d30] group"
                    onClick={() => setExpandedFolders(prev => {
                      const next = new Set(prev)
                      next.has(collection.id) ? next.delete(collection.id) : next.add(collection.id)
                      return next
                    })}
                    role="button"
                    aria-expanded={expandedFolders.has(collection.id)}
                    aria-label={`${collection.name} collection with ${collection.items.length} items`}
                  >
                    {expandedFolders.has(collection.id) ? (
                      <ChevronDown className="w-3.5 h-3.5 text-[#808080]" aria-hidden="true" />
                    ) : (
                      <ChevronRight className="w-3.5 h-3.5 text-[#808080]" aria-hidden="true" />
                    )}
                    <Folder className="w-3.5 h-3.5 text-[#ffd700]" aria-hidden="true" />
                    <span className="text-xs text-white flex-1 truncate">{collection.name}</span>
                    <span className="text-[10px] text-[#606060]">{collection.items.length}</span>
                    <button
                      onClick={(e) => {
                        e.stopPropagation()
                        deleteCollection(collection.id)
                      }}
                      className="p-0.5 hover:bg-[#5a1d1d] rounded opacity-0 group-hover:opacity-100"
                      aria-label={`Delete ${collection.name} collection`}
                    >
                      <Trash2 className="w-3 h-3 text-[#f48771]" />
                    </button>
                  </div>
                  {expandedFolders.has(collection.id) && (
                    <div className="ml-4">
                      {collection.items.map(item => {
                        if ('request' in item) {
                          return (
                            <div
                              key={item.id}
                              className="flex items-center gap-1.5 p-1.5 rounded cursor-pointer hover:bg-[#2d2d30] group"
                            >
                              <div
                                onClick={() => loadFromCollection(item)}
                                className="flex items-center gap-1.5 flex-1 min-w-0"
                                role="button"
                                aria-label={`Load ${item.name} request`}
                              >
                                <span className={`text-[9px] font-bold px-1 rounded ${METHOD_COLORS[item.request.method]}`}>
                                  {item.request.method.slice(0, 3)}
                                </span>
                                <span className="text-xs text-[#b0b0b0] truncate">{item.name}</span>
                              </div>
                              <button
                                onClick={(e) => {
                                  e.stopPropagation()
                                  deleteFromCollection(collection.id, item.id)
                                }}
                                className="p-0.5 hover:bg-[#5a1d1d] rounded opacity-0 group-hover:opacity-100"
                                aria-label={`Delete ${item.name} request`}
                              >
                                <Trash2 className="w-3 h-3 text-[#f48771]" />
                              </button>
                            </div>
                          )
                        }
                        return null
                      })}
                    </div>
                  )}
                </div>
              ))
            )
          )}

          {/* History Tab */}
          {sideTab === 'history' && (
            history.length === 0 ? (
              <div className="text-center text-xs text-[#808080] py-4">
                <Clock className="w-6 h-6 mx-auto mb-2 opacity-50" aria-hidden="true" />
                <p>No history yet</p>
                <p className="mt-1 text-[10px]">Send a request to see it here</p>
              </div>
            ) : (
              <>
                <button
                  onClick={clearHistory}
                  className="w-full text-xs text-[#f48771] hover:bg-[#5a1d1d] rounded p-1.5 mb-2 flex items-center justify-center gap-1"
                  aria-label="Clear all history"
                >
                  <Trash2 className="w-3 h-3" aria-hidden="true" />
                  Clear All
                </button>
                {history.slice(0, 50).map((entry, i) => (
                  <div
                    key={i}
                    className="p-2 rounded cursor-pointer hover:bg-[#2d2d30] mb-1 group"
                  >
                    <div className="flex items-center gap-1.5">
                      <div
                        onClick={() => loadFromHistory(entry)}
                        className="flex-1 min-w-0"
                        role="button"
                        aria-label={`Load ${entry.request.method} ${entry.request.url} from history`}
                      >
                        <div className="flex items-center gap-1.5">
                          <span className={`text-[9px] font-bold px-1 rounded ${METHOD_COLORS[entry.request.method]}`}>
                            {entry.request.method.slice(0, 3)}
                          </span>
                          {entry.response && (
                            <span className={`text-[10px] ${getStatusColor(entry.response.status)}`}>
                              {entry.response.status}
                            </span>
                          )}
                        </div>
                        <div className="text-[10px] text-[#808080] truncate mt-0.5">
                          {entry.request.url.replace(/^https?:\/\//, '')}
                        </div>
                      </div>
                      <button
                        onClick={(e) => {
                          e.stopPropagation()
                          deleteFromHistory(i)
                        }}
                        className="p-0.5 hover:bg-[#5a1d1d] rounded opacity-0 group-hover:opacity-100"
                        aria-label="Delete this history entry"
                      >
                        <Trash2 className="w-3 h-3 text-[#f48771]" />
                      </button>
                    </div>
                  </div>
                ))}
              </>
            )
          )}

          {/* Discover Tab */}
          {sideTab === 'discover' && (
            <div>
              <Button
                size="sm"
                variant="outline"
                onClick={handleDiscoverEndpoints}
                disabled={loadingEndpoints}
                className="w-full mb-3"
                aria-label="Scan codebase for API endpoints"
              >
                {loadingEndpoints ? <Spinner size="sm" /> : <RefreshCw className="w-3.5 h-3.5 mr-1.5" aria-hidden="true" />}
                Scan Codebase
              </Button>

              {endpoints.length > 0 && (
                <div className="mb-3">
                  <Button
                    size="sm"
                    variant="ghost"
                    onClick={() => setShowOpenApiGen(true)}
                    className="w-full text-xs"
                    aria-label="Generate OpenAPI specification"
                  >
                    <FileJson className="w-3.5 h-3.5 mr-1.5" aria-hidden="true" />
                    Generate OpenAPI
                  </Button>
                </div>
              )}

              {endpoints.length === 0 ? (
                <div className="text-center text-xs text-[#808080] py-4">
                  <Zap className="w-6 h-6 mx-auto mb-2 opacity-50" aria-hidden="true" />
                  <p>Click scan to discover endpoints</p>
                </div>
              ) : (
                Object.entries(groupEndpointsByTag(endpoints)).map(([tag, eps]) => (
                  <div key={tag} className="mb-2">
                    <div className="text-[10px] text-[#808080] uppercase px-1.5 mb-1">{tag}</div>
                    {eps.map((ep, i) => (
                      <div
                        key={i}
                        onClick={() => loadEndpoint(ep)}
                        className="flex items-center gap-1.5 p-1.5 rounded cursor-pointer hover:bg-[#2d2d30]"
                        role="button"
                        aria-label={`Load ${ep.method} ${ep.path} endpoint`}
                      >
                        <span className={`text-[9px] font-bold px-1 rounded ${METHOD_COLORS[ep.method]}`}>
                          {ep.method.slice(0, 3)}
                        </span>
                        <span className="text-xs text-[#b0b0b0] truncate">{ep.path}</span>
                      </div>
                    ))}
                  </div>
                ))
              )}
            </div>
          )}
        </div>

        {/* Environment Selector */}
        <div className="p-2 border-t border-[#3c3c3c]">
          <button
            onClick={() => setShowEnvPanel(true)}
            className="flex items-center gap-2 w-full p-2 rounded hover:bg-[#2d2d30] text-xs"
            aria-label="Manage environments"
            aria-haspopup="dialog"
          >
            <Globe className="w-3.5 h-3.5 text-[#5bcefa]" aria-hidden="true" />
            <span className="flex-1 text-left truncate">
              {activeEnv?.name || 'No Environment'}
            </span>
            <ChevronDown className="w-3.5 h-3.5 text-[#808080]" aria-hidden="true" />
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col overflow-hidden">
        {/* URL Bar */}
        <div className="p-3 border-b border-[#3c3c3c] bg-[#252526]">
          <div className="flex gap-2">
            <label htmlFor="method-select" className="sr-only">HTTP Method</label>
            <select
              id="method-select"
              value={request.method}
              onChange={(e) => setRequest(r => ({ ...r, method: e.target.value as HttpMethod }))}
              className={`w-28 px-2 py-1.5 text-sm border border-[#3c3c3c] rounded font-bold focus:outline-none focus:border-[#5bcefa] ${METHOD_COLORS[request.method]}`}
              aria-label="HTTP method"
            >
              {(['GET', 'POST', 'PUT', 'PATCH', 'DELETE', 'HEAD', 'OPTIONS'] as HttpMethod[]).map(m => (
                <option key={m} value={m}>{m}</option>
              ))}
            </select>
            <label htmlFor="url-input" className="sr-only">Request URL</label>
            <input
              ref={urlInputRef}
              id="url-input"
              type="text"
              placeholder="Enter URL (Ctrl+Enter to send)"
              value={request.url}
              onChange={(e) => setRequest(r => ({ ...r, url: e.target.value }))}
              className="flex-1 px-3 py-1.5 text-sm bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white font-mono placeholder-[#808080] focus:outline-none focus:border-[#5bcefa]"
              aria-label="Request URL"
            />
            <Button
              size="sm"
              variant="ghost"
              onClick={() => setShowSaveDialog(true)}
              disabled={!request.url.trim()}
              aria-label="Save request to collection"
            >
              <Save className="w-4 h-4" aria-hidden="true" />
            </Button>
            <Button
              size="sm"
              variant="ghost"
              onClick={() => setShowCodeGen(!showCodeGen)}
              aria-label={showCodeGen ? 'Hide code generation' : 'Show code generation'}
              aria-pressed={showCodeGen}
            >
              <Code className="w-4 h-4" aria-hidden="true" />
            </Button>
            {loading ? (
              <Button
                size="sm"
                variant="ghost"
                onClick={handleCancelRequest}
                className="px-4 text-[#f48771]"
                aria-label="Cancel request"
              >
                <StopCircle className="w-4 h-4 mr-1.5" aria-hidden="true" />
                Cancel
              </Button>
            ) : (
              <Button
                size="sm"
                onClick={sendRequest}
                disabled={!request.url.trim()}
                className="px-4"
                aria-label="Send request (Ctrl+Enter)"
              >
                <Send className="w-4 h-4 mr-1.5" aria-hidden="true" />
                Send
              </Button>
            )}
          </div>
          <div className="flex items-center gap-3 mt-2 text-[10px] text-[#808080]">
            <span>Timeout: {(request.timeout || DEFAULT_TIMEOUT) / 1000}s</span>
            <span>â€¢</span>
            <span>Press Ctrl+Enter to send, Escape to cancel</span>
          </div>
        </div>

        {/* Save Dialog */}
        {showSaveDialog && (
          <div className="p-3 border-b border-[#3c3c3c] bg-[#2d2d30]">
            <div className="flex gap-2">
              <label htmlFor="save-name" className="sr-only">Request name</label>
              <input
                id="save-name"
                type="text"
                placeholder="Request name..."
                value={saveName}
                onChange={(e) => setSaveName(e.target.value)}
                onKeyDown={(e) => {
                  if (e.key === 'Enter') saveToCollection()
                  if (e.key === 'Escape') setShowSaveDialog(false)
                }}
                className="flex-1 px-3 py-1.5 text-sm bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white placeholder-[#808080] focus:outline-none focus:border-[#5bcefa]"
                autoFocus
              />
              <Button size="sm" onClick={saveToCollection} disabled={!saveName.trim()}>Save</Button>
              <Button size="sm" variant="ghost" onClick={() => setShowSaveDialog(false)} aria-label="Cancel save">
                <X className="w-4 h-4" aria-hidden="true" />
              </Button>
            </div>
          </div>
        )}

        {/* Code Generation Panel */}
        {showCodeGen && (
          <div className="p-3 border-b border-[#3c3c3c] bg-[#2d2d30]">
            <div className="flex items-center gap-2 mb-2">
              <span className="text-xs text-[#808080]">Generate Code:</span>
              <div className="flex gap-1" role="tablist" aria-label="Code snippet languages">
                {codeSnippets.map((snippet, i) => (
                  <button
                    key={i}
                    onClick={() => setSelectedSnippet(i)}
                    className={`px-2 py-1 text-[10px] rounded ${
                      selectedSnippet === i ? 'bg-[#5bcefa] text-[#1e1e1e]' : 'bg-[#3c3c3c] text-[#b0b0b0]'
                    }`}
                    role="tab"
                    aria-selected={selectedSnippet === i}
                    aria-controls="code-snippet-panel"
                  >
                    {snippet.label}
                  </button>
                ))}
              </div>
              <div className="flex-1" />
              <button
                onClick={() => copyToClipboard(codeSnippets[selectedSnippet].code, 'code')}
                className="flex items-center gap-1 px-2 py-1 text-xs text-[#5bcefa] hover:bg-[#3c3c3c] rounded"
                aria-label={copied === 'code' ? 'Copied!' : 'Copy code to clipboard'}
              >
                {copied === 'code' ? <Check className="w-3 h-3" aria-hidden="true" /> : <Copy className="w-3 h-3" aria-hidden="true" />}
                {copied === 'code' ? 'Copied!' : 'Copy'}
              </button>
              <Button size="sm" variant="ghost" onClick={() => setShowCodeGen(false)} aria-label="Close code generation panel">
                <X className="w-4 h-4" aria-hidden="true" />
              </Button>
            </div>
            <pre
              id="code-snippet-panel"
              role="tabpanel"
              className="p-2 bg-[#1e1e1e] rounded text-xs font-mono text-[#b0b0b0] max-h-32 overflow-auto"
            >
              {codeSnippets[selectedSnippet].code}
            </pre>
          </div>
        )}

        {/* Request Tabs */}
        <div className="flex border-b border-[#3c3c3c]" role="tablist" aria-label="Request configuration tabs">
          {[
            { id: 'params', label: 'Params', count: request.queryParams.filter(p => p.enabled).length + request.pathVariables.filter(p => p.enabled).length },
            { id: 'headers', label: 'Headers', count: request.headers.filter(h => h.enabled).length },
            { id: 'auth', label: 'Auth', active: request.auth.type !== 'none' },
            { id: 'body', label: 'Body', active: request.body.type !== 'none' },
          ].map(tab => (
            <button
              key={tab.id}
              onClick={() => setRequestTab(tab.id as typeof requestTab)}
              className={`px-4 py-2 text-xs font-medium transition-colors ${
                requestTab === tab.id ? 'bg-[#2d2d30] text-white border-b-2 border-[#5bcefa]' : 'text-[#808080] hover:text-white'
              }`}
              role="tab"
              aria-selected={requestTab === tab.id}
              aria-controls={`request-${tab.id}-panel`}
              id={`request-${tab.id}-tab`}
            >
              {tab.label}
              {'count' in tab && tab.count > 0 && (
                <span className="ml-1.5 px-1 rounded bg-[#5bcefa] text-[#1e1e1e] text-[9px]">{tab.count}</span>
              )}
              {'active' in tab && tab.active && (
                <span className="ml-1.5 w-1.5 h-1.5 rounded-full bg-[#00ff7f] inline-block" aria-label="Configured" />
              )}
            </button>
          ))}
        </div>

        {/* Request Content */}
        <div className="flex-1 flex flex-col overflow-hidden">
          <div
            className="flex-1 overflow-auto p-3"
            style={{ maxHeight: '40%' }}
            role="tabpanel"
            id={`request-${requestTab}-panel`}
            aria-labelledby={`request-${requestTab}-tab`}
          >
            {/* Params Tab */}
            {requestTab === 'params' && (
              <div className="space-y-4">
                <div>
                  <div className="text-xs text-[#808080] mb-2">Query Parameters</div>
                  {request.queryParams.map((param, i) => (
                    <div key={param.id} className="flex items-center gap-2 mb-1">
                      <input
                        type="checkbox"
                        checked={param.enabled}
                        onChange={(e) => updateKV(request.queryParams, i, { enabled: e.target.checked }, 'queryParams')}
                        className="w-4 h-4"
                        aria-label={`Enable parameter ${param.key || 'unnamed'}`}
                      />
                      <label className="sr-only">Parameter key</label>
                      <input
                        placeholder="Key"
                        value={param.key}
                        onChange={(e) => updateKV(request.queryParams, i, { key: e.target.value }, 'queryParams')}
                        className="w-32 px-2 py-1 text-xs bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white font-mono focus:border-[#5bcefa] focus:outline-none"
                        aria-label="Parameter key"
                      />
                      <label className="sr-only">Parameter value</label>
                      <input
                        placeholder="Value"
                        value={param.value}
                        onChange={(e) => updateKV(request.queryParams, i, { value: e.target.value }, 'queryParams')}
                        className="flex-1 px-2 py-1 text-xs bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white font-mono focus:border-[#5bcefa] focus:outline-none"
                        aria-label="Parameter value"
                      />
                      <button
                        onClick={() => setRequest(r => ({ ...r, queryParams: r.queryParams.filter((_, idx) => idx !== i) }))}
                        className="p-1 hover:bg-[#5a1d1d] rounded"
                        aria-label={`Delete parameter ${param.key || 'unnamed'}`}
                      >
                        <Trash2 className="w-3.5 h-3.5 text-[#f48771]" aria-hidden="true" />
                      </button>
                    </div>
                  ))}
                  <button
                    onClick={() => setRequest(r => ({ ...r, queryParams: [...r.queryParams, createKeyValuePair()] }))}
                    className="text-xs text-[#5bcefa] hover:text-[#7dd3fc]"
                  >
                    <Plus className="w-3.5 h-3.5 inline mr-1" aria-hidden="true" />
                    Add Parameter
                  </button>
                </div>

                <div>
                  <div className="text-xs text-[#808080] mb-2">Path Variables</div>
                  {request.pathVariables.map((param, i) => (
                    <div key={param.id} className="flex items-center gap-2 mb-1">
                      <input
                        type="checkbox"
                        checked={param.enabled}
                        onChange={(e) => updateKV(request.pathVariables, i, { enabled: e.target.checked }, 'pathVariables')}
                        className="w-4 h-4"
                        aria-label={`Enable path variable ${param.key || 'unnamed'}`}
                      />
                      <input
                        placeholder="Key"
                        value={param.key}
                        onChange={(e) => updateKV(request.pathVariables, i, { key: e.target.value }, 'pathVariables')}
                        className="w-32 px-2 py-1 text-xs bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white font-mono focus:border-[#5bcefa] focus:outline-none"
                        aria-label="Path variable key"
                      />
                      <input
                        placeholder="Value"
                        value={param.value}
                        onChange={(e) => updateKV(request.pathVariables, i, { value: e.target.value }, 'pathVariables')}
                        className="flex-1 px-2 py-1 text-xs bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white font-mono focus:border-[#5bcefa] focus:outline-none"
                        aria-label="Path variable value"
                      />
                      <button
                        onClick={() => setRequest(r => ({ ...r, pathVariables: r.pathVariables.filter((_, idx) => idx !== i) }))}
                        className="p-1 hover:bg-[#5a1d1d] rounded"
                        aria-label={`Delete path variable ${param.key || 'unnamed'}`}
                      >
                        <Trash2 className="w-3.5 h-3.5 text-[#f48771]" aria-hidden="true" />
                      </button>
                    </div>
                  ))}
                  <button
                    onClick={() => setRequest(r => ({ ...r, pathVariables: [...r.pathVariables, createKeyValuePair()] }))}
                    className="text-xs text-[#5bcefa] hover:text-[#7dd3fc]"
                  >
                    <Plus className="w-3.5 h-3.5 inline mr-1" aria-hidden="true" />
                    Add Path Variable
                  </button>
                </div>
              </div>
            )}

            {/* Headers Tab */}
            {requestTab === 'headers' && (
              <div>
                {request.headers.map((header, i) => (
                  <div key={header.id} className="flex items-center gap-2 mb-1">
                    <input
                      type="checkbox"
                      checked={header.enabled}
                      onChange={(e) => updateKV(request.headers, i, { enabled: e.target.checked }, 'headers')}
                      className="w-4 h-4"
                      aria-label={`Enable header ${header.key || 'unnamed'}`}
                    />
                    <input
                      placeholder="Header"
                      value={header.key}
                      onChange={(e) => updateKV(request.headers, i, { key: e.target.value }, 'headers')}
                      className="w-40 px-2 py-1 text-xs bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white font-mono focus:border-[#5bcefa] focus:outline-none"
                      aria-label="Header name"
                    />
                    <input
                      placeholder="Value"
                      value={header.value}
                      onChange={(e) => updateKV(request.headers, i, { value: e.target.value }, 'headers')}
                      className="flex-1 px-2 py-1 text-xs bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white font-mono focus:border-[#5bcefa] focus:outline-none"
                      aria-label="Header value"
                    />
                    <button
                      onClick={() => setRequest(r => ({ ...r, headers: r.headers.filter((_, idx) => idx !== i) }))}
                      className="p-1 hover:bg-[#5a1d1d] rounded"
                      aria-label={`Delete header ${header.key || 'unnamed'}`}
                    >
                      <Trash2 className="w-3.5 h-3.5 text-[#f48771]" aria-hidden="true" />
                    </button>
                  </div>
                ))}
                <button
                  onClick={() => setRequest(r => ({ ...r, headers: [...r.headers, createKeyValuePair()] }))}
                  className="text-xs text-[#5bcefa] hover:text-[#7dd3fc]"
                >
                  <Plus className="w-3.5 h-3.5 inline mr-1" aria-hidden="true" />
                  Add Header
                </button>
              </div>
            )}

            {/* Auth Tab */}
            {requestTab === 'auth' && (
              <div>
                <div className="flex gap-2 mb-4" role="radiogroup" aria-label="Authentication type">
                  {(['none', 'bearer', 'basic', 'api-key'] as AuthType[]).map(type => (
                    <button
                      key={type}
                      onClick={() => setRequest(r => ({ ...r, auth: { ...r.auth, type } }))}
                      className={`px-3 py-1.5 text-xs rounded capitalize ${
                        request.auth.type === type ? 'bg-[#5bcefa] text-[#1e1e1e]' : 'bg-[#3c3c3c] text-[#b0b0b0]'
                      }`}
                      role="radio"
                      aria-checked={request.auth.type === type}
                    >
                      {type === 'api-key' ? 'API Key' : type}
                    </button>
                  ))}
                </div>

                {request.auth.type === 'bearer' && (
                  <div>
                    <label htmlFor="bearer-token" className="text-xs text-[#808080] block mb-1">Token</label>
                    <input
                      id="bearer-token"
                      type="text"
                      value={request.auth.bearer?.token || ''}
                      onChange={(e) => setRequest(r => ({ ...r, auth: { ...r.auth, bearer: { token: e.target.value } } }))}
                      placeholder="Enter bearer token..."
                      className="w-full px-3 py-1.5 text-sm bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white font-mono focus:border-[#5bcefa] focus:outline-none"
                    />
                  </div>
                )}

                {request.auth.type === 'basic' && (
                  <div className="space-y-2">
                    <div>
                      <label htmlFor="basic-username" className="text-xs text-[#808080] block mb-1">Username</label>
                      <input
                        id="basic-username"
                        type="text"
                        value={request.auth.basic?.username || ''}
                        onChange={(e) => setRequest(r => ({ ...r, auth: { ...r.auth, basic: { ...r.auth.basic, username: e.target.value, password: r.auth.basic?.password || '' } } }))}
                        className="w-full px-3 py-1.5 text-sm bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white focus:border-[#5bcefa] focus:outline-none"
                      />
                    </div>
                    <div>
                      <label htmlFor="basic-password" className="text-xs text-[#808080] block mb-1">Password</label>
                      <input
                        id="basic-password"
                        type="password"
                        value={request.auth.basic?.password || ''}
                        onChange={(e) => setRequest(r => ({ ...r, auth: { ...r.auth, basic: { ...r.auth.basic, password: e.target.value, username: r.auth.basic?.username || '' } } }))}
                        className="w-full px-3 py-1.5 text-sm bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white focus:border-[#5bcefa] focus:outline-none"
                      />
                    </div>
                  </div>
                )}

                {request.auth.type === 'api-key' && (
                  <div className="space-y-2">
                    <div className="flex gap-2">
                      <div className="flex-1">
                        <label htmlFor="apikey-key" className="text-xs text-[#808080] block mb-1">Key</label>
                        <input
                          id="apikey-key"
                          type="text"
                          value={request.auth.apiKey?.key || ''}
                          onChange={(e) => setRequest(r => ({ ...r, auth: { ...r.auth, apiKey: { ...r.auth.apiKey, key: e.target.value, value: r.auth.apiKey?.value || '', addTo: r.auth.apiKey?.addTo || 'header' } } }))}
                          placeholder="X-API-Key"
                          className="w-full px-3 py-1.5 text-sm bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white focus:border-[#5bcefa] focus:outline-none"
                        />
                      </div>
                      <div className="flex-1">
                        <label htmlFor="apikey-value" className="text-xs text-[#808080] block mb-1">Value</label>
                        <input
                          id="apikey-value"
                          type="text"
                          value={request.auth.apiKey?.value || ''}
                          onChange={(e) => setRequest(r => ({ ...r, auth: { ...r.auth, apiKey: { ...r.auth.apiKey, value: e.target.value, key: r.auth.apiKey?.key || '', addTo: r.auth.apiKey?.addTo || 'header' } } }))}
                          className="w-full px-3 py-1.5 text-sm bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white focus:border-[#5bcefa] focus:outline-none"
                        />
                      </div>
                    </div>
                    <div>
                      <label htmlFor="apikey-addto" className="text-xs text-[#808080] block mb-1">Add to</label>
                      <select
                        id="apikey-addto"
                        value={request.auth.apiKey?.addTo || 'header'}
                        onChange={(e) => setRequest(r => ({ ...r, auth: { ...r.auth, apiKey: { ...r.auth.apiKey, addTo: e.target.value as 'header' | 'query', key: r.auth.apiKey?.key || '', value: r.auth.apiKey?.value || '' } } }))}
                        className="w-full px-3 py-1.5 text-sm bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white focus:border-[#5bcefa] focus:outline-none"
                      >
                        <option value="header">Header</option>
                        <option value="query">Query Params</option>
                      </select>
                    </div>
                  </div>
                )}
              </div>
            )}

            {/* Body Tab */}
            {requestTab === 'body' && (
              <div>
                <div className="flex gap-2 mb-4 flex-wrap" role="radiogroup" aria-label="Request body type">
                  {(['none', 'json', 'form-data', 'x-www-form-urlencoded', 'raw', 'graphql'] as BodyType[]).map(type => (
                    <button
                      key={type}
                      onClick={() => setRequest(r => ({ ...r, body: { ...r.body, type } }))}
                      className={`px-3 py-1.5 text-xs rounded ${
                        request.body.type === type ? 'bg-[#5bcefa] text-[#1e1e1e]' : 'bg-[#3c3c3c] text-[#b0b0b0]'
                      }`}
                      role="radio"
                      aria-checked={request.body.type === type}
                    >
                      {type === 'x-www-form-urlencoded' ? 'URL Encoded' : type}
                    </button>
                  ))}
                </div>

                {request.body.type === 'json' && (
                  <JsonEditor
                    value={request.body.json || ''}
                    onChange={(value) => setRequest(r => ({ ...r, body: { ...r.body, json: value } }))}
                    placeholder='{"key": "value"}'
                    label="JSON Body"
                    minHeight="8rem"
                  />
                )}

                {request.body.type === 'raw' && (
                  <div>
                    <label htmlFor="raw-body" className="text-xs text-[#808080] block mb-1">Raw Body</label>
                    <textarea
                      id="raw-body"
                      value={request.body.raw || ''}
                      onChange={(e) => setRequest(r => ({ ...r, body: { ...r.body, raw: e.target.value } }))}
                      placeholder="Raw body content..."
                      className="w-full h-32 p-3 text-sm bg-[#2d2d30] border border-[#3c3c3c] rounded text-white font-mono resize-y focus:border-[#5bcefa] focus:outline-none"
                    />
                  </div>
                )}

                {request.body.type === 'graphql' && (
                  <div className="space-y-3">
                    <div>
                      <label htmlFor="graphql-query" className="text-xs text-[#808080] block mb-1">Query</label>
                      <textarea
                        id="graphql-query"
                        value={request.body.graphql?.query || ''}
                        onChange={(e) => setRequest(r => ({ ...r, body: { ...r.body, graphql: { ...r.body.graphql, query: e.target.value, variables: r.body.graphql?.variables || '' } } }))}
                        placeholder="query { ... }"
                        className="w-full h-24 p-3 text-sm bg-[#2d2d30] border border-[#3c3c3c] rounded text-white font-mono resize-y focus:border-[#5bcefa] focus:outline-none"
                      />
                    </div>
                    <JsonEditor
                      value={request.body.graphql?.variables || ''}
                      onChange={(value) => setRequest(r => ({ ...r, body: { ...r.body, graphql: { ...r.body.graphql, variables: value, query: r.body.graphql?.query || '' } } }))}
                      placeholder='{"variable": "value"}'
                      label="Variables (JSON)"
                      minHeight="4rem"
                    />
                  </div>
                )}

                {request.body.type === 'form-data' && (
                  <FormDataEditor
                    items={request.body.formData || []}
                    onChange={(items) => setRequest(r => ({ ...r, body: { ...r.body, formData: items } }))}
                  />
                )}

                {request.body.type === 'x-www-form-urlencoded' && (
                  <div>
                    <div className="text-xs text-[#808080] mb-2">URL Encoded Form Data</div>
                    {(request.body.urlencoded || []).map((param, i) => (
                      <div key={param.id} className="flex items-center gap-2 mb-1">
                        <input
                          type="checkbox"
                          checked={param.enabled}
                          onChange={(e) => setRequest(r => ({ ...r, body: { ...r.body, urlencoded: r.body.urlencoded?.map((p, idx) => idx === i ? { ...p, enabled: e.target.checked } : p) } }))}
                          className="w-4 h-4"
                          aria-label={`Enable field ${param.key || 'unnamed'}`}
                        />
                        <input
                          placeholder="Key"
                          value={param.key}
                          onChange={(e) => setRequest(r => ({ ...r, body: { ...r.body, urlencoded: r.body.urlencoded?.map((p, idx) => idx === i ? { ...p, key: e.target.value } : p) } }))}
                          className="w-32 px-2 py-1 text-xs bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white font-mono focus:border-[#5bcefa] focus:outline-none"
                          aria-label="Field key"
                        />
                        <input
                          placeholder="Value"
                          value={param.value}
                          onChange={(e) => setRequest(r => ({ ...r, body: { ...r.body, urlencoded: r.body.urlencoded?.map((p, idx) => idx === i ? { ...p, value: e.target.value } : p) } }))}
                          className="flex-1 px-2 py-1 text-xs bg-[#3c3c3c] border border-[#3c3c3c] rounded text-white font-mono focus:border-[#5bcefa] focus:outline-none"
                          aria-label="Field value"
                        />
                        <button
                          onClick={() => setRequest(r => ({ ...r, body: { ...r.body, urlencoded: r.body.urlencoded?.filter((_, idx) => idx !== i) } }))}
                          className="p-1 hover:bg-[#5a1d1d] rounded"
                          aria-label={`Delete field ${param.key || 'unnamed'}`}
                        >
                          <Trash2 className="w-3.5 h-3.5 text-[#f48771]" aria-hidden="true" />
                        </button>
                      </div>
                    ))}
                    <button
                      onClick={() => setRequest(r => ({ ...r, body: { ...r.body, urlencoded: [...(r.body.urlencoded || []), createKeyValuePair()] } }))}
                      className="text-xs text-[#5bcefa] hover:text-[#7dd3fc]"
                    >
                      <Plus className="w-3.5 h-3.5 inline mr-1" aria-hidden="true" />
                      Add Field
                    </button>
                  </div>
                )}
              </div>
            )}
          </div>

          {/* Response Section */}
          <div className="border-t border-[#3c3c3c] flex-1 flex flex-col overflow-hidden">
            {/* Response Tabs */}
            <div className="flex border-b border-[#3c3c3c]" role="tablist" aria-label="Response tabs">
              {[
                { id: 'body', label: 'Body' },
                { id: 'headers', label: 'Headers', count: response ? Object.keys(response.headers).length : 0 },
                { id: 'cookies', label: 'Cookies', count: response?.cookies.length || 0 },
                { id: 'code', label: 'Code' },
              ].map(tab => (
                <button
                  key={tab.id}
                  onClick={() => setResponseTab(tab.id as typeof responseTab)}
                  className={`px-4 py-2 text-xs font-medium transition-colors ${
                    responseTab === tab.id ? 'bg-[#2d2d30] text-white border-b-2 border-[#5bcefa]' : 'text-[#808080] hover:text-white'
                  }`}
                  role="tab"
                  aria-selected={responseTab === tab.id}
                  aria-controls={`response-${tab.id}-panel`}
                  id={`response-${tab.id}-tab`}
                >
                  {tab.label}
                  {'count' in tab && tab.count > 0 && (
                    <span className="ml-1.5 text-[10px] text-[#808080]">({tab.count})</span>
                  )}
                </button>
              ))}
              {response && (
                <div className="flex items-center gap-4 ml-auto px-3" aria-live="polite">
                  <span className={`text-sm font-bold ${getStatusColor(response.status)}`}>
                    {response.status} {response.statusText}
                  </span>
                  <span className="text-xs text-[#808080]">{response.duration}ms</span>
                  <span className="text-xs text-[#808080]">{formatBytes(response.bodySize)}</span>
                </div>
              )}
            </div>

            {/* Response Content */}
            <div
              className="flex-1 overflow-auto p-3"
              role="tabpanel"
              id={`response-${responseTab}-panel`}
              aria-labelledby={`response-${responseTab}-tab`}
            >
              {loading && (
                <div className="flex flex-col items-center justify-center h-full gap-3">
                  <Spinner size="lg" />
                  <div className="text-sm text-[#b0b0b0]">Sending request...</div>
                  <button
                    onClick={handleCancelRequest}
                    className="text-xs text-[#f48771] hover:text-[#ff9984] flex items-center gap-1"
                  >
                    <StopCircle className="w-3.5 h-3.5" aria-hidden="true" />
                    Cancel (Escape)
                  </button>
                </div>
              )}

              {error && !loading && (
                <div
                  className="p-4 bg-[rgba(244,135,113,0.1)] border border-[#f48771] rounded"
                  role="alert"
                >
                  <div className="flex items-center gap-2 text-sm text-[#f48771] font-medium">
                    <AlertCircle className="w-4 h-4" aria-hidden="true" />
                    Request Failed
                  </div>
                  <div className="text-xs text-[#b0b0b0] mt-1">{error}</div>
                </div>
              )}

              {!loading && !error && response && (
                <>
                  {responseTab === 'body' && (
                    <div className="h-full flex flex-col">
                      <div className="flex justify-end mb-2">
                        <button
                          onClick={() => copyToClipboard(response.body, 'response')}
                          className="flex items-center gap-1.5 px-2 py-1 text-xs text-[#5bcefa] hover:bg-[#2d2d30] rounded"
                          aria-label={copied === 'response' ? 'Copied!' : 'Copy response body'}
                        >
                          {copied === 'response' ? <Check className="w-3.5 h-3.5" aria-hidden="true" /> : <Copy className="w-3.5 h-3.5" aria-hidden="true" />}
                          {copied === 'response' ? 'Copied!' : 'Copy'}
                        </button>
                      </div>
                      <pre className="flex-1 p-3 bg-[#2d2d30] rounded overflow-auto text-xs font-mono text-[#cccccc]">
                        {response.body || '(empty response)'}
                      </pre>
                    </div>
                  )}

                  {responseTab === 'headers' && (
                    <div className="space-y-1">
                      {Object.entries(response.headers).map(([key, value]) => (
                        <div key={key} className="flex gap-2 text-xs">
                          <span className="text-[#5bcefa] font-mono">{key}:</span>
                          <span className="text-[#b0b0b0] font-mono break-all">{value}</span>
                        </div>
                      ))}
                    </div>
                  )}

                  {responseTab === 'cookies' && (
                    <div>
                      <div className="text-xs text-[#808080] bg-[#2d2d30] p-2 rounded mb-3">
                        <AlertCircle className="w-3.5 h-3.5 inline mr-1" aria-hidden="true" />
                        Note: Due to browser CORS restrictions, cookies from cross-origin requests cannot be read from JavaScript.
                        Cookies are still sent/received by the browser automatically.
                      </div>
                      {response.cookies.length > 0 ? (
                        <div className="space-y-2">
                          {response.cookies.map((cookie, i) => (
                            <div key={i} className="p-2 bg-[#2d2d30] rounded">
                              <div className="text-xs font-mono text-white">{cookie.name} = {cookie.value}</div>
                            </div>
                          ))}
                        </div>
                      ) : (
                        <div className="text-center text-xs text-[#808080] py-4">
                          No cookies available
                        </div>
                      )}
                    </div>
                  )}

                  {responseTab === 'code' && (
                    <div>
                      <div className="flex gap-2 mb-3 flex-wrap" role="tablist" aria-label="Code snippet language">
                        {codeSnippets.map((snippet, i) => (
                          <button
                            key={i}
                            onClick={() => setSelectedSnippet(i)}
                            className={`px-2 py-1 text-[10px] rounded ${
                              selectedSnippet === i ? 'bg-[#5bcefa] text-[#1e1e1e]' : 'bg-[#3c3c3c] text-[#b0b0b0]'
                            }`}
                            role="tab"
                            aria-selected={selectedSnippet === i}
                          >
                            {snippet.label}
                          </button>
                        ))}
                      </div>
                      <div className="flex justify-end mb-2">
                        <button
                          onClick={() => copyToClipboard(codeSnippets[selectedSnippet].code, 'snippet')}
                          className="flex items-center gap-1.5 px-2 py-1 text-xs text-[#5bcefa] hover:bg-[#2d2d30] rounded"
                          aria-label={copied === 'snippet' ? 'Copied!' : 'Copy code snippet'}
                        >
                          {copied === 'snippet' ? <Check className="w-3.5 h-3.5" aria-hidden="true" /> : <Copy className="w-3.5 h-3.5" aria-hidden="true" />}
                        </button>
                      </div>
                      <pre className="p-3 bg-[#2d2d30] rounded overflow-auto text-xs font-mono text-[#b0b0b0]">
                        {codeSnippets[selectedSnippet].code}
                      </pre>
                    </div>
                  )}
                </>
              )}

              {!loading && !error && !response && (
                <div className="flex flex-col items-center justify-center h-full text-center">
                  <Send className="w-8 h-8 text-[#858585] mb-3" aria-hidden="true" />
                  <div className="text-sm text-[#cccccc] font-medium">No response yet</div>
                  <div className="text-xs text-[#858585]">Press Ctrl+Enter or click Send to make a request</div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Environment Panel Modal */}
      {showEnvPanel && (
        <EnvironmentPanel
          environments={environments}
          onEnvironmentsChange={setEnvironments}
          onClose={() => setShowEnvPanel(false)}
        />
      )}

      {/* OpenAPI Generation Modal */}
      {showOpenApiGen && (
        <div
          className="fixed inset-0 bg-black/50 flex items-center justify-center z-50"
          role="dialog"
          aria-modal="true"
          aria-labelledby="openapi-modal-title"
          onClick={(e) => {
            if (e.target === e.currentTarget) setShowOpenApiGen(false)
          }}
        >
          <div className="bg-[#252526] rounded-lg w-[600px] max-h-[80vh] flex flex-col shadow-2xl">
            <div className="flex items-center justify-between p-4 border-b border-[#3c3c3c]">
              <h3 id="openapi-modal-title" className="text-white font-medium">Generate OpenAPI Specification</h3>
              <button
                onClick={() => setShowOpenApiGen(false)}
                className="p-1 hover:bg-[#3c3c3c] rounded"
                aria-label="Close OpenAPI generation dialog"
              >
                <X className="w-4 h-4" aria-hidden="true" />
              </button>
            </div>
            <div className="p-4 flex-1 overflow-auto">
              <div className="text-xs text-[#808080] mb-2">
                Generated from {endpoints.length} discovered endpoints
              </div>
              <pre className="p-3 bg-[#1e1e1e] rounded text-xs font-mono text-[#b0b0b0] max-h-96 overflow-auto">
                {generateOpenApiSpec()}
              </pre>
            </div>
            <div className="flex justify-end gap-2 p-4 border-t border-[#3c3c3c]">
              <Button
                size="sm"
                variant="outline"
                onClick={() => copyToClipboard(generateOpenApiSpec(), 'openapi')}
                aria-label={copied === 'openapi' ? 'Copied!' : 'Copy OpenAPI specification'}
              >
                {copied === 'openapi' ? <Check className="w-3.5 h-3.5 mr-1.5" aria-hidden="true" /> : <Copy className="w-3.5 h-3.5 mr-1.5" aria-hidden="true" />}
                Copy
              </Button>
              <Button
                size="sm"
                onClick={() => {
                  const blob = new Blob([generateOpenApiSpec()], { type: 'application/json' })
                  const url = URL.createObjectURL(blob)
                  const a = document.createElement('a')
                  a.href = url
                  a.download = 'openapi.json'
                  a.click()
                  URL.revokeObjectURL(url)
                }}
              >
                <Download className="w-3.5 h-3.5 mr-1.5" aria-hidden="true" />
                Download
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  )
}

