node_type.code:
sideTab === 'collections' && (
            collections.length === 0 ? (
              <div className="text-center text-xs text-[#808080] py-4">
                <FolderOpen className="w-6 h-6 mx-auto mb-2 opacity-50" aria-hidden="true" />
                <p>No saved requests</p>
                <p className="mt-1 text-[10px]">Save a request to create a collection</p>
              </div>
            ) : (
              collections.map(collection => (
                <div key={collection.id} className="mb-2">
                  <div
                    className="flex items-center gap-1.5 p-1.5 rounded cursor-pointer hover:bg-[#2d2d30] group"
                    onClick={() => setExpandedFolders(prev => {
                      const next = new Set(prev)
                      next.has(collection.id) ? next.delete(collection.id) : next.add(collection.id)
                      return next
                    })}
                    role="button"
                    aria-expanded={expandedFolders.has(collection.id)}
                    aria-label={`${collection.name} collection with ${collection.items.length} items`}
                  >
                    {expandedFolders.has(collection.id) ? (
                      <ChevronDown className="w-3.5 h-3.5 text-[#808080]" aria-hidden="true" />
                    ) : (
                      <ChevronRight className="w-3.5 h-3.5 text-[#808080]" aria-hidden="true" />
                    )}
                    <Folder className="w-3.5 h-3.5 text-[#ffd700]" aria-hidden="true" />
                    <span className="text-xs text-white flex-1 truncate">{collection.name}</span>
                    <span className="text-[10px] text-[#606060]">{collection.items.length}</span>
                    <button
                      onClick={(e) => {
                        e.stopPropagation()
                        deleteCollection(collection.id)
                      }}
                      className="p-0.5 hover:bg-[#5a1d1d] rounded opacity-0 group-hover:opacity-100"
                      aria-label={`Delete ${collection.name} collection`}
                    >
                      <Trash2 className="w-3 h-3 text-[#f48771]" />
                    </button>
                  </div>
                  {expandedFolders.has(collection.id) && (
                    <div className="ml-4">
                      {collection.items.map(item => {
                        if ('request' in item) {
                          return (
                            <div
                              key={item.id}
                              className="flex items-center gap-1.5 p-1.5 rounded cursor-pointer hover:bg-[#2d2d30] group"
                            >
                              <div
                                onClick={() => loadFromCollection(item)}
                                className="flex items-center gap-1.5 flex-1 min-w-0"
                                role="button"
                                aria-label={`Load ${item.name} request`}
                              >
                                <span className={`text-[9px] font-bold px-1 rounded ${METHOD_COLORS[item.request.method]}`}>
                                  {item.request.method.slice(0, 3)}
                                </span>
                                <span className="text-xs text-[#b0b0b0] truncate">{item.name}</span>
                              </div>
                              <button
                                onClick={(e) => {
                                  e.stopPropagation()
                                  deleteFromCollection(collection.id, item.id)
                                }}
                                className="p-0.5 hover:bg-[#5a1d1d] rounded opacity-0 group-hover:opacity-100"
                                aria-label={`Delete ${item.name} request`}
                              >
                                <Trash2 className="w-3 h-3 text-[#f48771]" />
                              </button>
                            </div>
                          )
                        }
                        return null
                      })}
                    </div>
                  )}
                </div>
              ))
            )
          )

